import * as React from 'react';
import {Button, Row, Col, Card, Form, InputGroup, FormControl, Modal, Tabs, Tab, Badge, Table} from 'react-bootstrap';
import DataTable, { createTheme } from 'react-data-table-component';
import Layout from '../../../../components/Layout';
import { getItemIncome } from "../../../../redux/actions/item";
import { updateSubmission, getSubmissionById, getSubmissionCategory } from "../../../../redux/actions/submission";
import {connect} from 'react-redux'
import CounterInput from 'react-counter-input'
import Router from "next/router";
import {alertSuccess, alertDanger} from '../../../../components/alert'
import {getProduct} from '../../../../redux/actions/product'
import ModalCustom from "components/ModalCustom";
import BookPerDay from "../../../../components/BookPerDay";
import {resetDateSchedule, resetRentTime} from '../../../../redux/actions/product_schedule'
import {formatCurrency} from '../../../../libs/numbers'
import dayjs from 'dayjs'

class edit extends React.Component {

    constructor(props){
        super(props)
        this.state = {
            modalProduct: false,
            columns: [
                {
                    name: 'ID',
                    selector: (row, index) =>  ((index+1) + (this.state.page - 1) * this.state.num) + '.',
                    sortable: true,
                    width: '10%'
                },
                {
                    name: 'Name',
                    selector: 'attributes.item_name',
                    sortable: true,
                },
                {
                    name: 'Selling Price (IDR)',
                    selector: 'attributes.selling_price',
                    sortable: true,
                },
                {
                    name: 'Actions',
                    selector: (row, index) => (<div style={{ color:'green' }}><i style={{ color:'#FFF', borderRadius:'50%', background:'green', padding:'2px' }} className="fa fa-plus" onClick={ () => {
                        // console.log(this.state.keranjang)
                        if(this.state.keranjang.find((data) => data.item_name == row.attributes.item_name)){
                            let index = this.state.keranjang.findIndex((data) => data.item_name == row.attributes.item_name)
                            let items = [...this.state.keranjang];
                            let item = {...items[index]};
                            item.qty = Number(item.qty) + 1

                            items[index] = item;
                            this.setState({
                                keranjang: items,
                            })
                        }else{
                            let keranjang = this.state.keranjang
                            row.id = row.attributes.id
                            row.qty = 1
                            row.discount = 0
                            row.ppn = 0
                            row.total = 0
                            row.selling_price = row.attributes.selling_price
                            row.item_name = row.attributes.item_name
                            row.submission_id = row.attributes.submission_id
