    generateStartFinishSchedule = (times, scheduleDate) => {
        const selectedRentTime = times.sort();

        let counter = 0;
        let finalSelectedRentTime = [];
        let temp = "";
        for (const rent of selectedRentTime) {
          let nextRentTime = selectedRentTime[counter + 1];
          if (nextRentTime !== undefined) {
            const splittedRent = rent.split(" - ");
            let splittedNextRentTime = nextRentTime.split(" - ");
            if (splittedRent[1] === splittedNextRentTime[0]) {
              let joinedRentTime = `${splittedRent[0]} - ${splittedNextRentTime[1]}`;
              if (temp === "") {
                temp = joinedRentTime;
              } else {
                let newTemp = temp.split(" - ");
                temp = newTemp[0] + " - " + splittedNextRentTime[1];
              }
            } else {
              if (temp !== "") {
                finalSelectedRentTime.push(temp);
              } else {
                finalSelectedRentTime.push(rent);
              }
              temp = "";
            }
          } else {
            if (temp) {
              finalSelectedRentTime.push(temp);
              temp = "";
            } else {
              finalSelectedRentTime.push(rent);
            }
          }
          counter++;
        }
        let newFinalSelectedRentTime = [];
        finalSelectedRentTime.forEach((rent) => {
          let splitted = rent.split(" - ");

          const start = dayjs(scheduleDate.fullDate)
            .startOf("day")
            .hour(parseInt(splitted[0].split(".")[0]))
            .format("YYYY-MM-DD HH:mm:ss");
          const finish = dayjs(scheduleDate.fullDate)
            .startOf("day")
            .hour(parseInt(splitted[1].split(".")[0]))
            .format("YYYY-MM-DD HH:mm:ss");
          newFinalSelectedRentTime.push(`${start} - ${finish}`);
        });
        return newFinalSelectedRentTime;
      };

    handleBook = async () => {
        const { carts, selectedDateSchedule, selectedRentTime } = this.props;

        const {
          id,
          product_name,
          product_type,
          unit,
          stock,
          buying_price,
          selling_price,
        } = this.state.selectedProduct.attributes;

        let scheduleTimes = this.generateStartFinishSchedule(
          selectedRentTime,
          selectedDateSchedule
        );

        if (!scheduleTimes || scheduleTimes.length < 1) {
            alertDanger('Anda Belum memilih jam sewa!')
        }else{
            let newCarts = [];
            newCarts = [...this.state.keranjang];
            for (const schedule of scheduleTimes) {
              let payload = {
                // attributes: {
                    id: null,
                    product_id: id,
                    item_name: product_name,
                    product_type,
                    unit,
                    stock,
                    submission_id:Router.router.query.id, 
                    buying_price,
                    selling_price,
                    qty: 1,
                    product_schedules: [],
                    discount: 0,
                    ppn: 0,
                    type: 'schedules'
                // }
              };
              const splittedTime = schedule.split(" - ");
              const newSchedule = {
                id: null,
                product_id: payload.product_id,
                start: splittedTime[0],
                finish: splittedTime[1],
              };
              payload.product_schedules.push(newSchedule);

              payload.qty = dayjs(newSchedule.finish).diff(
                newSchedule.start,
                "hour"
              );

                if(this.state.keranjang.find((data) => data.item_name == payload.item_name)){

                    let index = this.state.keranjang.findIndex((data) => data.item_name == payload.item_name)
                    let item = {...newCarts[index]};
                    item.qty = Number(item.qty) + parseInt(this.props.selectedRentTime.length)
                    item.type = 'schedules'
                    item.total = item.total + (parseInt(this.props.selectedRentTime.length) * item.selling_price)
                    newCarts[index] = item;
                }
                else{
                    payload.total = parseInt(this.props.selectedRentTime.length) * payload.selling_price
                    newCarts.push(payload);
                }
                this.setState({keranjang: newCarts})
            }
            await this.props.resetRentTime();
            await this.props.resetDateSchedule();

            this.setState({ bookPerDayModalVisible: false });
            alertSuccess('items added!')
        }

      };


