import * as React from 'react';
import {Button,Tabs, Tab, Row, Col, Card, Form, InputGroup, FormControl, Modal, Badge} from 'react-bootstrap';
import DataTable from 'react-data-table-component';
import Layout from '../../components/Layout';
import Confirm from '../../components/Confirm'
import {toast} from 'react-toastify'
import { get, store, update, destroy, destroyAttachment, createAttachmentProductCategory } from "../../redux/actions/product_category";
import {getProduct, destroyProduct, storeProduct, updateProduct} from '../../redux/actions/product'
import {connect} from 'react-redux'
import {Typeahead} from 'react-bootstrap-typeahead'
import {alertSuccess} from '../../components/alert'
import { AsyncTypeahead } from 'react-bootstrap-typeahead';
import moment from 'moment';
import { convertCurrency } from "libs/numbers";

class index extends React.Component {

  constructor(props){
    super(props)
    this.state = {
            customStyles: {
                rows: {
                    // style: {
                    //   cursor: 'pointer'
                    // }
                },
            },
            columnsCategory:[
                {
                    name: 'ID',
                    selector: 'attributes.id',
                    sortable: true,
                    width: '20%',
                    wrap: true,
                },
                {
                    name: 'Category',
                    selector: 'attributes.category_name',
                    sortable: true,
                    width: '65%',
                    wrap: true,
                },
                {
                    name: 'Actions',
                    selector: (row, index) => (<div>
                    <i style={{ color:'blue' }} className="fa fa-image" onClick={ () => this.setState({
                            modalAttachmentCategory: true,
                            dataImage: row.attributes.attachemnts ? row.attributes.attachemnts : [],
                            tabIndexCategory: index
                        })}></i>{' '}&nbsp;    
                    <img className="mr-2" style={{width: '15px'}} src="https://storage.googleapis.com/spaces-semanggi-app/back-office/super-entity/png/border-color-24px.png" onClick={ () => this.setState({
                        modalCategory:true, tabIndexCategory:index, nama_category: row.attributes.category_name, dataDetailCategory:row
                    })} /><img style={{width: '15px'}} src="https://storage.googleapis.com/spaces-semanggi-app/back-office/super-entity/png/delete.png" onClick={() => this.setState({modalDeleteCategory: true, dataDetailCategory: row, tabIndexCategory: index})} /></div>),
                    width: '10%',
                    wrap: true,
                }
            ],
            columns: [
                {
                    name: 'ID',
                    selector: (row,index) => (<u style={{ color:'#0DAA66' }}>{row.id}</u>),
                    sortable: true,
                    wrap: true,
                },
                {
                    name: 'Category',
                    selector: 'attributes.category.attributes.category_name',
                    sortable: true,
                    wrap: true,
                },
                {
                    name: 'Name',
                    selector: 'attributes.product_name',
                    sortable: true,
                    wrap: true,
                },
                {
                    name: 'Unit',
                    selector: 'attributes.unit',
                    sortable: true,
                    wrap: true,
                },
                {
                    name: 'Selling Price (IDR)',
                    selector: (row,index) => convertCurrency(parseFloat(row.attributes.selling_price)),
                    sortable: true,
                    wrap: true,
                },
                {
                    name: 'Cost Price (IDR)',
                    selector: (row,index) => convertCurrency(parseFloat(row.attributes.buying_price)),
                    sortable: true,
                    wrap: true,
                },
                {
                    name: 'Status',
                    cell: row => <div data-tag="allowRowEvents" style={{width: '70%', textAlign: 'center', fontSize: '11px'}}>{
                        row.attributes.status == 'APPROVED' ? <div className="py-2 px-3" style={{borderRadius: '18px', color: '#277DC1', border: 'solid 1px'}}> {row.attributes.status}</div> : 
                        row.status == 'PENDING' ? <div className="py-2 px-3" style={{borderRadius: '18px', color: '#F2994A', border: 'solid 1px'}}> {row.attributes.status}</div> : 
                        <div className="py-2 px-2" style={{borderRadius: '18px', color: '#F1DB0E', border: 'solid 1px'}}> {row.attributes.status}</div>}</div>,
                    sortable: true,
                    wrap: true,
                },
                {
                    name: 'Actions',
                    selector: (row, index) => (<div><img className="mr-2" style={{width: '15px'}} src="https://storage.googleapis.com/spaces-semanggi-app/back-office/super-entity/png/border-color-24px.png" onClick={() => this.editProduct(row, index)} />

                    <img style={{width: '15px'}} src="https://storage.googleapis.com/spaces-semanggi-app/back-office/super-entity/png/delete.png" onClick={() => this.setState({modalDelete: true, dataDelete: row, tabIndex: index})} /></div>),
                    width: '10%',
                    wrap: true,
                }
            ],
            loading: true,
            data: [],
            dataCategory:[],
            modalDelete: false,
            dataDelete: null,
            modalProduct: false,
            tabIndex: -1,
            tabIndexCategory: -1,
            modalCategory: false,
            nama_category: null,
            modalDeleteCategory: false,
            dataDetailCategory: null,
            modalAttachmentCategory: false,
            attachmentCategory: null,
            included: {},
            dataImage: [],
            attachmentLabel: null,
            dataInterfaces: [],
            optionsCategory: [],
            product_type: 'NO LIMIT',
            product_name: '',
            product_category: [],
            labelProductFoto: 'Upload Product Foto',
            labelProductContent: 'Upload Product Content',
            product_foto: null,
            product_content: [],
            min_stock: 0,
            unit: 'Pcs',
            interfaces: [],
            includeProduct: {},
            page: 1,
            num: 10,
            pageCategory: 1,
            numCategory: 10,
            inputSearch : null,
            inputSearchCategory: null,
            isLoading: false,
            options: [],
            start: null,
            finish: null,
            selling_price: null,
            buying_price: null,
            stock: 0,
            dataDetailProduct: null,
            id_product: null,
            detailProduct: null,
            description: null,
            statusProduct: null
        }

        this.save = this.save.bind(this);
        this.saveAttachment = this.saveAttachment.bind(this)
        this.saveProduct = this.saveProduct.bind(this)
    }

    editProduct = (data, index) => {
        data.attributes.visibility.forEach((data) => {
            interfaces.push({
                id: data.attributes.interface_id,
                label: data.attributes.interface_name
            })
        })

        let product_category = [{
            id: data.attributes.category.attributes.id,
            label: data.attributes.category.attributes.category_name
        }]
        this.setState({
            modalProduct: true,
            tabIndex: index,
            product_name: data.attributes.product_name,
            product_type: data.attributes.product_type,
            product_category: product_category,
            unit: data.attributes.unit,
            selling_price: data.attributes.selling_price,
            buying_price: data.attributes.buying_price,
            stock: data.attributes.stock,
            min_stock: data.attributes.min_stock,
            start: data.attributes.schedule.length && moment(data.attributes.schedule[0].attributes.start).format('YYYY-MM-DDTHH:mm'),
            finish: data.attributes.schedule.length && moment(data.attributes.schedule[0].attributes.finish).format('YYYY-MM-DDTHH:mm'),
            interfaces: interfaces,
            id_product: data.attributes.id,
            description: data.attributes.description,
            statusProduct: data.attributes.status
        })
    }

    getData = async (page, size = this.state.num, options) => {
        let opts = {
            params: {
                include: 'product_attachment,product_visibility,product_category,product_schedule',
                'page[limit]': size,
                'page[num]': page,
            }
        }

        if(options){
            Object.assign(opts.params, options)
        }

        await this.props.getProduct(opts).then(() => {
            let data = this.props.dataProduct.map((d) => {
                let attachemnts = []
                let visibility = []
                let schedules = []
                d.relationships.product_attachment.forEach((data) => {
                    attachemnts.push(this.props.includeProduct.product_attachment[data.id])
                })

                d.relationships.product_visibility.forEach((data) => {
                    visibility.push(this.props.includeProduct.product_visibility[data.id])
                })

                d.relationships.product_schedule.forEach((data) => {
                    schedules.push(this.props.includeProduct.product_schedule[data.id])
                })

                let category = this.props.includeProduct.product_category[d.relationships.product_category.id]
                d.attributes.category = category
                d.attributes.attachemnts = attachemnts
                d.attributes.visibility = visibility
                d.attributes.schedule = schedules
                return d;
            })
            this.setState({
                data: data,
                includeProduct : this.props.includeProduct,
                page: page,
                num: size
            })

        })
    }


    getProductCategory = async (page, size = this.state.numCategory, options) => {
        let opts = {
            params: {
                include: 'product_category_attachment',
                'page[limit]': size,
                'page[num]': page,
            }
        }
        if(options){
            Object.assign(opts.params, options)
        }
        await this.props.get(opts).then(() => {
            let optionsCategory = []
            let attachemnts = this.props.included.product_category_attachment
            let dataAttachment = Object.keys(attachemnts).map(function(key, index) { return attachemnts[key] });

            let data = this.props.data.map((d) => {
                optionsCategory.push({
                    id: d.attributes.id,
                    label: d.attributes.category_name
                })
                d.attributes.attachemnts = []
                dataAttachment.forEach((data) => {
                    if(data.attributes.category_id == d.id){
                        d.attributes.attachemnts.push(data)
                    }
                })
                return d;
            })
            console.log(optionsCategory)
            this.setState({
                loading: false,
                dataCategory: data,
                included: this.props.included,
                optionsCategory: optionsCategory
            })
        })
    }

    async saveProduct (e){
        e.preventDefault()
        if(this.state.tabIndex === -1){

            let payload = new FormData()
            payload.append('product_type', this.state.product_type)
            payload.append('description', this.state.description)
            payload.append('product_name', this.state.product_name)
            payload.append('category_id', this.state.product_category.length && this.state.product_category[0].id)
            payload.append('min_stock', this.state.min_stock)
            payload.append('file', this.state.product_foto)
            payload.append('unit', this.state.unit)
            payload.append('stock', this.state.stock)
            payload.append('buying_price', this.state.buying_price)
            payload.append('selling_price', this.state.selling_price)
            for(const key of Object.keys(this.state.product_content)){
                payload.append('detail_images[]', this.state.product_content[key])
            }

            for(const key of Object.keys(this.state.interfaces)){
                payload.append('visibilities[]', this.state.interfaces[key].id)
            }

            if(this.state.product_type == 'LIMITED' || this.state.product_type == 'TIME LIMIT'){
                payload.append('schedules[start]', this.state.start)
                payload.append('schedules[finish]', this.state.finish)
                payload.append('schedules[item_id]', null)
            }

            this.props.storeProduct(payload).then(() => {
                let attachemnts = []
                let visibility = []
                let schedules = []
                this.props.dataProduct.relationships.product_attachment.forEach((data) => {
                    attachemnts.push(this.props.includeProduct.product_attachment[data.id])
                })

                this.props.dataProduct.relationships.product_visibility.forEach((data) => {
                    visibility.push(this.props.includeProduct.product_visibility[data.id])
                })

                this.props.dataProduct.relationships.product_schedule.forEach((data) => {
                    schedules.push(this.props.includeProduct.product_schedule[data.id])
                })

                this.props.dataProduct.attributes.category = this.props.includeProduct.product_category[this.props.dataProduct.attributes.category_id]
                this.props.dataProduct.attributes.attachemnts = attachemnts
                this.props.dataProduct.attributes.visibility = visibility
                this.props.dataProduct.attributes.schedule = schedules

                let add_data = [...this.state.data]
                add_data.splice(0, 0, this.props.dataProduct)
                this.setState({
                    data: add_data,
                    modalProduct: false
                })

                alertSuccess('Product Created!')
            }).catch((error) => {})
        }else{
            let payload = new FormData()
            payload.append('product_type', this.state.product_type)
            payload.append('description', this.state.description)
            payload.append('product_name', this.state.product_name)
            payload.append('category_id', this.state.product_category.length && this.state.product_category[0].id)
            payload.append('min_stock', this.state.min_stock)
            payload.append('file', this.state.product_foto)
            payload.append('unit', this.state.unit)
            payload.append('stock', this.state.stock)
            payload.append('buying_price', this.state.buying_price)
            payload.append('selling_price', this.state.selling_price)
            for(const key of Object.keys(this.state.product_content)){
                payload.append('detail_images[]', this.state.product_content[key])
            }

            for(const key of Object.keys(this.state.interfaces)){
                payload.append('visibilities[]', this.state.interfaces[key].id)
            }

            if(this.state.product_type == 'LIMITED' || this.state.product_type == 'TIME LIMIT'){
                payload.append('schedules[start]', this.state.start)
                payload.append('schedules[finish]', this.state.finish)
                payload.append('schedules[item_id]', null)
            }
            payload.append('id', this.state.id_product)
            payload.append('_method', 'PUT')
            this.props.updateProduct(payload).then(() => {
                let attachemnts = []
                let visibility = []
                let schedules = []
                this.props.dataProduct.relationships.product_attachment.forEach((data) => {
                    attachemnts.push(this.props.includeProduct.product_attachment[data.id])
                })

                this.props.dataProduct.relationships.product_visibility.forEach((data) => {
                    visibility.push(this.props.includeProduct.product_visibility[data.id])
                })

                this.props.dataProduct.relationships.product_schedule.forEach((data) => {
                    schedules.push(this.props.includeProduct.product_schedule[data.id])
                })

                this.props.dataProduct.attributes.attachemnts = attachemnts
                this.props.dataProduct.attributes.visibility = visibility
                this.props.dataProduct.attributes.schedule = schedules
                this.props.dataProduct.attributes.category = this.props.includeProduct.product_category[this.props.dataProduct.attributes.category_id]
                let edit_data = [...this.state.data]
                edit_data.splice(this.state.tabIndex, 1);
                edit_data.splice(this.state.tabIndex, 0, this.props.dataProduct)
                this.setState({
                    data: edit_data,
                    modalProduct: false
                })
                alertSuccess('Product Updated!')
            })
        }
    }

    async componentDidMount() {
        this.getProductCategory(this.state.pageCategory, this.state.numCategory)
        this.getData(this.state.page, this.state.num)
    }

    async saveAttachment (e) {
        e.preventDefault()

        const payload = new FormData();
        payload.append('category_id', this.state.dataCategory[this.state.tabIndexCategory].id);
        payload.append('file', this.state.attachmentCategory);
        this.props.createAttachmentProductCategory(payload).then(() => {
            let add_data = [...this.state.dataImage]
            add_data.splice(this.state.dataImage.length, 0, this.props.dataImage) 
            let items = [...this.state.dataCategory];
            let item = {...items[this.state.tabIndexCategory]};
            let attributes = {...item.attributes}
            let attachments = [...attributes.attachemnts]
            attachments.splice(this.state.dataImage.length, 0, this.props.dataImage)
            items[this.state.tabIndexCategory].attributes.attachemnts = attachments;
            this.setState({
                dataImage: add_data,
                attachmentLabel: 'Upload File',
                dataCategory: items,
            })
            alertSuccess('Upload Image Success!')
        }).catch((error) => {
            console.log(error)
        })

    }

    async save(){
        if(this.state.tabIndexCategory == -1){
            let payload = {
                category_name: this.state.nama_category
            }
            await this.props.store(payload).then(() => {
                let add_data = [...this.state.dataCategory]
                add_data.splice(0, 0, this.props.data)
                this.setState({
                    dataCategory: add_data,
                    modalCategory: false
                })
                alertSuccess('Product Category Created!')
            })
        }else{
            let payload = {
                category_name: this.state.nama_category,
                _method: 'PATCH',
                id: this.state.dataDetailCategory.id 
            }
            await this.props.update(payload).then(() => {
                let edit_data = [...this.state.dataCategory]
                edit_data.splice(this.state.tabIndexCategory, 1);
                edit_data.splice(this.state.tabIndexCategory, 0, this.props.data)
                this.setState({
                    dataCategory: edit_data,
                    modalCategory: false
                })
                alertSuccess('Product Category Updated!')
            })
        }
    }

    handlePageChange = page => {
        this.getData(page)
    }

    handlePerRowsChange = async (newPerPage, page) => {
        this.getData(page, newPerPage);
    };

    handleSearch = async (e) => {
        if (e.key === 'Enter') {
          e.preventDefault()
          let metode = {'filter[product_name][like]' : this.state.inputSearch}
          this.getData(1, this.state.num, metode)
        }
    }

    handlePageChangeCategory = page => {
        this.getProductCategory(page)
    }

    handlePerRowsChangeCategory = async (newPerPage, page) => {
        this.getProductCategory(page, newPerPage);
    };

    handleSearchCategory = async (e) => {
        if (e.key === 'Enter') {
          e.preventDefault()
          let metode = {'filter[category_name][like]' : this.state.inputSearchCategory}
          this.getProductCategory(1, this.state.numCategory, metode)
        }
    }

    handleSearchProduct = (query) => {
        this.setState({ isLoading: true})
        let opts = {
            params: {
                'filter[item_name][like]' : query
            }
        }
        this.props.getItemSubmission(opts).then(() => {
            const options = this.props.dataItem.map((i) => ({
                item_name: i.attributes.item_name,
                id: i.attributes.id,
                buying_price: i.attributes.buying_price,
                selling_price: i.attributes.selling_price,
            }));
            this.setState({ isLoading: false, options: options})
        })
    };

    onInputChangeProduct = (query) => {
        setTimeout(() => {
            let opts = {
                params: {
                    'filter[item_name][like]' : query
                }
            }
            this.props.getItemSubmission(opts).then(() => {
                let data = this.props.dataItem.find((value) => value.attributes.item_name.toLowerCase() == query.toLowerCase())
                if(data){
                    this.setState({
                        product_name: data.attributes.item_name,
                        selling_price: data.attributes.selling_price,
                        id: data.attributes.id,
                        buying_price: data.attributes.buying_price,
                        dataDetailProduct: this.props.dataItem
                    })
                }else{
                    this.setState({
                        product_name: query,
                        selling_price: null,
                        id: null,
                        buying_price: null,
                        dataDetailCategory: null
                    })
                }
            })
        }, 1000)
    }

    filterBy = () => true;


    render () {



      return (

        <Layout title="Products" classname="dashboard theme-light">
        {/* Modal Form */}
        <Modal
            show={this.state.modalProduct}
            onHide={() => this.setState({modalProduct: false, tabIndex: -1, statusProduct: null})}
            backdrop="static"
            keyboard={false}
            centered
            size="lg"
            aria-labelledby="contained-modal-title-vcenter"
        >
            <Modal.Header style={{ border:'none' }} closeButton>
            <Modal.Title>{this.state.tabIndex == -1 ? 'Create Product' : 'Edit Product'}</Modal.Title>
            </Modal.Header>
            <Form onSubmit={this.saveProduct}>
            <Modal.Body className="p-5">
                {this.state.statusProduct != 'APPROVED' ?
                <div>
                <Row>
                    <Col xs={4} className="pt-1">
                        <strong>Product Name</strong>
                    </Col>
                    <Col xs={8}>
                    <AsyncTypeahead
                        defaultInputValue={this.state.product_name}
                        filterBy={this.filterBy}
                        labelKey="item_name"
                        id="async-example"
                        isLoading={this.state.isLoading}
                        minLength={1}
                        onChange={selected => {
                            if (selected.length === 0) {
                              this.setState({
                                    product_name: null,
                                    selling_price: 0,
                                    buying_price:0,
                                    dataDetailProduct: null
                              })
                            } else {
                                this.setState({
                                    product_name: selected[0].item_name,
                                    selling_price: Number(selected[0].selling_price),
                                    buying_price: Number(selected[0].buying_price),
                                    dataDetailProduct: selected
                                })
                            }
                        }}
                        onSearch={this.handleSearchProduct}
                        onInputChange={this.onInputChangeProduct}
                        options={this.state.options}
                        placeholder="Search product..."
                        renderMenuItemChildren={(option, props) => (
                            <div key={option.id}>
                            <span>{option.item_name}</span>
                            </div>
                        )}
                    />
                    </Col>
                </Row>
                <Row className="pt-4">
                    <Col xs={4} className="pt-1">
                        <strong>Description</strong>
                    </Col>
                    <Col xs={8}>
                        <Form.Control type="text" placeholder="Description" defaultValue={this.state.description} onChange={(e) => this.setState({description: e.target.value})} />
                    </Col>
                </Row>
                <Row className="pt-4">
                    <Col xs={4} className="pt-1">
                        <strong>Product Type</strong>
                    </Col>
                    <Col xs={8}>
                        <Form.Control as="select" defaultValue={this.state.product_type} onChange={(e) => this.setState({product_type: e.target.value})}>
                            <option value="NO LIMIT">NO LIMIT</option>
                            <option value="TIME LIMIT">TIME LIMIT</option>
                            <option value="STOCK LIMIT">STOCK LIMIT</option>
                            <option value="LIMITED">LIMITED</option>
                        </Form.Control>
                    </Col>
                </Row>
                <Row className="pt-4">
