                                                                <Col xs={4} key={index}>
                                                                    <img
                                                                        width={'100%'}
                                                                        height={200}
                                                                        className="mr-3"
                                                                        src={data.attributes.file_location+data.attributes.file_name}
                                                                        alt="Generic placeholder"
                                                                    />

                                                                    <Badge variant="danger" style={{width: '100%'}} onClick={async () => {
                                                                        this.props.destroyAttachment(data.id).then(()=>{
                                                                            let delete_data = [...this.state.dataImage]
                                                                            delete_data.splice(index, 1) 
                                                                            let items = [...this.state.dataCategory];
                                                                            let item = {...items[this.state.tabIndexCategory]};
                                                                            let attributes = {...item.attributes}
                                                                            let attachments = [...attributes.attachemnts]
                                                                            attachments.splice(index, 1)
                                                                            items[this.state.tabIndexCategory].attributes.attachemnts = attachments;
                                                                            this.setState({
                                                                                dataImage: delete_data,
                                                                                dataCategory: items,
                                                                            })

                                                                            alertSuccess('Data Image Berhasil di Hapus')
                                                                        })


