                                                        </InputGroup.Prepend>
                                                        <FormControl id="inlineFormInputGroup" placeholder="Find Category By Name" onKeyPress={this.handleSearchCategory} onChange={(e) => this.setState({ inputSearchCategory: e.target.value })} />
                                                    </InputGroup>
