import DashboardService from "../../service/dashboard";

export const getDashboard = (opts) => (dispatch) => {
    return DashboardService.getDashboard(opts).then(
    (data) => {
      dispatch({
          type: "GET_DASHBOARD",
          payload: {
              data: data.data ? data.data.data : null,
              included: data.data && data.data.included ? data.data.included : []
          }
      })
      return Promise.resolve();
    },
    (error) => {
      dispatch({
        type: "FAIL",
      });

      return Promise.reject();
    }
  );
};

