import * as React from "react";
import {
  Card,
  Col,
  Row,
  Table,
  Form,
  InputGroup,
  FormControl,
  Button,
  Badge,
  Modal,
  Alert,
} from "react-bootstrap";
import Layout, {
  showLoadingSpinner,
  hideLoadingSpinner,
  addNotification,
  handleError,
} from "../../components/Layout";
import CustomSelect from "../../components/CustomSelect";
import ReactTooltip from "react-tooltip";
import DataTable2 from "../../components/DataTable2";
import { getRole } from "redux/actions/role";
import Confirm from "../../components/Confirm";
import { CustomRadioButton } from "components/CustomRadioButton";
import FindData from "components/FindData";
import { toast } from "react-toastify";
import Select from "react-select";
import debounce from "lodash/debounce";
import {
  searchUser,
  storeUser,
  editUserCompany,
  assignUserToCompany,
  getUsers,
  destroyUser,
  deleteUserFromCompany
} from "../../redux/actions/user";
import { connect } from "react-redux";
import { FormModal } from "components/Modal";

const ONLINE_COLOR = {
  Online: "#0daa66",
  Offline: "#78828a",
};

const CreateUserModalForm = ({ formData, listRoles, handleInputUser }) => (
  <>
    <Form.Group as={Row}>
      <Form.Label column sm="4">
        <span style={{ fontSize: "13px" }}>Name</span>
      </Form.Label>
      <Col sm="8">
        <Form.Control
          type="text"
          placeholder="Name"
          onChange={handleInputUser("full_name")}

          size="lg"
        />
      </Col>
    </Form.Group>

    <Form.Group as={Row}>
      <Form.Label column sm="4">
        <span style={{ fontSize: "13px" }}>Username</span>
      </Form.Label>
      <Col sm="8">
        <Form.Control
          type="text"
          placeholder="Username"
          onChange={handleInputUser("username")}

          size="lg"
        />
      </Col>
    </Form.Group>

    <Form.Group as={Row}>
      <Form.Label column sm="4">
        <span style={{ fontSize: "13px" }}>Email</span>
      </Form.Label>
      <Col sm="8">
        <Form.Control
          type="email"
          placeholder="Email"
          onChange={handleInputUser("email")}

          size="lg"
        />
      </Col>
    </Form.Group>

    <Form.Group as={Row}>
      <Form.Label column sm="4">
        <span style={{ fontSize: "13px" }}>Phone</span>
      </Form.Label>
      <Col sm="8">
        <Form.Control
          type="text"
          placeholder="Phone"
          onChange={handleInputUser("phone")}

          size="lg"
        />
      </Col>
    </Form.Group>

    <Form.Group as={Row}>
      <Form.Label column sm="4">
        <span style={{ fontSize: "13px" }}>Role</span>
      </Form.Label>
      <Col sm="8">
        <Select
          styles={{
            menuList: (provided) => {
              return {
                ...provided,
                backgroundColor: "#fff",
              };
            },
            control: (base, state) => ({
              ...base,
              "&:hover": { borderColor: "#0daa66" },
              border: state.isFocused ? "1px solid #0daa66" : "1px solid #ccc",
              boxShadow: "none",
            }),
          }}

          options={listRoles}
          getOptionLabel={(option) => option.attributes.custom_name}
          getOptionValue={(option) => option.id}
          placeholder="Select a Role"
          onChange={handleInputUser("role")}
        />
      </Col>
    </Form.Group>
    <Form.Group as={Row}>
      <Form.Label column sm="4">
        <span style={{ fontSize: "13px" }}>Password</span>
      </Form.Label>
      <Col sm="8">
        <Form.Control
          type="password"
          placeholder="Password"
          onChange={handleInputUser("password")}
          className="mb-3"

          size="lg"
        />
        <small className="font-italic">
          Give this password to the user you just created. They can change their
          password in the section profile after logging in to the system.
        </small>
      </Col>
    </Form.Group>
  </>
);

const EditUserModalForm = ({ formData, listRoles, handleInputUser }) => (
  <>
    <Form.Group as={Row}>
      <Form.Label column sm="4">
        <span style={{ fontSize: "13px" }}>Name</span>
      </Form.Label>
      <Col sm="8">
        <Form.Control
          type="text"
          placeholder="Name"
          readOnly
          onChange={handleInputUser("full_name")}

          value={formData.full_name}
          size="lg"
        />
      </Col>
    </Form.Group>

    <Form.Group as={Row}>
      <Form.Label column sm="4">
        <span style={{ fontSize: "13px" }}>Role</span>
      </Form.Label>
      <Col sm="8">
        <CustomSelect

          value={formData.role}
          options={listRoles}
          getOptionLabel={(option) => option.attributes.custom_name}
          getOptionValue={(option) => option.id}
          placeholder="Select a Role"
          onChange={handleInputUser("role")}
        />
      </Col>
    </Form.Group>
  </>
);

const AssignUserModalForm = ({
  formData,
  isAlertVisible,
  foundUser,
  listRoles,
  handleInputUser,
  handleSearchUser,
  handleKeyDown,
  closeAlertSearchUser,
}) => (
  <>
    <Form.Group as={Row}>
      <Col sm="9">
        <InputGroup>
          <InputGroup.Prepend>
            <InputGroup.Text>
              <i className="fa fa-search" />
            </InputGroup.Text>
          </InputGroup.Prepend>
          <Form.Control
            type="text"
            placeholder="Search user by email or phone number"
            onChange={handleInputUser("keywordSearch")}
            onKeyDown={handleKeyDown}
            required
            size={"lg"}
          />{" "}
        </InputGroup>
      </Col>
      <Col sm="3">
        <Button
          type="submit"
          className="btn-info-semanggi"
          onClick={handleSearchUser}
        >
          Search
        </Button>
      </Col>
    </Form.Group>
    {foundUser === "NOT FOUND" && isAlertVisible ? (
      <Alert variant="danger" dismissible onClose={closeAlertSearchUser}>
        <Alert.Heading>User Not Found!</Alert.Heading>
        <p>Please enter another email/phone</p>
      </Alert>
    ) : foundUser && foundUser.name && isAlertVisible ? (
      <Alert variant="info" dismissible onClose={closeAlertSearchUser}>
        <Alert.Heading className="text-center">User Found!</Alert.Heading>
        <div
          style={{
            display: "flex",
            flexDirection: "row",
          }}
        >
          <div style={{ minWidth: "25%" }}>
            <p className="mb-2">Name:</p>
            <p className="mb-2">Email:</p>
            <p className="mb-2">Phone:</p>
          </div>
          <div style={{ minWidth: "10%" }}>
            <p className="mb-2">:</p>
            <p className="mb-2">:</p>
            <p className="mb-2">:</p>
          </div>
          <div>
            <p className="mb-2">{foundUser.name}</p>
            <p className="mb-2">{foundUser.email}</p>
            <p className="mb-2">{foundUser.phone}</p>
          </div>
        </div>
      </Alert>
    ) : null}
    {foundUser !== "NOT FOUND" && foundUser !== null ? (
      <Form.Group as={Row}>
        <Form.Label column sm="4">
          <span style={{ fontSize: "13px" }}>Assign a Role to User</span>
        </Form.Label>
        <Col sm="8">
          <CustomSelect
            options={listRoles}
            required
            getOptionLabel={(option) => option.attributes.custom_name}
            getOptionValue={(option) => option.id}
            placeholder="Select a Role"
            onChange={handleInputUser("role")}
          />
        </Col>
      </Form.Group>
    ) : null}
  </>
);

class index extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      tabIndex: -1,
      modalDelete: false,
      dataDelete: null,
      btnSave: false,
      show: true,
      page: 1,
      num: 10,

      formData: {
        createBy: "NEW USER",
        full_name: "",
        username: "",
        email: "",
        activated: true,
        type: "PERSONAL",
      },

      foundUser: null,
      createUserModalVisible: false,
      editUserModalVisible: false,
      isAlertVisible: false,
      paginationUsers: {
        pageCount: 0,
      },
      getUserOpts: {
        params: {
          include: "user_has_company,roles",
          "filter[user_has_company.id][is]": this.props.dataLogin.company_id,
        },
      },
    };

    this.UserTable = React.createRef();
    this.searchUsers = debounce(this.searchUsers, 1500);
  }

  columns = [
    {
      Header: "Name",
      accessor: "attributes.full_name",
    },
    {
      Header: "Status",
      accessor: "attributes.is_online",
      style: {
        textAlign: "center",
      },
      Cell: (props) => {
        return (
          <>
            <div
              style={{
                display: "inline-block",
                width: "10px",
                height: "10px",
                backgroundColor: ONLINE_COLOR[props.value ? "Online" : "Offline"],
              }}
              data-tip={props.value ? "Online" : "Offline"}
            ></div>
            <ReactTooltip place="bottom" />
          </>
        );
      },
    },
    {
      Header: "Username",
      accessor: "attributes.username",
    },
    {
      Header: "Email",
      accessor: "attributes.email",
    },
    {
      Header: "Role",
      accessor: "attributes.role",
      Cell: (props) => {
        return <span>{props.value.filter(role => role.company_id === this.props.dataLogin.company_id).map((r) => r.custom_name).join(", ")}</span>;
      },
    },
    {
      Header: "Action",
      accessor: "",
      style: {
        width: 80,
      },
      Cell: (props) => (
        <div>
          <img
            className="mr-2"
            style={{ width: "15px" }}
            src="https://storage.googleapis.com/spaces-semanggi-app/back-office/super-entity/png/border-color-24px.png"
            onClick={this.openEditUserModal(props.cell.row.original)}
          />
          <img
            style={{ width: "15px" }}
            src="https://storage.googleapis.com/spaces-semanggi-app/back-office/super-entity/png/delete.png"
            onClick={() => this.deletUser(props.cell.row.original.attributes.id)}
          />
        </div>
      ),
    },
  ];

  async deletUser(id){
    await this.props.deleteUserFromCompany(id, {})
    const { paginationUsers } = this.state;
    this.fetchData({
      pageSize: paginationUsers.pageSize,
      pageIndex: paginationUsers.pageIndex,
    });
    toast.success("data has been deleted !", {
      position: toast.POSITION.TOP_RIGHT,
      autoClose: 3000,
    });
  }

  async componentDidMount() {
    this.props.getRole({
      params: {
        include: "permissions",
      },
    });
  }

  fetchData = async ({ pageSize, pageIndex, sortBy }) => {
    const opts = {
      params: {
        ...this.state.getUserOpts.params,
        "page[limit]": pageSize,
        "page[num]": pageIndex + 1,
      },
    };
    await this.props.getUsers(opts).then((data) => {

    }).catch((error) => {
      alert('error')
    });

    this.setState({
      paginationUsers: {
        pageSize,
        pageIndex,
        pageCount: Math.ceil(this.props.total / pageSize),
      },
    });
  };

  handleFindData = async (e) => {
    this.setState({
      getUserOpts: {
        params: {
          ...this.state.getUserOpts.params,
          "filter[inclusive][full_name][like]": e.target.value,
          "filter[inclusive][email][like]": e.target.value,
          "filter[inclusive][username][like]": e.target.value,
          "filter[inclusive][roles.custom_name][like]": e.target.value,
        },
      },
    });
    this.searchUsers();
  };

  searchUsers = async (e) => {
    showLoadingSpinner();
    const { paginationUsers } = this.state;
    this.fetchData({
      pageSize: paginationUsers.pageSize,
      pageIndex: paginationUsers.pageIndex,
    });

    hideLoadingSpinner();
  };

  handleInputUser = (key) => (e) => {
    if (key === "role") {
      this.setState({
        formData: {
          ...this.state.formData,
          [key]: e,
          role_id: e.id,
        },
      });
    } else if (key === "createBy") {
      this.setState({
        formData: {
          ...this.state.formData,
          [key]: e,
          role_id: "",
        },
      });
    } else {
      this.setState({
        formData: {
          ...this.state.formData,
          [key]: e.target.value,
        },
      });
    }
  };

  handleKeyDown = (e) => {
    if (e.keyCode === 13) {
      return this.handleSearchUser();
    }
  };

  handleSearchUser = async (e) => {
    if (e) e.preventDefault();
    const { formData } = this.state;

    this.setState({
      foundUser: null,
    });

    showLoadingSpinner();

    const result = await this.props.searchUser({
      q: formData.keywordSearch,
    });

    if (result) {
      this.setState({
        foundUser: {
          id: result.data.attributes.id,
          name: result.data.attributes.full_name,
          email: result.data.attributes.email,
          phone: result.data.attributes.phone,
        },
        isAlertVisible: true,
      });
    } else {
      this.setState({
        foundUser: "NOT FOUND",
        isAlertVisible: true,
      });
    }
    hideLoadingSpinner();
  };

  handleSubmitCreateUser = async (e) => {
    if (e) e.preventDefault();
    const { formData } = this.state;
    await this.props.storeUser(formData, {});
    addNotification({
      level: "success",
      message: "Success Created User!",
    });
    this.setState({
      createUserModalVisible: false
    })
  };

  handleAssignUser = async (e) => {
    if (e) e.preventDefault();

    showLoadingSpinner();

    const { foundUser, formData, paginationUsers } = this.state;
    if (!foundUser || foundUser === "NOT FOUND") {
      return handleError({
        message: "User cannot be empty!",
      });
    }
    if (!formData.role_id) {
      return handleError({
        message: "Role cannot be empty!",
      });
    }

    let payload = {
      user_id: foundUser.id,
      role_id: formData.role_id,
    }


    await this.props.assignUserToCompany(
      {
        user_id: foundUser.id,
        role_id: formData.role_id,
      },
      {}
    );

    this.fetchData({
      pageSize: paginationUsers.pageSize,
      pageIndex: paginationUsers.pageIndex,
    });

    addNotification({
      level: "success",
      message: "Success assigned user to company!",
    });

    this.closeCreateUserModal();

    hideLoadingSpinner();
  };

  openCreateUserModal = (e) => {
    this.setState({
      formData: {
        createBy: "NEW USER",
        full_name: "",
        username: "",
        email: "",
        activated: true,
        type: "PERSONAL",
      },
      createUserModalVisible: true,
    });
  };

  closeCreateUserModal = (e) => {
    this.setState({
      createUserModalVisible: false,
    });
  };

  // Edit User

  openEditUserModal = (row) => (e) => {
    const { attributes } = row;
    let selectedRole =
      attributes.role && attributes.role.length > 0 ? attributes.role[0] : "";
    this.setState({
      formData: {
        ...attributes,
        role: !selectedRole
          ? ""
          : {
              attributes: {
                ...selectedRole,
              },
            },
      },
      editUserModalVisible: true,
    });
  };

  closeEditUserModal = (e) => {
    this.setState({
      editUserModalVisible: false,
    });
  };

  handleSubmitEditUser = async (e) => {
    if (e) e.preventDefault();
    let { formData, paginationUsers } = this.state;
    formData.role_id = formData.role.attributes.id;
    formData.user_id = formData.id
    let payload = {
      role_id: formData.role.attributes.id,
      user_id: formData.id
    }

    showLoadingSpinner();

    await this.props.editUserCompany(payload, {});

    this.fetchData({
      pageSize: paginationUsers.pageSize,
      pageIndex: paginationUsers.pageIndex,
    });

    this.setState({
      editUserModalVisible: false,
    });
    addNotification({
      level: "success",
      message: "Edit user successfully!",
    });
    hideLoadingSpinner();
  };

  // End Edit User

  closeAlertSearchUser = (e) => {
    this.setState({
      isAlertVisible: false,
    });
  };

  render() {
    const {
      formData,
      createUserModalVisible,
      editUserModalVisible,
    } = this.state;
    return (
      <Layout title="Manage User" classname="dashboard theme-light">
        {/* Modal User */}
        <FormModal
          title={<span style={{ fontWeight: "bold" }}>Create User</span>}
          visible={createUserModalVisible}
          onClose={this.closeCreateUserModal}
          onSubmit={
            formData.createBy === "NEW USER"
              ? this.handleSubmitCreateUser
              : this.handleAssignUser
          }
          onSubmitTitle={formData.createBy === "NEW USER" ? "Create" : "Assign"}
          size="lg"
        >
          <Form.Group as={Row} className="mb-5 justify-content-center">
            <Col sm="4">
              <CustomRadioButton
                value="NEW USER"
                selected={formData.createBy}
                text="New User"
                onChange={this.handleInputUser("createBy")}
              />
            </Col>
            <Col sm="4">
              <CustomRadioButton
                value="ASSIGN USER"
                selected={formData.createBy}
                text="Assign Existing User"
                onChange={this.handleInputUser("createBy")}
              />
            </Col>
          </Form.Group>
          {formData.createBy === "NEW USER" ? (
            <CreateUserModalForm
              formData={formData}
              handleInputUser={this.handleInputUser}
              listRoles={this.props.dataRole}
            />
          ) : (
            <AssignUserModalForm
              formData={formData}
              isAlertVisible={this.state.isAlertVisible}
              foundUser={this.state.foundUser}
              handleInputUser={this.handleInputUser}
              listRoles={this.props.dataRole}
              handleSearchUser={this.handleSearchUser}
              handleKeyDown={this.handleKeyDown}
              closeAlertSearchUser={this.closeAlertSearchUser}
            />
          )}
        </FormModal>

        <FormModal
          title={<span style={{ fontWeight: "bold" }}>Edit User</span>}
          visible={editUserModalVisible}
          onClose={this.closeEditUserModal}
          onSubmit={this.handleSubmitEditUser}
          onSubmitTitle={"Save"}
          size="lg"
        >
          <EditUserModalForm
            formData={formData}
            handleInputUser={this.handleInputUser}
            listRoles={this.props.dataRole}
          />
        </FormModal>

        {/* Modal Delete */}
        {this.state.dataDelete && (
          <Confirm
            title="Delete Product"
            body={
              <div>
                <small>
                  Are you sure you want to Delete User with the folowing
                  details:{" "}
                </small>
                <div
                  style={{
                    background: "#F7F8FC",
                    borderRadius: "15px",
                    marginTop: "20px",
                    padding: "10px 10px 10px 10px",
                  }}
                >
                  <table>
                    <tbody>
                      <tr>
                        <th width="200px">
                          <small>User ID</small>
                        </th>
                        <td>
                          <small>:</small>
                        </td>
                        <td>
                          <small>{this.state.dataDelete.id}</small>
                        </td>
                      </tr>
                      <tr>
                        <th width="200px">
                          <small>Name</small>
                        </th>
                        <td>
                          <small>:</small>
                        </td>
                        <td>
                          <small>
                            {this.state.dataDelete.attributes.full_name}
                          </small>
                        </td>
                      </tr>
                      <tr>
                        <th width="200px">
                          <small>Username</small>
                        </th>
                        <td>
                          <small>:</small>
                        </td>
                        <td>
                          <small>
                            {this.state.dataDelete.attributes.username}
                          </small>
                        </td>
                      </tr>
                      <tr>
                        <th width="200px">
                          <small>Role</small>
                        </th>
                        <td>
                          <small>:</small>
                        </td>
                        <td>
                          <small>
                            {
                              this.state.dataDelete.attributes.role[0]
                                .custom_name
                            }
                          </small>
                        </td>
                      </tr>
                    </tbody>
                  </table>
                </div>
              </div>
            }
            show={this.state.modalDelete}
            deleteData={() => {
              this.props.destroyUser(this.state.dataDelete.id).then(() => {
                let data_delete = [...this.state.data];
                data_delete.splice(this.state.tabIndex, 1);
                this.setState({
                  modalDelete: false,
                  data: data_delete,
                  tabIndex: -1,
                });
                toast.success("data has been deleted !", {
                  position: toast.POSITION.TOP_RIGHT,
                  autoClose: 3000,
                });
              });
            }}
            onHide={() => this.setState({ modalDelete: false })}
          />
        )}
        <Row>
          <Col lg={12} md={12} sm={12}>
            <Card style={{ backgroundColor: "white", borderRadius: "10px" }}>
              <Row
                className="position-relative show-grid"
                style={{ margin: "20px 20px 0px 20px" }}
              >
                <Col xs={12}>
                  <Row className="align-items-center justify-content-between">
                    <Col xs="6">
                      <FindData
                        placeholder="Search"
                        onChange={this.handleFindData}
                      />
                    </Col>
                    <Col
                      xs="2"
                      md={{ span: 2, offset: 2 }}
                      style={{ textAlign: "right" }}
                    >
                      <Button
                        onClick={this.openCreateUserModal}
                        variant="success"
                        className="mb-2"
                      >
                        Create User
                      </Button>
                    </Col>
                  </Row>
                </Col>
              </Row>
              <Row>
                <Col xs={12}>
                  <DataTable2
                    data={this.props.data}
                    totalData={this.props.total}
                    columns={this.columns}
                    fetchData={this.fetchData}
                    pageCount={this.state.paginationUsers.pageCount}
                  />
                </Col>
              </Row>
            </Card>
          </Col>
        </Row>
      </Layout>
    );
  }
}
function mapStateToProps(state) {
  const { users, total, include } = state.user;
  return {
    data: users,
    dataLogin: state.auth.user,
    dataRole: state.role.data,
    include: include,
    total,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    getRole: (opts) => dispatch(getRole(opts)),
    searchUser: (payload) => dispatch(searchUser(payload)),
    editUserCompany: (id, payload, opts) =>
      dispatch(editUserCompany(id, payload, opts)),
    assignUserToCompany: (payload, opts) =>
      dispatch(assignUserToCompany(payload, opts)),
    storeUser: (payload, opts) => dispatch(storeUser(payload, opts)),
    getUsers: (opts) => dispatch(getUsers(opts)),
    destroyUser: (id) => dispatch(destroyUser(id)),
    deleteUserFromCompany: (id) => dispatch(deleteUserFromCompany(id))
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(index);
