import PaymentTransactionService from "../../service/payment_transaction";

export const getPaymentTransaction = (opts) => (dispatch) => {
    return PaymentTransactionService.getPaymentTransaction(opts).then(
    (data) => {
      dispatch({
          type: "GET_PAYMENT_TRANSACTION",
          payload: {
              data: data.data.data,
              include: data.data.included,
          }
      })
      return Promise.resolve();
    },
    (error) => {
      dispatch({
        type: "FAIL",
      });

      return Promise.reject();
    }
  );
};

export const storePaymentTransaction = (payload, opts) => (dispatch) => {
  return PaymentTransactionService.storePaymentTransaction(payload, opts).then(
    (data) => {
      dispatch({
        type: "STORE_PAYMENT_TRANSACTION",
        payload: {
          data: data.data.data,
          include: data.data.included,
        },
      });
      return Promise.resolve();
    },
    (error) => {
      dispatch({
        type: "FAIL",
      });

      return Promise.reject();
    }
  );
};

