export const addNotification = params => {
  if (!params) return;

  if (!params.level) {
    params.level = "warning";
  }

  const classes = {
    success: "custom-toast-success",
    warning: "custom-toast-warning"
  };
  toast.success(params.message, {
    className: classes[params.level],
    position: toast.POSITION.TOP_RIGHT,
    autoClose: params.duration ? params.duration : 3000
  });
};
export const handleError = error => {
  console.warn("Handling error:", error.message);
  toast.error(error.message, {
    position: toast.POSITION.TOP_RIGHT,
    autoClose: 3000
  });
  hideLoadingSpinner();
};
export const showLoadingSpinner = () => {
  if (!_loadingSpinner) return;

  _loadingSpinner.show();
};
export const hideLoadingSpinner = () => {
  if (!_loadingSpinner) return;

  _loadingSpinner.hide();
};

const mapStateToProps = state => {
  const {
    data,
    companyIncluded
  } = state.company;
  return {
    company: data,
    companyIncluded
  };
};

const mapDispatchToProps = dispatch => {
  return {
    getCompany: payload => dispatch(getCompany(payload)),
    updateBillingInvoice: (id, payload, opts) => dispatch(updateBillingInvoice(id, payload, opts))
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Layout);

