import BaseService from './baseService.js'
import cloneDeep from 'lodash/cloneDeep'

class ProductService extends BaseService{
    getProduct(opts = {}){
        this.endPoint = '/get-products'
        // ?include=product_attachment,product_visibility,product_category'
        return this.get(opts)
    }

    storeProduct(payload, opts ={}){
        this.endPoint = '/products?include=product_attachment,product_visibility,product_category,product_schedule'
        const newOpts = cloneDeep(opts, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
        })
        return this.post(payload, newOpts);
    }


    updateProduct(payload, opts ={}){
        this.endPoint = '/products/' + payload.get('id') + '?include=product_attachment,product_visibility,product_category,product_schedule'
        const newOpts = cloneDeep(opts, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
        })
        return this.post(payload, newOpts)
    }

    deleteProduct(id, opts = {}){
        this.endPoint = '/products'
        return this.delete(id, opts)
    }

    // deleteAttachmentProductCategory(id, opts={}){
