import * as React from "react";
import { Card, Col, Modal, Row, Form, Button } from "react-bootstrap";
import {
  getCompanies,
  toggleStatusCompany,
} from "../../redux/actions/company";
import { connect } from "react-redux";
import Layout, {
  addNotification,
  handleError,
  showLoadingSpinner,
  hideLoadingSpinner,
} from "../../components/Layout";
import DataTable2 from "../../components/DataTable2";
import Switch from "react-switch";
import NumberFormat from "react-number-format";

const SetBillingForm = ({ formData, handleInput }) => (
  <Form>
    <Form.Group as={Row} controlId="formPlaintextPassword">
      <Form.Label column sm="4">
        <span style={{ fontSize: "13px" }}>Amount</span>
      </Form.Label>
      <Col sm="8">
        <NumberFormat
          required
          className="form-control"
          prefix="Rp"
          value={formData.amount}
          onValueChange={handleInput("amount")}
          thousandSeparator={"."}
          decimalSeparator=","
          allowNegative={false}
          isAllowed={(values) => {
            const { floatValue } = values;
            return floatValue > 0;
          }}
        />
      </Col>
    </Form.Group>
  </Form>
);

class Index extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      checked: false,
      formData: {
        amount: 0,
      },
      getCompaniesOpts: {
        params: {
          "page[limit]": 100,
          include: "company",
        },
      },
      editModalVisible: false,
      confirmationModalVisible: false,
    };
  }

  columns = [
    {
      Header: "Company Name",
      accessor: "attributes.business_name",
    },
    {
      Header: "Email",
      accessor: "attributes.email",
    },
    {
      Header: "Status",
      accessor: "attributes.activated",
      Cell: (props) => {
        const activated =
          props.cell.row.original.attributes.email_verified_at ? true : false;
        return (
          <Switch
            checked={activated}
            onChange={this.handleChange(props.cell.row.original)}
            offColor="#c6c5c5"
            onColor="#86d4b3"
            onHandleColor="#0DAA66"
            offHandleColor="#F1F1F1"
            handleDiameter={30}
            uncheckedIcon={false}
            checkedIcon={false}
            boxShadow="0px 1px 5px rgba(0, 0, 0, 0.6)"
            activeBoxShadow="0px 0px 1px 10px rgba(0, 0, 0, 0.2)"
            height={20}
            width={48}
            className="react-switch"
            id="material-switch"
          />
        );
      },
    },
  ];

  async editData(data, index) {
    let items = [...this.state.data];
    data.status == "Active"
      ? (data.status = "Inactive")
      : (data.status = "Active");
    items[index] = data;
    this.setState({
      data: items,
    });
  }

  componentDidMount() {
    // this.props.getUserCompany({
    //   params: {
    //     "page[limit]": 100,
    //     include: "company",
    //   },
    // });
    const { getCompaniesOpts } = this.state;
    this.props.getCompanies(getCompaniesOpts);
  }

  closeEditModal = () => {
    this.setState({
      editModalVisible: false,
    });
  };

  closeConfirmationModal = () => {
    this.setState({
      confirmationModalVisible: false,
    });
  };

  handleChange = (row) => (e) => {
    const { id, business_name, user_enterprise, email_verified_at } = row.attributes;
    if (!email_verified_at) {
      this.setState({
        formData: {
          ...this.state.formData,
          id,
          business_name,
          activated: !email_verified_at ? true:false,
        },
        editModalVisible: true,
      });
    } else {
      this.setState({
        formData: {
          ...this.state.formData,
          id,
          business_name,
          activated: !email_verified_at ? true:false,
        },
        confirmationModalVisible: true,
      });
    }
  };

  handleInput = (key) => (e) => {
    this.setState({
      formData: {
        ...this.state.formData,
        [key]: e.floatValue,
      },
    });
  };

  handleSubmitEditCompany = async (e) => {
    if (e) e.preventDefault();
    const { formData, getCompaniesOpts } = this.state;
    showLoadingSpinner();
    try {      
      if (formData.activated && formData.amount < 10000) {
        throw {
          message: "Amount cannot be less than 10.000",
        };
      }
      await this.props.toggleStatusCompany(formData.id, formData);
      await this.props.getCompanies(getCompaniesOpts);
      let textNotification = formData.activated ? 'Activate' : 'Deactivate';
      addNotification({
        level: "success",
        message: `${textNotification} company ${formData.business_name} successfully`,
      });
      this.setState({
        editModalVisible: false,
        confirmationModalVisible: false,
      });
    } catch (e) {
      handleError(e);
    }

    hideLoadingSpinner();
  };

  render() {
    const { formData } = this.state;
    return (
      <Layout title="Activation" classname="dashboard theme-light">
        {/* Modal Edit Billing Invoice */}
        <Modal
          show={this.state.editModalVisible}
          onHide={this.closeEditModal}
          dialogClassName="modal-90w"
          aria-labelledby="example-custom-modal-styling-title"
          centered
        >
          <Modal.Header
            closeButton
            style={{ border: "none", paddingLeft: "20px" }}
          >
            <Modal.Title id="example-custom-modal-styling-title">
              <small>Set Billing Invoice Monthly</small>
            </Modal.Title>
          </Modal.Header>
          <Modal.Body style={{ padding: "20px 30px 20px 30px" }}>
            <SetBillingForm
              handleInput={this.handleInput}
              formData={this.state.formData}
            />
          </Modal.Body>
          <Modal.Footer style={{ border: "none" }}>
            <Button
              className="btn-secondary-semanggi"
              onClick={this.closeEditModal}
            >
              Cancel
            </Button>
            <Button
              className="btn-success-semanggi"
              onClick={this.handleSubmitEditCompany}
            >
              Submit
            </Button>
          </Modal.Footer>
        </Modal>
        {/* Modal Confirmation */}
        <Modal
          show={this.state.confirmationModalVisible}
          onHide={this.closeConfirmationModal}
          dialogClassName="modal-90w"
          aria-labelledby="example-custom-modal-styling-title"
          centered
        >
          <Modal.Header
            closeButton
            style={{ border: "none", paddingLeft: "20px" }}
          >
            <Modal.Title id="example-custom-modal-styling-title">
              <h3>Confirmation</h3>
            </Modal.Title>
          </Modal.Header>
          <Modal.Body style={{ padding: "20px 30px 20px 30px" }}>
            <p>
              Are you sure you want to deactivate the company{" "}
              <b>{formData.business_name}</b>?
            </p>
          </Modal.Body>
          <Modal.Footer style={{ border: "none" }}>
            <Button
              className="btn-secondary-semanggi"
              onClick={this.closeConfirmationModal}
            >
              Cancel
            </Button>
            <Button
              className="btn-success-semanggi"
              onClick={this.handleSubmitEditCompany}
            >
              Submit
            </Button>
          </Modal.Footer>
        </Modal>
        <Row>
          <Col lg={12} md={12} sm={12}>
            <Card style={{ backgroundColor: "white", borderRadius: "10px" }}>
              <Row>
                <Col xs={12}>
                  <DataTable2
                    data={this.props.data}
                    columns={this.columns}
                    showGlobalFilter={true}
                    totalData={this.props.total}
                  />
                </Col>
              </Row>
            </Card>
          </Col>
        </Row>
      </Layout>
    );
  }
}

const mapStateToProps = (state) => {
  const { companies, include, total } = state.company;
  let newCompanies = companies.filter((user) => {
    if (user["attributes"]["user_enterprise"]) {
      return user;
    }
  });
  console.log(newCompanies)
  return {
    data: newCompanies,
    total,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    getCompanies: (opts) => dispatch(getCompanies(opts)),
    toggleStatusCompany: (id, payload) =>
      dispatch(toggleStatusCompany(id, payload)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Index);
