import * as React from "react";
import Link from "next/link";
import Router from "next/router";
import { Nav, NavDropdown, FormControl, Form, Badge, Col, Row } from "react-bootstrap";
import { connect } from "react-redux";
import { logout } from "redux/actions/auth";
import ModalCustom from './ModalCustom'
import {changePassword} from './../redux/actions/user'
import {alertSuccess} from './alert'

class Header extends React.Component {

  constructor(props){
    super(props)
    this.state = {
      modal:false,
      password: null,
      confirm_password: null
    }
  }

  handleChangePassword = (e) => {
    e.preventDefault();
    let payload = {
      password: this.state.password,
      password_confirmation: this.state.confirm_password
    }
    this.props.changePassword(payload).then((data) => {
      this.setState({
        modal: false
      })
      alertSuccess('Change Password Is Successfully')
    })
  }

  handleLogOut = (e) => {
    if (e) e.preventDefault();
    this.props.logout();
    Router.replace("/login");
  };
  render() {
    return (
      <Nav className="align-items-center">
        <ModalCustom
            customTitle={
                <div>
                <h4
                    style={{
                    fontSize: "1.3rem",
                    color: "#252733",
                    fontWeight: "bold",
                    }}
                >
                  Change Password
                </h4>
                {/* <h5 style={{ fontSize: "0.875rem", color: "#252733" }}>
                    Select one or multiple schedule to you want to book. Rent price
                    is{" "}
                    <span style={{ fontWeight: "bold" }}> */}
                    {/* Rp{" "}
                    {formatCurrency(
                        this.state.selectedProduct &&
                        this.state.selectedProduct.attributes
                        ? this.state.selectedProduct.attributes.selling_price
                        : ""
                    )}{" "}
                    per hour */}
                    {/* </span>
                    .
                </h5> */}
                </div>
            }
            body={
                <Form style={{ padding: '10px 65px 10px 65px' }}>
                  <Form.Group as={Row} controlId="formPlaintextPassword">
                      <Form.Label column sm="5">
                      New Password
                      </Form.Label>
                      <Col sm="7">
                      <Form.Control type="password" placeholder="New Password" onChange={(e) => this.setState({ password : e.target.value })} />
                      </Col>
                  </Form.Group>

                  <Form.Group as={Row} controlId="formPlaintextPassword">
                      <Form.Label column sm="5">
                      Confirm Password
                      </Form.Label>
                      <Col sm="7" style={{ textAlign: 'end' }}>
                      <Form.Control type="password" placeholder="Confirm Password" onChange={(e) => this.setState({ confirm_password: e.target.value })} />
                      </Col>
                  </Form.Group>
                </Form>
            }
            show={this.state.modal}
            // size={"sm"}
            onSubmit={this.handleChangePassword}
            submitTitle={"Save"}
            onHide={() => {
                this.setState({ modal: false });
            }}
            onClose={() => {
                this.setState({ modal: false });
            }}
        />
        <Nav.Link className="notification-cont position-relative">
          <Link href="#">
            <span>
              <i className="fa fa-bell" aria-hidden="true" />
              <Badge
                variant="danger"
                className="rounded-circle position-absolute"
              >
                3
              </Badge>
            </span>
          </Link>
        </Nav.Link>
        <Nav.Link style={{ cursor: "auto" }}>
          {/* <Link href="#"> */}
          <span>{this.props.user && this.props.user.full_name}</span>
          {/* </Link> */}
        </Nav.Link>
        <NavDropdown
          title={
            <div className="d-inline-block user-avatar">
              <img
                src="https://semanggi.app/images/semanggi-app-logo-1-247x247.jpg"
                className="img-fluid rounded-circle"
              />
            </div>
          }
          id="basic-nav-dropdown"
          className="profile-dropdown  p-0  mr-2 position-relative"
        >
          <NavDropdown.Item className="pt-2 pb-2 theme-container">
            <Link href="/profile">
              <a className="d-block navbarLink">
                <p className="mb-0">Profile</p>
              </a>
            </Link>
          </NavDropdown.Item>
          <NavDropdown.Item className="pt-2 pb-2 theme-container" onClick={() => this.setState({ modal:true })}>
            <Link href="#">
              <a className="d-block navbarLink">
                <p className="mb-0">Change Password</p>
              </a>
            </Link>
          </NavDropdown.Item>
          <NavDropdown.Item className="pt-2 pb-2 theme-container">
            <a onClick={this.handleLogOut} className="d-block navbarLink">
              <p className="mb-0">Log Out</p>
            </a>
          </NavDropdown.Item>
        </NavDropdown>
      </Nav>
    );
  }
}

const mapStateToProps = (state) => {
  const { user } = state.auth;
  return {
    user,
  };
};
function mapDispatchToProps(dispatch) {
  return {
    changePassword: (payload, opts) => dispatch(changePassword(payload, opts)),
    logout: () => dispatch(logout())
  };
}
export default connect(mapStateToProps, mapDispatchToProps)(Header);
