const initialState = {
  data: null,
  included: {},
  dataImage: {},
  total: 0
}

export default function (state = initialState, action) {
  const { type, payload } = action;
  switch (type) {


    case "GET_CATEGORY_PRODUCT":
      return {
        ...state,
        data: payload.data,
        included: payload.included,
        total: payload.total
      };
    case "STORE_CATEGORY_PRODUCT":
      return {
        ...state,
        data: payload.data,
      };

      case "UPDATE_CATEGORY_PRODUCT":
      return {
        ...state,
        data: payload.data,
      };

    case "FAIL":
      return {
        ...state,
        data: null,
      };
    case "DELETE_CATEGORY_PRODUCT":
      return {
        ...state,
      };
    case "DELETE_ATTACHMENT_CATEGORY_PRODUCT" :
       return{
         ...state
       }
       case "STORE_CATEGORY_PRODUCT_ATTACHMENT":
        return {
          ...state,
          dataImage: payload.data,
        };
    default:
      return state;
  }
}
