import UserService from "../../service/user";
import { hideLoadingSpinner } from "components/Layout";

export const getCustomer = (payload) => (dispatch) => {
  return UserService.getCustomer(payload).then(
    (data) => {
      dispatch({
        type: "GET_CUSTOMER",
        payload: {
          data: data.data.data,
          included: data.data.included,
          total: data.data.meta.pagination.total,
        },
      });
      return Promise.resolve();
    },
    (error) => {
      dispatch({
        type: "FAIL",
      });
      hideLoadingSpinner();
      return Promise.reject();
    }
  );
};

export const getUsers = (opts) => (dispatch) => {
