import ItemService from "../../service/item";

export const getItem = (opts) => (dispatch) => {
    return ItemService.getItem(opts).then(
    (data) => {
      dispatch({
          type: "GET_ITEM",
          payload: {
            data: data.data.data,
            included: data.data.included,
            total: data.data.meta.pagination.total,
            amount_income: data.data.amount_income,
            amount_expense: data.data.amount_expense,
            quantity_expense: data.data.quantity_expense,
            quantity_income: data.data.quantity_income,
          }
      })
      return Promise.resolve();
    },
    (error) => {
      dispatch({
        type: "FAIL",
      });

      return Promise.reject();
    }
  );
};

export const getItemIncome = (payload) => (dispatch) => {
  return ItemService.getItemIncome(payload).then(
  (data) => {
    dispatch({
        type: "GET_ITEM_INCOME",
        payload: {
            data: data.data.data,
            included: data.data.included,
            total: data.data.meta.pagination.total
        }
    })
    return Promise.resolve();
  },
  (error) => {
    dispatch({
      type: "FAIL",
    });

    return Promise.reject();
  }
);
};

export const getItemExpense = (payload) => (dispatch) => {
  return ItemService.getItemExpense(payload).then(
  (data) => {
    dispatch({
        type: "GET_ITEM_EXPENSE",
        payload: {
            data: data.data.data,
            included: data.data.included,
            total: data.data.meta.pagination.total
        }
    })
    return Promise.resolve();
  },
  (error) => {
    dispatch({
      type: "FAIL",
    });

    return Promise.reject();
  }
);
};

export const getItemSubmission = (opts) => (dispatch) => {
  return ItemService.getItemSubmission(opts).then(
  (data) => {
    dispatch({
        type: "GET_ITEM_SUBMISSION",
        payload: {
            data: data.data.data,
        }
    })
    return Promise.resolve();
  },
  (error) => {
    dispatch({
      type: "FAIL",
    });

    return Promise.reject();
  }
);
};
