function mapStateToProps(state) {
  const {
    dataCategory,
    data,
    include,
    totalSubmission,
    dataDraft,
    includeDraft,
    totalSubmissionDraft,
    total_amount,
    total_potensial
  } = state.submission;
  return {
    dataCategory: dataCategory,
    data: data,
    include: include,
    totalSubmission: totalSubmission,
    dataDraft: dataDraft,
    includeDraft: includeDraft,
    totalSubmissionDraft: totalSubmissionDraft,
    total_amount,
    total_potensial,
    dataLogin: state.auth.user
  };
}

function mapDispatchToProps(dispatch) {
  return {
    getSubmission: opts => dispatch(getSubmission(opts)),
    getSubmissionDraft: payload => dispatch(getSubmissionDraft(payload)),
    getSubmissionCategory: payload => dispatch(getSubmissionCategory(payload)),
    storeSubmissionCategory: payload => dispatch(storeSubmissionCategory(payload)),
    updateSubmissionCategory: payload => dispatch(updateSubmissionCategory(payload)),
    destroy: id => dispatch(destroy(id)),
    destroySubmission: id => dispatch(destroySubmission(id)),
    updateStatusSubmission: (id, payload, opts) => dispatch(updateStatusSubmission(id, payload, opts)),
    updateFulfilledSubmission: (id, payload, opts) => dispatch(updateFulfilledSubmission(id, payload, opts))
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(index);

