                                        </Row>
                                        <Row>
                                            <Col xs={12}>
                                                <DataTable
                                                    data={this.state.dataDraft}
                                                    columns={this.state.columnsDraft}
                                                    highlightOnHover
                                                    paginationTotalRows={this.props.totalSubmissionDraft}
                                                    paginationPerPage={this.state.numDraft}
                                                    selectableRows
                                                    wrap={true}
                                                    pagination
                                                    paginationServer
                                                    onChangePage={this.handlePageChangeDraft}
                                                    onChangeRowsPerPage={this.handlePerRowsChangeDraft}
                                                />
                                            </Col>
                                        </Row>
                                    </Tab>
                                    <Tab eventKey="category" title="Category" className="mr-5">
                                    <Modal
                                        show={this.state.modalCategory}
                                        onHide={() => this.setState({ modalCategory: false, categoryName: null, max:null, tabIndexCategory: -1  })}
                                        aria-labelledby="contained-modal-title-vcenter"
                                        centered
                                    >
                                        <Modal.Header closeButton style={{ border:'none' }}>
                                            <Modal.Title id="contained-modal-title-vcenter">
                                                {this.state.tabIndexCategory == -1 ? 'Create Submission Category' : 'Edit Submission Category'}
                                            </Modal.Title>
                                        </Modal.Header>
                                        <Modal.Body className="p-5">
                                            <Row>
                                                <Col xs={4} className="pt-1">Category Name</Col>
                                                <Col xs={8}><Form.Control type="text" placeholder="Category Name" onChange={(e) => this.setState({
                                                    categoryName: e.target.value
                                                })} defaultValue={this.state.categoryName} /></Col>
                                            </Row>
                                            <Row className="pt-4">
                                                <Col xs={4} className="pt-1">Maximum Pending</Col>
                                                <Col xs={8}><Form.Control type="text" placeholder="Maximum Pending" onChange={(e) => this.setState({
                                                    max: e.target.value
                                                })} defaultValue={this.state.max} /></Col>
                                            </Row>

                                            <Row className="pt-1">
                                                <Col xs={4} className="pt-1"></Col>
                                                <Col xs={8}><small><i>Set maximum pending transactions allowed for this submission category</i></small></Col>
                                            </Row>

                                        </Modal.Body>
                                        <Modal.Footer style={{ border:'none' }}>
                                            <Button variant="secondary" onClick={() => this.setState({ modalCategory: false, categoryName: null, max:null,tabIndexCategory: -1  })}>CANCEL</Button>
                                            <Button variant="success" onClick={this.saveCategory}>{this.state.tabIndexCategory == -1 ? 'CREATE' : 'SAVE'}</Button>
                                        </Modal.Footer>
                                    </Modal>
                                    {this.state.dataDeleteCategory && <Confirm 
                                        title="Delete Submission Category"
                                        body={(
                                        <div>
                                            <small>Are you sure you want to Delete Submission Category with the folowing details:  </small>
                                            <div style={{ background: '#F7F8FC', borderRadius: '15px', marginTop: '20px', padding: '10px 10px 10px 10px' }}>
                                            <table>
                                                <tbody>
                                                <tr>
                                                    <th width="100px"><small>Categoy Name</small></th>
                                                    <td><small>:</small></td>
                                                    <td><small>{this.state.dataDeleteCategory.attributes.category_name}</small></td>
                                                </tr>
                                                <tr>
                                                    <th width="100px"><small>Maximum Pending</small></th>
                                                    <td><small>:</small></td>
                                                    <td><small>{this.state.dataDeleteCategory.attributes.maximum}</small></td>
                                                </tr>
                                                </tbody>
                                            </table>
                                            </div>
                                        </div>)}
                                        show={this.state.modalDeleteCategory}
                                        deleteData={() => {
                                            this.props.destroy(this.state.dataDeleteCategory.id).then(() => {
                                                let data_delete = [...this.state.dataCategory]
                                                data_delete.splice(this.state.tabIndexCategory, 1)
                                                this.setState({ modalDeleteCategory: false, dataCategory: data_delete, dataDeleteCategory:null, tabIndexCategory: -1})
                                                toast.success("Data Berhasil di Hapus", {
                                                    position: toast.POSITION.TOP_RIGHT,
                                                    autoClose: 3000
                                                })
                                            })

                                        }}
                                        onHide={() => this.setState({ modalDeleteCategory: false})}
                                    />

                                    }
                                        <Row>
                                            <Col style={{ textAlign:'end' }} xs={12}><Button variant="success" onClick={() => this.setState({ modalCategory: true  })}>CREATE SUBMISSION CATEGORY</Button></Col>
                                        </Row>
                                        <Row>
                                            <Col style={{ textAlign:'right' }} xs={12}>
                                                <DataTable
                                                    data={this.state.dataCategory}
                                                    columns={this.state.columnsCategory}
                                                    highlightOnHover
                                                    pagination
                                                    // paginationServer
                                                />
                                            </Col>
                                        </Row>
                                    </Tab>
                                </Tabs>
                            </Col>
                            <Col xs={4}>
                            </Col>
                        </Row>
                        <Row className="position-relative show-grid m-2">
                            <Col xs={12}>

                            </Col>
                        </Row>
                    </Card>
                </Col>
            </Row>
        </Layout>
      );
    }
  }

function mapStateToProps ( state ) {
    const { dataCategory, data, include, totalSubmission, dataDraft, includeDraft, totalSubmissionDraft, total_amount, total_potensial } = state.submission;
    return {
        dataCategory: dataCategory,
        data: data,
        include: include,
        totalSubmission: totalSubmission,
        dataDraft: dataDraft,
        includeDraft: includeDraft,
        totalSubmissionDraft: totalSubmissionDraft,
        total_amount,
        total_potensial,
        dataLogin: state.auth.user,
    };
}

  function mapDispatchToProps (dispatch) {
    return {
        getSubmission: (opts) => dispatch(getSubmission(opts)),
        getSubmissionDraft: (payload) => dispatch(getSubmissionDraft(payload)),
        getSubmissionCategory: (payload) => dispatch(getSubmissionCategory(payload)),
        storeSubmissionCategory: (payload) => dispatch(storeSubmissionCategory(payload)),
        updateSubmissionCategory: (payload) => dispatch(updateSubmissionCategory(payload)),
        destroy: (id) => dispatch(destroy(id)),
        destroySubmission: (id) => dispatch(destroySubmission(id)),
        updateStatusSubmission: (id,payload,opts) => dispatch(updateStatusSubmission(id,payload,opts)),
        updateFulfilledSubmission: (id,payload,opts) => dispatch(updateFulfilledSubmission(id,payload,opts)),
    }
  }


export default connect(mapStateToProps, mapDispatchToProps)(index);
