import * as React from "react";
import {
  Navbar,
  Nav,
  Badge,
  Form,
  FormControl,
  NavDropdown,
  Modal,
} from "react-bootstrap";
import Router from "next/router";
import Link from "../components/ActiveLink";
import encryptedLS from "libs/encryptedLS";
import { withRouter } from "next/router";

const currentUserDontHavePermissions = (permissions) => (userPermissions) => {
  if (!userPermissions) return true;
  return !permissions.some((r) => userPermissions.indexOf(r) >= 0);
};

const isPathnameAndLinkMatch = (url, link) => link.indexOf(url.pathname) >= 0;

class Menu extends React.Component {
  state = {
    show: false,
    setShow: false,
    showModal: false,
    userPermissions: [],
  };

  constructor(props) {
    super(props);
    this.handleOpenModal = this.handleOpenModal.bind(this);
    this.handleCloseModal = this.handleCloseModal.bind(this);
    this.state = {
      show: false,
      setShow: false,
    };
  }

  componentDidMount() {
    let currentSession = null;
    currentSession = encryptedLS.get("___user_data");
    let userPermissions = [];
    if (!currentSession) {
      Router.replace("/login");
    }
    userPermissions = currentSession.permissions;
    this.setState({
      userPermissions,
    });
  }

  handleOpenModal() {
    this.setState({ showModal: true });
  }

  handleCloseModal() {
    this.setState({ showModal: false });
  }

  render() {
    const { userPermissions } = this.state;
    const { router } = this.props;
    return (
      <div>
        <Navbar id="responsive-navbar-nav" className="p-0">
          <Nav className="sidebar-contents">
            <Link
              href="#"
              activeClassName="active"
              isHidden={currentUserDontHavePermissions([
                "Read Billing Invoices",
              ])(userPermissions)}
            >
              <a className="mt-3 mb-4 menu-super-admin">
                <span>Super Admin</span>
              </a>
            </Link>
            <Link href="/dashboard" activeClassName="active">
              <a className="nav-link data-table">
                <img
                  className="mr-2 ml-2"
                  style={{ width: "15px" }}
                  src="https://storage.googleapis.com/spaces-semanggi-app/back-office/super-entity/png/dashboard.png"
                />{" "}
                <span className="span-text">Dashboard</span>
              </a>
            </Link>

            <Link
              href="/billing"
              activeClassName="active"
              isHidden={currentUserDontHavePermissions([
                "Read Billing Invoices",
              ])(userPermissions)}
            >
              <a className="nav-link data-table">
                <img
                  className="mr-2 ml-2"
                  style={{ width: "15px" }}
                  src="https://storage.googleapis.com/spaces-semanggi-app/back-office/super-entity/png/billing.png"
                />{" "}
                <span className="span-text">Billing</span>
              </a>
            </Link>

            <NavDropdown
              isHidden={currentUserDontHavePermissions([
                "Read All Companies",
              ])(userPermissions)}
              className="elements"
              active={router.pathname.indexOf("customer") > -1}
              title={
                <span>
                  <img
                    className="mr-2 ml-2"
                    style={{ width: "14px" }}
                    src="https://storage.googleapis.com/spaces-semanggi-app/back-office/super-entity/png/user-alt.png"
                  />{" "}
                  <span className="span-text">Customer</span>{" "}
                </span>
              }
              id="uielemets"
            >
              <li>
                <Link href="/customer/activation" activeClassName="active">
                  <a className="child-nav-list dropdown-item buttons">
                    Activation
                  </a>
                </Link>
              </li>
              <li>
                <Link href="/customer/master-data" activeClassName="active">
                  <a className="child-nav-list dropdown-item buttons">
                    Master Data
                  </a>
                </Link>
              </li>
            </NavDropdown>

            <Link
              href="#"
              activeClassName="active"
              isHidden={currentUserDontHavePermissions([
                "Create Billing Invoices",
              ])(userPermissions)}
            >
              <a className="mt-4 mb-4 menu-super-admin">
                <span>Back Office</span>
              </a>
            </Link>

            <NavDropdown
              className="elements"
              title={
                <span>
                  <img
                    className="mr-2 ml-2"
                    style={{ width: "15px", height: "17px" }}
                    src="https://storage.googleapis.com/spaces-semanggi-app/back-office/super-entity/png/compare-arrows.png"
                  />{" "}
                  <span className="span-text">Submissions</span>{" "}
                </span>
              }
              id="uielemets"
            >
              <li>
                <Link href="/submissions/income" activeClassName="active">
                  <a className="child-nav-list dropdown-item buttons">Income</a>
                </Link>
              </li>
              <li>
                <Link href="/submissions/expenses" activeClassName="active">
                  <a className="child-nav-list dropdown-item buttons">
                    Expenses
                  </a>
                </Link>
              </li>
            </NavDropdown>

            <NavDropdown
              className="form"
              title={
                <span>
                  <img
                    className="mr-2 ml-2"
                    style={{ width: "14px" }}
                    src="https://storage.googleapis.com/spaces-semanggi-app/back-office/super-entity/png/database.png"
                  />{" "}
                  <span className="span-text">Master Data</span>{" "}
                </span>
              }
              id="formstuffs"
            >
              <li>
                <Link href="/products" activeClassName="active">
                  <a className="child-nav-list dropdown-item form-elements">
                    Product
                  </a>
                </Link>
              </li>
              <li>
                <Link href="/items" activeClassName="active">
