                                                <Row className="mt-3 justify-content-md-start">
                                                    {
                                                        this.state.dataImage.map((data, index) => {
                                                            return (
                                                                <Col xs={4} key={index}>
                                                                    <img
                                                                        width={'100%'}
                                                                        height={200}
                                                                        className="mr-3"
                                                                        src={data.attributes.file_location+data.attributes.file_name}
                                                                        alt="Generic placeholder"
                                                                    />

                                                                    <Badge variant="danger" style={{width: '100%'}} onClick={async () => {
                                                                        this.props.destroyAttachment(data.id).then(()=>{
                                                                            let delete_data = [...this.state.dataImage]
                                                                            delete_data.splice(index, 1) 
                                                                            let items = [...this.state.dataCategory];
                                                                            let item = {...items[this.state.tabIndexCategory]};
                                                                            let attributes = {...item.attributes}
                                                                            let attachments = [...attributes.attachemnts]
                                                                            attachments.splice(index, 1)
                                                                            items[this.state.tabIndexCategory].attributes.attachemnts = attachments;
                                                                            this.setState({
                                                                                dataImage: delete_data,
                                                                                dataCategory: items,
                                                                            })

                                                                            alertSuccess('Data Image Berhasil di Hapus')
                                                                        })


                                                                    }}>
                                                                        Hapus Image
                                                                    </Badge>
                                                                </Col>
                                                            )
                                                        }) 
                                                    }
                                                </Row>
                                            }


                                        </Modal.Body>
                                        <Modal.Footer style={{ border:'none' }}>
                                            <Button variant="secondary" onClick={() => this.setState({ modalAttachmentCategory: false,tabIndexCategory: -1  })}>CANCEL</Button>
                                            {this.state.dataImage.length < 6 && <Button variant="success" type="submit" >Save</Button>}

                                        </Modal.Footer>
                                        </Form>

                                    </Modal>
                                    {/* Modal Form Category Product */}
                                    <Modal
                                            show={this.state.modalCategory}
                                            onHide={() => this.setState({
                                                modalCategory: false,
                                                nama_category: null
                                            })}
                                            dialogClassName="modal-90w"
                                            aria-labelledby="example-custom-modal-styling-title"
                                            centered
                                        >
                                        <Modal.Header closeButton style={{ border:'none', paddingLeft:'20px' }}>
                                            <Modal.Title id="example-custom-modal-styling-title">
                                            <small>{this.state.tabIndexCategory == -1 ? 'Add Category' : 'Edit Category'}</small>
                                            </Modal.Title>
                                        </Modal.Header>
                                        <Modal.Body style={{ padding: '20px 30px 20px 30px' }}>
                                            <Form>

                                                <Form.Group as={Row} controlId="formPlaintextPassword">
                                                    <Form.Label column sm="4">
                                                    <span style={{ fontSize: '13px' }}>Category Name</span>
                                                    </Form.Label>
                                                    <Col sm="8">
                                                    <Form.Control type="text" placeholder="Category Name" defaultValue={this.state.nama_category} onChange={(e) => this.setState({ nama_category: e.target.value })} />
                                                    </Col>
                                                </Form.Group>
                                            </Form>
                                        </Modal.Body>
                                        <Modal.Footer style={{ border:'none' }}>
                                        <Button variant="light" onClick={() => this.setState({
                                        modalCategory: false,
                                        nama_category: null
                                        })}>Cancel</Button>
                                            <Button variant="success" onClick={this.save}>{this.state.tabIndexCategory == -1 ? 'Create' : 'Save'}</Button>
                                        </Modal.Footer>
                                    </Modal>

                                    {/* Modal Delete */}
                                    {this.state.dataDetailCategory && <Confirm 
                                            title="Delete Category Product"
                                            body={(
                                            <div>
                                                <small>Are you sure you want to Delete Product Category named {this.state.dataDetailCategory.attributes.name} ?  </small>

                                            </div>)}
                                            show={this.state.modalDeleteCategory}
                                            deleteData={() => {
                                                this.props.destroy(this.state.dataDetailCategory.attributes.id).then(() => {
                                                    let data_delete = [...this.state.dataCategory]
                                                    data_delete.splice(this.state.tabIndexCategory, 1)
                                                    this.setState({ modalDeleteCategory: false, dataCategory: data_delete, tabIndexCategory: -1})
                                                    alertSuccess('Data berhasil di delete')
                                                })

                                            }}
                                            onHide={() => this.setState({ modalDeleteCategory: false})}
                                        />

                                    }

                                    <Row className="position-relative show-grid" style={{ margin:'40px 20px 0px 20px' }}>
                                        <Col xs={10}>
                                            <Form>
                                                <Form.Row className="align-items-center">
                                                    <Col xs="4">
                                                    <Form.Label htmlFor="inlineFormInputGroup" srOnly>
                                                        Find Category By Name
                                                    </Form.Label>
                                                    <InputGroup className="mb-2">
                                                        <InputGroup.Prepend>
                                                        <InputGroup.Text style={{ backgroundColor:'white' }}><i className="fa fa-search"></i></InputGroup.Text>
                                                        </InputGroup.Prepend>
                                                        <FormControl id="inlineFormInputGroup" placeholder="Find Category By Name" onKeyPress={this.handleSearchCategory} onChange={(e) => this.setState({ inputSearchCategory: e.target.value })} />
                                                    </InputGroup>
                                                    </Col>
                                                </Form.Row>
                                            </Form>
                                        </Col>
                                        <Col xs={2} style={{ textAlign:'end' }}>
                                            <Button variant="success" onClick={() => this.setState({modalCategory: true, nama_category: null})}>Add Category</Button>
                                        </Col>
                                    </Row>
                                    <Row>
                                        <Col xs={12}>
                                            <DataTable
                                                noHeader={true}
                                                paginationTotalRows={this.props.total_product_category}
                                                paginationPerPage={this.state.numCategory}
                                                selectableRows
                                                pagination
                                                paginationServer
                                                onChangePage={this.handlePageChangeCategory}
                                                onChangeRowsPerPage={this.handlePerRowsChangeCategory}
                                                data={this.state.dataCategory}
                                                columns={this.state.columnsCategory}
                                                highlightOnHover
                                                // paginationServer
                                            />
                                        </Col>
                                    </Row>
                                </Tab>
                            </Tabs>
                        </Col>
                        </Row>
                    </Card>
                </Col>
            </Row>
        </Layout>
      );
    }

  }
  function mapStateToProps ( state ) {
    const { data, included, dataImage } = state.product_category;
    const {dataProduct, includeProduct, total} = state.product
    // const  {dataInterfaces} = state.interfaces
    return {
        data: data,
        included: included,
        dataImage: dataImage,
        dataProduct: dataProduct,
        includeProduct: includeProduct,
        // dataInterfaces,
        total,
        total_product_category: state.product_category.total,
        dataItem : state.item.data
    };
}

  function mapDispatchToProps (dispatch) {
    return {
      get: (opts) => dispatch(get(opts)),
      store: (payload) => dispatch(store(payload)),
      update: (payload) => dispatch(update(payload)),
      destroy: (id) => dispatch(destroy(id)),
      destroyAttachment: (id) => dispatch(destroyAttachment(id)),
      createAttachmentProductCategory: (payload) => dispatch(createAttachmentProductCategory(payload)),
      getProduct: (opts) => dispatch(getProduct(opts)),
      destroyProduct: (id) => dispatch(destroyProduct(id)),
      getInterface: () => dispatch(getInterface()),
      storeProduct: (payload) => dispatch(storeProduct(payload)),
      getItemSubmission: (opts) => dispatch(getItemSubmission(opts)),
      updateProduct: (payload) => dispatch(updateProduct(payload))
    }
  }


export default connect(mapStateToProps, mapDispatchToProps)(index);
