import * as React from 'react';
import {Button,Tabs, Tab, Row, Col, Card, Form, InputGroup, FormControl, Modal, Badge} from 'react-bootstrap';
import DataTable from 'react-data-table-component';
import Layout from '../../components/Layout';
import { getContent, storeContent, updateContent, deleteContent, getContentCategory } from "../../redux/actions/content";
import {connect} from 'react-redux'
import {toast} from 'react-toastify'
import dayjs from "dayjs";


class index extends React.Component {

  constructor(props){
    super(props)
    this.state = {
            isServer: true,
            columns:[
                {
                    name: 'ID',
                    selector: 'id',
                    sortable: true,
                    width: '20%'
                },
                {
                    name: 'Content Name',
                    selector: 'attributes.content_name',
                    sortable: true,
                    width: '65%'
                },
                {
                    name: 'Actions',
                    selector: (row, index) => (<div>
                    <img className="mr-2" style={{width: '15px'}} src="https://storage.googleapis.com/spaces-semanggi-app/back-office/super-entity/png/border-color-24px.png" onClick={ () => this.editData(row)} /><img style={{width: '15px'}} src="https://storage.googleapis.com/spaces-semanggi-app/back-office/super-entity/png/delete.png" onClick={() => this.handleDelete(row.attributes.id)} /></div>),
                    width: '10%'
                }
            ],
            payloadContent: {
                content_name: null,
                content: null,
                date: "",
                status: null,
                category_id: null,
            },
            dataCategory: [],
            modal: false,
            tabIndex: -1,
            dataDetail: null,
            data: []
        }

    }


    handleSaveCategory = async (e) => {
        e.preventDefault()
        if(this.state.tabIndex === -1){
            await this.props.storeContent(this.state.payloadContent)
            toast.success("Data Berhasil di Simpan", {
                position: toast.POSITION.TOP_RIGHT,
                autoClose: 3000
            })
        }else{
            await this.props.updateContent(this.state.dataDetail.attributes.id, this.state.payloadContent, {});
            toast.success("Data Berhasil di Update", {
                position: toast.POSITION.TOP_RIGHT,
                autoClose: 3000
            })
        }
        this.getData()
        this.setState({
            modal: false
        })
    }  

    async componentDidMount() {
        await this.props.getContentCategory({}).then(() => {
            this.setState({
                dataCategory: this.props.dataCategory
            })
        })
        this.getData()
        this.CKEditor = require("@ckeditor/ckeditor5-react").CKEditor;
        this.ClassicEditor = require("@ckeditor/ckeditor5-build-classic");
        this.setState({ isServer: false });
    }

    editData = (data) => {
        this.setState({
            modal:true, 
            tabIndex:index, 
            dataDetail:data,
            payloadContent: {
                ...this.state.payloadContent,
                content: data.attributes.content,
                content_name: data.attributes.content_name,
                date: dayjs(data.attributes.date).format("YYYY-MM-DD"),
                status: data.attributes.status,
                category_id: data.attributes.category_id,
                category_name: data.attributes.category_name
            }
        })
    }

    getData = async () => {
        await this.props.getContent({}).then(() => {
            this.setState({
                data: this.props.data
            })
        })
    }

    handleDelete = async (id) => {
        await this.props.deleteContent(id).then(() => {
            this.getData()
            toast.success("Data Berhasil di Delete", {
                position: toast.POSITION.TOP_RIGHT,
                autoClose: 3000
            })
        })
    }

    render () {
      return (

        <Layout title="Content" classname="dashboard theme-light">
        {/* Modal Form */}
        <Modal
            show={this.state.modal}
            onHide={() => this.setState({
                modal: false, 
                tabIndex: -1, 
                statusProduct: null,
                payloadContent: {
                    ...this.state.payloadContent,
                    content_name: "",
                    content: "",
                    date: "",
                    status: "",
                    category_id: "",
                }
            })}
            backdrop="static"
            keyboard={false}
            centered
            size="lg"
            aria-labelledby="contained-modal-title-vcenter"
        >
            <Modal.Header style={{ border:'none' }} closeButton>
            <Modal.Title>{this.state.tabIndex == -1 ? 'Create Contents' : 'Edit Contents'}</Modal.Title>
            </Modal.Header>
            <Form onSubmit={this.saveProduct}>
            <Modal.Body className="p-5">
                <Row>
                    <Col xs={4} className="pt-1">
                        <strong>Content Name</strong>
                    </Col>
                    <Col xs={8}>
                        <Form.Control type="text" defaultValue={this.state.payloadContent.content_name} onChange={(e) => this.setState({
                            payloadContent:{
                                ...this.state.payloadContent,
                                content_name: e.target.value
                            },

                        })} min="0" placeholder="Category Name" />
                    </Col>
                </Row>
                <Row className="pt-3">
                    <Col xs={4} className="pt-1">
                        <strong>Category</strong>
                    </Col>
                    <Col xs={8}>
                        <Form.Control as="select" defaultValue={this.state.payloadContent.category_id} onChange={(e) => this.setState({
                            payloadContent:{
                                ...this.state.payloadContent,
                                category_id: e.target.value
                            },
                        })}>
                            <option value="">-Chose Category-</option>
                            {
                                this.state.dataCategory && this.state.dataCategory.map((data, index) => (
                                    <option key={data.id} value={data.id}>{data.attributes.category_name}</option>
                                ))
                            }
                        </Form.Control>
                    </Col>
                </Row>
                <Row className="pt-3">
                    <Col xs={4} className="pt-1">
                        <strong>Date</strong>
                    </Col>
                    <Col xs={8}>
                        <Form.Control type="date" defaultValue={this.state.payloadContent.date} onChange={(e) => this.setState({
                            payloadContent:{
                                ...this.state.payloadContent,
                                date: e.target.value
                            },

                        })} />
                    </Col>
                </Row>
                <Row className="pt-3">
                    <Col xs={4} className="pt-1">
                        <strong>Status</strong>
                    </Col>
                    <Col xs={8}>
                        <Form.Control as="select" defaultValue={this.state.payloadContent.status} onChange={(e) => 
                            this.setState({
                                payloadContent:{
                                    ...this.state.payloadContent,
                                    status: e.target.value
                                },
                            })}
                        >
                            <option value="">-Chose Status-</option>
                            <option value="DRAFT">DRAFT</option>
                            <option value="ARCHIVED">ARCHIVED</option>
                            <option value="PUBLISHED">PUBLISHED</option>
                            <option value="PENDING">PENDING</option>
                        </Form.Control>
                    </Col>
                </Row>
                <Row className="pt-3">
                    <Col xs={4}><strong>Content</strong></Col>
                    <Col xs={8}>
                        <this.CKEditor
                            editor={ this.ClassicEditor }
                            data={this.state.payloadContent.content}
                            onChange={ ( event, editor ) => {
                                const data = editor.getData();
                                this.setState({
                                    payloadContent:{
                                        ...this.state.payloadContent,
                                        content: data
                                    },
                                })
                            } }
                        />
                    </Col>
                </Row>
            </Modal.Body>
            <Modal.Footer style={{ border:'none' }}>
            <Button variant="secondary" onClick={() => this.setState({
                modal: false, 
                tabIndex: -1, 
                statusProduct: null,
                payloadContent: {
                    ...this.state.payloadContent,
                    content_name: "",
                    content: "",
                    date: "",
                    status: "",
                    category_id: "",
                }
            })}>
                Close
            </Button>
            <Button variant="primary" type="submit" onClick={this.handleSaveCategory}>Save</Button>
            </Modal.Footer>
            </Form>
        </Modal>

        <Row className="pt-15">
            <Col lg={12} md={12} sm={12}>
                <Card style={{ backgroundColor: 'white', borderRadius:'10px' }}>
                    <Row className="position-relative show-grid" style={{ margin:'20px 20px 0px 20px' }}>
                    <Col xs={12}>
                        <Row className="position-relative show-grid" >
                            <Col xs={10}>
                                <Form>
                                    <Form.Row>
                                        <Col xs="4">
                                            <Form.Label htmlFor="inlineFormInputGroup" srOnly>
                                                Find Contents By Name
                                            </Form.Label>
                                            <InputGroup className="mb-2">
                                                <InputGroup.Prepend>
                                                <InputGroup.Text style={{ backgroundColor:'white' }}><i className="fa fa-search"></i></InputGroup.Text>
                                                </InputGroup.Prepend>
                                                <FormControl id="inlineFormInputGroup" placeholder="Find Contents By Name" onKeyPress={this.handleSearch} onChange={(e) => this.setState({ inputSearch: e.target.value })} />
                                            </InputGroup>
                                        </Col>
                                    </Form.Row>
                                </Form>
                            </Col>
                            <Col xs={2} style={{ textAlign:'end' }}>
                                <Button variant="success" onClick={() => {
                                    this.setState({modal: true})
                                }}>Add Content</Button>
                            </Col>
                        </Row>
                        <Row>
                            <Col xs={12}>
                                <DataTable
                                    data={this.state.data}
                                    columns={this.state.columns}
                                    highlightOnHover
                                    noHeader={true}
                                    paginationTotalRows={this.props.total}
                                    paginationPerPage={this.state.num}
                                    pagination
                                    paginationServer
                                    onChangePage={this.handlePageChange}
                                    onChangeRowsPerPage={this.handlePerRowsChange}
                                    // paginationServer
                                />
                            </Col>
                        </Row>

                    </Col>
                    </Row>
                </Card>
            </Col>
        </Row>
        </Layout>
      );
    }

  }
  function mapStateToProps ( state ) {

    const { data, total, dataCategory } = state.content;
    return {
        dataCategory,
        data,
        total
    };
}

  function mapDispatchToProps (dispatch) {
    return {
        getContentCategory: (opts) => dispatch(getContentCategory(opts)),
        getContent: (opts) => dispatch(getContent(opts)),
        storeContent: (payload, opts) => dispatch(storeContent(payload, opts)),
        updateContent: (id, payload, opts) => dispatch(updateContent(id, payload, opts)),
        deleteContent: (id) => dispatch(deleteContent(id)),
    }
  }


export default connect(mapStateToProps, mapDispatchToProps)(index);
