import ProductSchedulesService from "service/product_schedule";

export const getProductSchedules = (payload) => async (dispatch) => {
  try {
    const response = await ProductSchedulesService.getProductSchedules(payload);
    return dispatch({
      type: "GET_PRODUCT_SCHEDULES",
      payload: response && response.data ? response.data : [],
    });
  } catch (e) {}
};

export const getProductSchedulesPerDay = payload => async () => {
  try {
    const response = await ProductSchedulesService.getProductSchedules(payload);
    return response.data && response.data.data ? response.data.data : [];
  } catch (e) {
    console.log(e)
  }
}

export const storeProductSchedule = (payload) => async (dispatch) => {
  try{
    const response = await ProductSchedulesService.storeProductSchedule(payload);
    return response && response.data ? response.data : [];
  }catch(e){}
};

export const selectDateSchedule = (payload) => (dispatch) => {
  return dispatch({
    type: "SELECT_DATE_SCHEDULE",
    payload,
  });
};

export const selectRentTime = (payload) => (dispatch) => {
  return dispatch({
    type: "SELECT_RENT_TIME",
    payload,
  });
};

export const deselectRentTime = (payload) => (dispatch) => {
  return dispatch({
    type: "DESELECT_RENT_TIME",
    payload,
  });
};

export const resetDateSchedule = () => (dispatch) => {
  return dispatch({
    type: "RESET_DATE_SCHEDULE",
  });
};

export const resetRentTime = () => (dispatch) => {
  return dispatch({
    type: "RESET_RENT_TIME",
  });
};
