function mapStateToProps(state) {
  // const { data, include, total } = state.item;
  const {
    data,
    include,
    dataComment,
    includeComment
  } = state.submission;
  return {
    data: state.submission.data,
    include: state.submission.include,
    dataLogin: state.auth.user,
    dataComment: dataComment,
    includeComment,
    dataPayment: state.payment_transaction.data,
    dataWallet: state.wallet.data,
    includePayment: state.payment_transaction.include,
    dataAttachment: state.submission.dataAttachment,
    includeAttachment: state.submission.includeAttachment,
    // dataItem: state.item.data
    auth: state.auth.user
  };
}

function mapDispatchToProps(dispatch) {
  return {
    // getItem: (payload) => dispatch(getItem(payload)),
    // updateSubmission: (id, payload) => dispatch(updateSubmission(id, payload)),
    getSubmission: opts => dispatch(getSubmission(opts)),
    getSubmissionComment: opts => dispatch(getSubmissionComment(opts)),
    destroySubmissionComment: id => dispatch(destroySubmissionComment(id)),
    storeSubmissionComment: (payload, opts) => dispatch(storeSubmissionComment(payload, opts)),
    getPaymentTransaction: opts => dispatch(getPaymentTransaction(opts)),
    storePaymentTransaction: (payload, opts) => dispatch(storePaymentTransaction(payload, opts)),
    getWallet: opts => dispatch(getWallet(opts)),
    getSubmissionAttachment: opts => dispatch(getSubmissionAttachment(opts)),
    storeSubmissionAttachment: (payload, opts) => dispatch(storeSubmissionAttachment(payload, opts)),
    updateStatusSubmission: (id, payload, opts) => dispatch(updateStatusSubmission(id, payload, opts)),
    destroySubmissionAttachment: id => dispatch(destroySubmissionAttachment(id))
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Invoice);

