import React from 'react'
import Layout from '../../../../components/Layout'
import {Card, Row, Col, Button, Table, Tabs, Tab, Form, InputGroup, Modal} from 'react-bootstrap'
import {connect} from 'react-redux'
import { getSubmission, getSubmissionComment, destroySubmissionComment, storeSubmissionComment, updateStatusSubmission, getSubmissionAttachment, storeSubmissionAttachment, destroySubmissionAttachment } from "../../../../redux/actions/submission";
import {getPaymentTransaction, storePaymentTransaction} from '../../../../redux/actions/payment_transaction'
import {getWallet} from '../../../../redux/actions/wallet'
import Router from "next/router";
import {alertSuccess} from '../../../../components/alert'
import Resizer from 'react-image-file-resizer'

class Invoice extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            modalPayment: false,
            keranjang: [],
            dataComment: [],
            submission_id: Router.router.query.id,
            comment: null,
            data: {},
            dataPayment: [],
            dataWallet: [],
            payment_date: null,
            amount_payment: null,
            wallet_payment: null,
            file_payment: null,
            dataAttachment: null,
            file_attachment: null
        }
    }

    getTotals = (data) => {
        let total = 0;
        data.forEach(item => {
          total += item.total;
        });
        return total;
    };

    getTotalsPayment = (data) => {
        let total = 0;
        data.forEach(item => {
          total += parseFloat(item.attributes.amount);
        });
        return parseFloat(total);
    };

    sendComment = () => {
        let payload = {
            submission_id: this.state.submission_id,
            comment: this.state.comment
        }
        this.props.storeSubmissionComment(payload, {params: { include: 'user,company' }}).then(() => {
            this.props.dataComment.attributes.user = this.props.includeComment.user[this.props.dataComment.attributes.user_id]
            this.props.dataComment.attributes.company = this.props.includeComment.company[this.props.dataComment.attributes.company_id]
            let add_data = [...this.state.dataComment]
            add_data.splice(0, 0, this.props.dataComment)
            this.setState({
                comment: '',
                dataComment: add_data
            })

            alertSuccess('Comment Send')
        })
    }

    getSubmissionById = () => {
        let opts = {
            params: {
                include: 'submission_category,user',
                'filter[id][is]': this.state.submission_id
            }
        }
        this.props.getSubmission(opts).then(() => {
            this.props.data[0].attributes.users_login = this.props.include.user[this.props.data[0].attributes.user_id]
            this.props.data[0].attributes.item.forEach(item => {
                item.qty = item.quantity
                item.discount = item.discount
                item.ppn = item.tax
                let total_harga = item.qty * item.selling_price
                let total_harga_discount = total_harga - ((total_harga * item.discount )/100)
                item.total = total_harga_discount + ((total_harga_discount * item.ppn)/100)
            });

            console.log(this.props.data)

            this.setState({
                keranjang: this.props.data[0].attributes.item,
                data: this.props.data,
                include: this.props.include
            })
        })

    }

    submissionApproval = () => {
        this.props.updateStatusSubmission(this.state.submission_id, payload, opts).then(() => {
            this.getSubmissionById()
        })
    }
    getSubmissionAttachment = () => {
        let opts = {
            params: {
                include: 'submission',
                'filter[submission_id][is]': this.state.submission_id
            }
        }

        this.props.getSubmissionAttachment(opts).then(() => {
            this.setState({
                dataAttachment: this.props.dataAttachment
            })
        })
    }

    componentDidMount() {

        this.getSubmissionById()

        this.getSubmissionAttachment()

        this.props.getWallet({page: 1, limit: 100}).then(() => {    
            this.setState({
                dataWallet: this.props.dataWallet,
            })
        })

        let opts = {
            params: {
                include: 'user,company',
                'filter[submission_id][is]': this.state.submission_id
            }
        }

        this.props.getPaymentTransaction({
            params: {
                'filter[submission_id][is]' : this.state.submission_id,
                include: 'company_wallet,payment_transaction_attachment'
            }
        }).then(() => {
            this.props.dataPayment.forEach((data) => {
                data.attributes.wallet = this.props.includePayment.company_wallet[data.attributes.company_wallet_id]
                data.attributes.attachment = this.props.includePayment.payment_transaction_attachment[data.relationships.payment_transaction_attachment.length && data.relationships.payment_transaction_attachment[0].id] 
            })
            this.setState({
                dataPayment: this.props.dataPayment
            })
        })

        this.props.getSubmissionComment(opts).then(() => {
            this.props.dataComment.forEach(element => {
                element.attributes.user = this.props.includeComment.user[element.attributes.user_id]
                element.attributes.company = this.props.includeComment.company[element.attributes.company_id]
            });
            this.setState({ dataComment: this.props.dataComment })

        })

    }

    resizeFile = (file) =>
        new Promise((resolve) => {
            Resizer.imageFileResizer(
                file,
                720,
                720,
                "PNG",
                100,
                0,
                (uri) => {
                    resolve(uri);
                },
                "base64"
            );
    });

    dataURIToBlob = (dataURI) => {
        const splitDataURI = dataURI.split(",");
        const byteString =
          splitDataURI[0].indexOf("base64") >= 0
            ? atob(splitDataURI[1])
            : decodeURI(splitDataURI[1]);
        const mimeString = splitDataURI[0].split(":")[1].split(";")[0];
        const ia = new Uint8Array(byteString.length);
        for (let i = 0; i < byteString.length; i++) ia[i] = byteString.charCodeAt(i);
        return new Blob([ia], { type: mimeString });
    };

    storeSubmissionAttachment = async () => {
        const payload = new FormData()
        const file = this.state.file_attachment;
        const image = await this.resizeFile(file);
        const newFile = this.dataURIToBlob(image);
        payload.append('submission_id', this.state.submission_id)
        payload.append('file', newFile, this.state.file_attachment.name)

        this.props.storeSubmissionAttachment(payload, {}).then(() => {
            let add_data = [...this.state.dataAttachment]
            add_data.splice(0, 0, this.props.dataAttachment)
            this.setState({
                file_attachment: '',
                dataAttachment: add_data
            })
            alertSuccess('Attachment Uploaded!')
        })
    }

    addPayment = async (e) => {
        e.preventDefault()
        let opts = {
            params: {
                include: 'company_wallet,payment_transaction_attachment'
            }
        }

        let payload = new FormData()
        payload.append('amount', this.state.amount_payment)
        payload.append('date', this.state.payment_date)
        payload.append('company_wallet_id', this.state.wallet_payment)
        payload.append('submission_id', this.state.submission_id)
        if(this.state.file_payment){
            const file = this.state.file_payment;
            const image = await this.resizeFile(file);
            const newFile = this.dataURIToBlob(image);
            payload.append('file', newFile , this.state.file_payment.name)
        }

        await this.props.storePaymentTransaction(payload, opts).then(() => {
            let add_data = [...this.state.dataPayment]
            this.props.dataPayment.attributes.wallet = this.props.includePayment.company_wallet[this.props.dataPayment.attributes.company_wallet_id]
            this.props.dataPayment.attributes.attachment = this.props.includePayment.payment_transaction_attachment[this.props.dataPayment.relationships.payment_transaction_attachment.length && this.props.dataPayment.relationships.payment_transaction_attachment[0].id]
            add_data.splice(0, 0, this.props.dataPayment)

            this.setState({
                dataPayment: add_data,
                modalPayment: false
            })
            alertSuccess('Payment is successful')
        })
        this.getSubmissionById()
    }


    destroySubmissionAttachment = (id, index) => {
        this.props.destroySubmissionAttachment(id).then(() => {
            let data_delete = [...this.state.dataAttachment]
            data_delete.splice(index, 1)
            this.setState({ dataAttachment: data_delete})
            alertSuccess('Submission Attachment has been deleted!')
        })
    }

    render(){

        return(
            <Layout title="Invoice" classname="dashboard theme-light">
                {this.state.data[0] && this.state.data[0].attributes && 
               <Card style={{ borderRadius:'8px', backgroundColor: 'white' }} className="p-2 pt-3">
                    <Row style={{ backgroundColor: '#78828A', color:'white', fontSize:'24px' }} className="p-3">
                        <Col xs={12}>
                            <span>Invoice #{this.state.data[0].id}</span>
                        </Col>
                    </Row>
                    <Row className="mr-4 ml-4 mt-4">
                        <Col xs={2}>
                            Date
                        </Col>
                        <Col xs={4}>
                        : {this.state.data[0].attributes.date}
                        </Col>
                        <Col xs={4} style={{ textAlign:'end' }}>
                            Employee
                        </Col>
                        <Col xs={2} >
                            : {this.state.data[0].attributes.users_login.attributes.full_name}
                        </Col>
                    </Row>
                    <Row className="mr-4 ml-4 mt-2">
                        <Col xs={2}>
                            Due Date
                        </Col>
                        <Col xs={4}>
                        : {this.state.data[0].attributes.due_date}
                        </Col>
                        <Col xs={4} style={{ textAlign:'end' }}>
                        Customer Name
                        </Col>
                        <Col xs={2}>
                            {/*  */}
                        : {this.state.data[0].attributes.customer.full_name}
                        </Col>
                    </Row>
                    <Row className="mr-4 ml-4 mt-2">
                        <Col xs={2}>
                        Transaction Name
                        </Col>
                        <Col xs={4}>
                        : {this.state.data[0].attributes.submission_name}
                        </Col>
                        <Col xs={4} style={{ textAlign:'end' }}>
                        Phone Number
                        </Col>
                        <Col xs={2}>
                        : {this.state.data[0].attributes.customer.phone}
                        </Col>
                    </Row>
                    <Row className="mr-4 ml-4 mt-2">
                        <Col xs={2}>
                        Transaction Type
                        </Col>
                        <Col xs={4}>
                        : {this.state.data[0].attributes.submission_type}
                        </Col>
                        <Col xs={4} style={{ textAlign:'end' }}>
                        Document Reference No.
                        </Col>
                        <Col xs={2}>
                        : {this.state.data[0].attributes.reference_doc_number}
                        </Col>
                    </Row>
                    <Row className="mr-4 ml-4 mt-2 mb-5">
                        <Col xs={2}>
                            Status
                        </Col>
                        <Col xs={4}>
                        : <Button variant="outline-info" style={{ borderRadius:'20px' }}>{this.state.data[0].attributes.status}</Button>
                        </Col>
                    </Row>

                    <Row>
                        <Col xs={12}>
                            <Table size="lg" className="mb-5 table-size" responsive>
                                <thead>
                                    <tr>
                                        <th className="pl-5">No</th>
                                        <th>Products or Services</th>
                                        <th>Qty</th>
                                        <th>Price (IDR)</th>
                                        <th>Discount (%)</th>
                                        <th>PPN (%)</th>
                                        <th>Total Price</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {this.state.keranjang.map((r, index) => (
                                    <tr key={index}>
                                        <td className="pl-5">1</td>
                                        <td>{r.item_name}</td>
                                        <td>{r.qty}</td>
                                        <td>{r.selling_price}</td>
                                        <td>{r.discount}</td>
                                        <td>{r.ppn}</td>
                                        <td>Rp. {r.qty * r.selling_price}</td>
                                    </tr>
                                    ))}
                                </tbody>
                                <tfoot>
                                    <tr>
                                        <td colSpan="5"></td>
                                        <th>PAY</th>
                                        <th>Rp. {this.getTotalsPayment(this.state.dataPayment)}</th>
                                    </tr>
                                    <tr>
                                        <td colSpan="4"></td>
                                        <td><Button variant="outline-success" style={{ borderRadius:'8px' }}>$ { this.state.data && this.state.data[0].attributes && this.state.data[0].attributes.status}</Button></td>
                                        <th>TOTAL</th>
                                        <th>Rp. {this.getTotals(this.state.keranjang)}</th>
                                    </tr>
                                </tfoot>
                            </Table>
                        </Col>
                    </Row>

                    <Row className="mr-5 mb-5">
                        <Modal
                            show={this.state.modalPayment}
                            onHide={() => this.setState({
                                modalPayment: false
                            })}
                            dialogClassName="modal-50w"
                            aria-labelledby="example-custom-modal-styling-title"
                            centered
                        >
                        <Modal.Header closeButton style={{ border:'none', paddingLeft:'20px' }}>
                            <Modal.Title id="example-custom-modal-styling-title">
                                <small>Add Payment</small>
                            </Modal.Title>
                        </Modal.Header>
                        <Modal.Body>
                            <Form>
                                <Form.Group as={Row} controlId="formPlaintextPassword">
                                    <Form.Label column sm="4">
                                    Payment Date
                                    </Form.Label>
                                    <Col sm="8">
                                    <Form.Control type="date" placeholder="Payment Date" onChange={(e) => this.setState({ payment_date: e.target.value })} />
                                    </Col>
                                </Form.Group>
                                <Form.Group as={Row} controlId="formPlaintextPassword">
                                    <Form.Label column sm="4">
                                    Amount (in IDR)
                                    </Form.Label>
                                    <Col sm="8">
                                    <Form.Control type="text" placeholder="Amount (in IDR)" onChange={(e) => this.setState({ amount_payment: e.target.value })} />
                                    </Col>
                                </Form.Group>
                                <Form.Group as={Row} controlId="formPlaintextPassword">
                                    <Form.Label column sm="4">
                                    Wallet
                                    </Form.Label>
                                    <Col sm="8">
                                        <Form.Control as="select" onChange={(e) => this.setState({ wallet_payment: e.target.value })}>
                                            <option value="">-Choose Wallet From-</option>
                                            {
                                            this.state.dataWallet.length && this.state.dataWallet.map((data) => (
                                                <option key={data.id} value={data.attributes.id}>{data.attributes.wallet_name}</option>
                                            ))
                                            }
                                        </Form.Control>
                                    </Col>
                                </Form.Group>
                                <Form.Group as={Row} controlId="formPlaintextPassword">
                                    <Form.Label column sm="4">
                                    Invoice
                                    </Form.Label>
                                    <Col sm="8">
                                        <InputGroup className="mb-2">
                                            <InputGroup.Prepend>
                                            <InputGroup.Text><i className="fa fa-paperclip"></i></InputGroup.Text>
                                            </InputGroup.Prepend>
                                            <Form.File
                                                onChange={(e)=> this.setState({
                                                    file_payment: e.target.files[0]
                                                })}
                                                id="custom-file-translate-scss"
                                                label="no file chosen"
                                                lang="en"
                                                data-browse="Chosee File"
                                                custom
                                            />
                                        </InputGroup>
                                    </Col>
                                </Form.Group>
                            </Form>
                        </Modal.Body>
                        <Modal.Footer style={{ border:'none' }}>
                        <Button variant="light" onClick={() => this.setState({
                                modalPayment: false
                            })}>CANCEL</Button>
                            <Button variant="success" onClick={this.addPayment}>ADD</Button>
                        </Modal.Footer>
                        </Modal>
                        {this.state.data[0].attributes.status != 'PAID' ?
                            <Col xs={12} style={{ textAlign:'end' }}>
                                <Button variant="success" style={{ width:'150px' }} onClick={() => this.setState({ modalPayment: true })}>ADD PAYMENT</Button>
                            </Col> :

                            <Col xs={12} style={{ textAlign:'end' }}>
                                <Button variant="success" style={{ width:'150px' }} onClick={this.submissionApproval}>Invoice Selesai</Button>
                            </Col>
                        }
                    </Row>
               </Card> 
                }

               <Card style={{ borderRadius:'8px', backgroundColor: 'white' }} className="p-5 mt-4">
                  <Row>
                      <Col xs={12}>
                      <Tabs style={{justifyContent: 'left'}} defaultActiveKey="notes" id="uncontrolled-tab-example" className="ml-4">
                            <Tab eventKey="notes" title="Notes" className="mt-5 p-3" >
                                <div>
                                    <div style={{ backgroundColor:'#F7F8FC', border:'1px solid #DDE2FF', boxSizing:'border-box' }} className="p-3"> 
                                        <Row>
                                            <Col xs={12}>
                                                <strong>{this.props.dataLogin.full_name}</strong>
                                            </Col>
                                        </Row>

                                        <Row>
                                            <Col xs={12}>
                                                <small>Cinta Company</small>
                                            </Col>
                                        </Row>
                                        <Row>
                                            <Col xs={12}>
                                            <Form.Group controlId="exampleForm.ControlTextarea1">
                                                <Form.Label></Form.Label>
                                                <Form.Control as="textarea" defaultValue={this.state.comment} rows={5} placeholder="Write your notes for this transaction... " onChange={(e) => this.setState({
                                                    comment: e.target.value
                                                })} />
                                            </Form.Group>
                                            </Col>
                                        </Row>
                                        <Row>
                                            <Col xs={12} style={{textAlign: 'right'}} >
                                            <Button variant="success" style={{marginLeft: '10px'}} className="mt-3" onClick={this.sendComment}><i className="fa fa-paper-plane"></i> SEND</Button>
                                            </Col>

                                        </Row>

                                    </div>
                                </div>

                                {this.state.dataComment.map((data, index) => (
                                    <div className="pt-3" key={index}>
                                    <div style={{ backgroundColor:'#F7F8FC', border:'1px solid #DDE2FF', boxSizing:'border-box' }} className="p-3"> 
                                        <Row>
                                            <Col xs={6}>
                                                <strong>{data.attributes.user.attributes.full_name}</strong>
                                            </Col>
                                            <Col xs={6} style={{textAlign: 'right'}}>
                                                <Button variant="danger" style={{marginLeft: '10px'}} className="mt-3" onClick={() => {
                                                    this.props.destroySubmissionComment(data.id).then(() => {
                                                        let data_delete = [...this.state.dataComment]
                                                        data_delete.splice(index, 1)
                                                        this.setState({ dataComment: data_delete})
                                                        alertSuccess('Comment deleted!')
                                                    })

                                                }
                                                }><i className="fa fa-trash"></i></Button>
                                            </Col>
                                        </Row>

                                        <Row>
                                            <Col xs={12}>
                                                <small>{data.attributes.company && data.attributes.company.attributes.business_name}</small>
                                            </Col>
                                        </Row>
                                        <Row className="pt-3">
                                            <Col xs={12}>
                                            {data.attributes.comment}
                                            </Col>
                                        </Row>
                                    </div>
                                </div>

                                ))}



                            </Tab>
                            <Tab eventKey="attachment" title="Attachment" className="mt-5 p-3">
                                <div style={{ backgroundColor:'#F7F8FC', border:'1px solid #DDE2FF', boxSizing:'border-box' }} className="p-3"> 
                                    <Row className="pt-5">
                                        <Col xs={12} style={{ textAlign:'center' }}>
                                        <Table size="lg" className="table-size" responsive>
                                            <thead>
                                                <tr>
                                                    <th>No</th>
                                                    <th>File Name</th>
                                                    <th>Uploaded At</th>
                                                    <th>Uploaded By</th>
                                                    <th>Action</th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                {this.state.dataAttachment && this.state.dataAttachment.map((data , index) => (

                                                    <tr key={index}>
                                                        <td>{index+1}</td>
                                                        <td><a href={`${data.attributes.file_location+data.attributes.file_name}`} target="_blank"><span style={{ color:'#0DAA66' }}>{data.attributes.file_name}</span></a></td>
                                                        <td>{data.attributes.created_at}</td>
                                                        <td>User</td>
                                                        <td>

                                                            <i onClick={() => this.destroySubmissionAttachment(data.attributes.id, index)} style={{color:'red'}} className="fa fa-trash"></i>
                                                        </td>
                                                    </tr>
                                                ))}
                                            </tbody>
                                        </Table>
                                        </Col>
                                    </Row>
                                    <Row className="pt-5 pb-2">
                                        <Col xs={12}>
                                            <InputGroup className="mb-2">
                                                <InputGroup.Prepend>
                                                <InputGroup.Text><i className="fa fa-paperclip"></i></InputGroup.Text>
                                                </InputGroup.Prepend>
                                                <Form.File 
                                                    id="custom-file-translate-scss"
                                                    label="no file chosen"
                                                    capture ="environment"
                                                    lang="en"
                                                    data-browse="Chosee File"
                                                    custom
                                                    onChange={(e)=> this.setState({
                                                        file_attachment: e.target.files[0]
                                                    })}
                                                />
                                            </InputGroup>
                                        </Col>
                                    </Row>
                                </div>
                                <Button onClick={this.storeSubmissionAttachment} variant="success" style={{ float:'right', width:'100px' }} className="mt-3">UPLOAD</Button>
                            </Tab>
                            <Tab eventKey="payment" title="Payment Record" className="mt-5 p-3">
                                <Table size="lg" className="table-size" responsive>
                                    <thead>
                                        <tr>
                                            <th>No</th>
                                            <th>Date</th>
                                            <th>Amount</th>
                                            <th>Payment Method</th>
                                            <th>Attachment</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {this.state.dataPayment && this.state.dataPayment.map((data , index) => (

                                            <tr key={index}>
                                                <td>{index+1}</td>
                                                <td>{data.attributes.date}</td>
                                                <td>Rp {data.attributes.amount}</td>
                                                <td>{data.attributes.wallet.attributes.wallet_name}</td>
                                                <td>{data.attributes.attachment ? <a href={`${data.attributes.attachment.attributes.file_location+data.attributes.attachment.attributes.file_name}`} target="_blank"><span style={{ color:'#0DAA66' }}>View</span></a> : '-' }</td>
                                            </tr>
                                        ))}
                                    </tbody>
                                </Table>
                            </Tab>
                        </Tabs>
                      </Col>
                  </Row>
               </Card>
            </Layout>
        )
    }
}

function mapStateToProps ( state ) {
    // const { data, include, total } = state.item;
    const { data, include, dataComment, includeComment } = state.submission;
    return {
        data: state.submission.data,
        include: state.submission.include,
        dataLogin: state.auth.user,
        dataComment: dataComment,
        includeComment,
        dataPayment: state.payment_transaction.data,
        dataWallet: state.wallet.data,
        includePayment: state.payment_transaction.include,
        dataAttachment: state.submission.dataAttachment,
        includeAttachment: state.submission.includeAttachment,
        // dataItem: state.item.data
        auth: state.auth.user,
    };
}

function mapDispatchToProps (dispatch) {
    return {
        // getItem: (payload) => dispatch(getItem(payload)),
        // updateSubmission: (id, payload) => dispatch(updateSubmission(id, payload)),
        getSubmission: (opts) => dispatch(getSubmission(opts)),
        getSubmissionComment: (opts) => dispatch(getSubmissionComment(opts)),
        destroySubmissionComment: (id) => dispatch(destroySubmissionComment(id)),
        storeSubmissionComment: (payload, opts) => dispatch(storeSubmissionComment(payload, opts)),
        getPaymentTransaction: (opts) => dispatch(getPaymentTransaction(opts)),
        storePaymentTransaction: (payload, opts) => dispatch(storePaymentTransaction(payload, opts)),
        getWallet: (opts) => dispatch(getWallet(opts)),
        getSubmissionAttachment: (opts) => dispatch(getSubmissionAttachment(opts)),
        storeSubmissionAttachment: (payload, opts) => dispatch(storeSubmissionAttachment(payload, opts)),
        updateStatusSubmission: (id,payload,opts) => dispatch(updateStatusSubmission(id,payload,opts)),
        destroySubmissionAttachment: (id) => dispatch(destroySubmissionAttachment(id)),
    }
}


export default connect(mapStateToProps, mapDispatchToProps)(Invoice);
