import * as React from 'react';
import {Button, Row, Col, Card, Form, InputGroup, FormControl, Modal, Tabs, Tab, Badge, Table} from 'react-bootstrap';
import DataTable, { createTheme } from 'react-data-table-component';
import Layout from '../../../../components/Layout';
import { getItemIncome } from "../../../../redux/actions/item";
import { updateSubmission, getSubmissionById, getSubmissionCategory } from "../../../../redux/actions/submission";
import {connect} from 'react-redux'
import CounterInput from 'react-counter-input'
import Router from "next/router";
import {alertSuccess, alertDanger} from '../../../../components/alert'
import {getProduct} from '../../../../redux/actions/product'
import ModalCustom from "components/ModalCustom";
import BookPerDay from "../../../../components/BookPerDay";
import {resetDateSchedule, resetRentTime} from '../../../../redux/actions/product_schedule'
import {formatCurrency} from '../../../../libs/numbers'
import dayjs from 'dayjs'

class edit extends React.Component {

    constructor(props){
        super(props)
        this.state = {
            modalProduct: false,
            columns: [
                {
                    name: 'ID',
                    selector: (row, index) =>  ((index+1) + (this.state.page - 1) * this.state.num) + '.',
                    sortable: true,
                    width: '10%'
                },
                {
                    name: 'Name',
                    selector: 'attributes.item_name',
                    sortable: true,
                },
                {
                    name: 'Selling Price (IDR)',
                    selector: 'attributes.selling_price',
                    sortable: true,
                },
                {
                    name: 'Actions',
                    selector: (row, index) => (<div style={{ color:'green' }}><i style={{ color:'#FFF', borderRadius:'50%', background:'green', padding:'2px' }} className="fa fa-plus" onClick={ () => {
                        // console.log(this.state.keranjang)
                        if(this.state.keranjang.find((data) => data.item_name == row.attributes.item_name)){
                            let index = this.state.keranjang.findIndex((data) => data.item_name == row.attributes.item_name)
                            let items = [...this.state.keranjang];
                            let item = {...items[index]};
                            item.qty = Number(item.qty) + 1

                            items[index] = item;
                            this.setState({
                                keranjang: items,
                            })
                        }else{
                            let keranjang = this.state.keranjang
                            row.id = row.attributes.id
                            row.qty = 1
                            row.discount = 0
                            row.ppn = 0
                            row.total = 0
                            row.selling_price = row.attributes.selling_price
                            row.item_name = row.attributes.item_name
                            row.submission_id = row.attributes.submission_id
                            keranjang.push(row)
                            this.setState({
                                keranjang: keranjang
                            })
                        }

                        alertSuccess('items added!')
                    }}></i> Add</div>),
                    width: '10%'
                }
            ],
            columnsService: [
                {
                    name: 'ID',
                    selector: (row, index) =>  ((index+1) + (this.state.pageService - 1) * this.state.numService) + '.',
                    sortable: true,
                    width: '10%'
                },
                {
                    name: 'Name',
                    selector: 'attributes.product_name',
                    sortable: true,
                },
                {
                    name: 'Selling Price (IDR)',
                    selector: 'attributes.selling_price',
                    sortable: true,
                },
                {
                    name: 'Actions',
                    selector: (row, index) => (<div style={{ color:'green' }}><i style={{ color:'#FFF', borderRadius:'50%', background:'green', padding:'2px' }} className="fa fa-plus" onClick={this.openBookModal(row)}></i> Add</div>),
                    width: '10%'
                }
            ],
            data: [],
            keranjang: [],
            total: 0,
            date: null,
            due_date: null,
            submission_name: null,
            customer_name: null,
            customer_phone: null,
            page: 1,
            num: 5,
            category_id: null,
            item_name: null,
            selling_price: null,
            total_items: 0,
            pageService: 1,
            numService: 5,
            totalService: 0,
            dataService: null,
            bookPerDayModalVisible: false,
            total_before: 0,
            status_submission: null,
            orderFor: {
                type: "PERSONAL",
            },
            search_item: '',
            search_item_service: ''
        }
    }

    openBookModal = (product) => (e) => {
        this.setState({
          selectedProduct: product,
          bookPerDayModalVisible: true,
        });
    };

    getTotals = (data) => {
        let total = 0;
        data.forEach(item => {
          total += item.total;
        });
        return total;
    };

    addProduct = async () => {
        this.getData(this.state.page, this.state.num)
        this.setState({ modalProduct:true })
    }

    getData = (page, size = this.state.num) => {
        this.props.getItemIncome({page: page, limit: size, item_name: this.state.search_item}).then(() => {
            this.setState({
                data: this.props.dataItem,
                page: page,
                num: size,
                total_items: this.props.total
            })
        })
    }
    handlePageChange = page => {
        this.getData(page)
    }

    handlePerRowsChange = async (newPerPage, page) => {
        this.getData(page, newPerPage);
    };

    searchItemService = async () => {
        this.getService(1, this.state.num)
    }

    searchItem = async () => {
        this.getData(1, this.state.num)
    }

    getService = async (page, size = this.state.num) => {

        let opts = {
            params: {
                include: 'product_attachment,product_visibility,product_category',
                'page[limit]': size,
                'page[num]': page,
                'filter[product_type][in]': 'TIME LIMIT, LIMITED',
                'filter[product_name][like]' : this.state.search_item_service
            }
        }

        await this.props.getProduct(opts).then(() => {
            let data = this.props.dataProduct.map((d) => {
                let category = this.props.includeProduct.product_category[d.relationships.product_category.id]
                d.attributes.category = category
                return d;
            })
            this.setState({
                dataService: data,
                includeProduct : this.props.includeProduct,
                pageService: page,
                numService: size,
                totalService: this.props.totalProduct,

            })

        })

    }

    handlePageChangeService = page => {
        this.getService(page)
    }

    handlePerRowsChangeService = async (newPerPage, page) => {
        this.getService(page, newPerPage);
    };

    async componentDidMount() {

        let payload = {
            type: 'INCOME'
        }

        await this.props.getSubmissionCategory(payload).then(() => {
            this.setState({
                dataCategory: this.props.dataCategory
            })
        })

        let opts = {
            params: {
                include: 'customer',
            }
        }

        this.props.getSubmissionById(Router.router.query.id, opts).then(() => {
            // this.props.data.attributes.customer_name = this.props.include.customer[this.props.data.attributes.partner_id]
            let total_before = 0
            this.props.data.attributes.item.forEach(item => {
                item.qty = item.quantity
                item.discount = item.discount
                item.ppn = item.tax
                let total_harga = item.qty * item.selling_price
                let total_harga_discount = total_harga - ((total_harga * item.discount )/100)
                item.total = total_harga_discount + ((total_harga_discount * item.ppn)/100)
                total_before += item.total
            });
            this.setState({
                keranjang: this.props.data.attributes.item,
                date: this.props.data.attributes.date,
                due_date : this.props.data.attributes.due_date,
                submission_name: this.props.data.attributes.submission_name,
                customer_name: this.props.data.attributes.customer.email,
                customer_phone: this.props.data.attributes.customer.phone,
                category_id: this.props.data.attributes.category_id,
                total_before: total_before,
                status_submission: this.props.data.attributes.status,
                orderFor: {
                    type: this.props.data.attributes.customer.roles.custom_name === 'enterprise' ? 'COMPANY' : 'PERSONAL',
                    company_id: this.props.data.attributes.customer.roles.custom_name === 'enterprise' ? this.props.data.attributes.customer.company_id : null
                }
            })
        })

        this.getService(this.state.pageService, this.state.numService)

    }

    checkOut = () => {
        if(this.state.dataSearch && this.state.dataSearch.user_has_company.length){
           this.setState({
                modalOrder: true
            })
        }else{
            this.saveSubmission()
        }
    }

    submitCheckout = () => {
        this.saveSubmission()
    }

    saveSubmission = (e) => {
        e.preventDefault()
        let items = []
        this.state.keranjang.forEach(item => {
            items.push({
                id: item.id,
                item_name: item.item_name,
                buying_price: item.buying_price,
                selling_price: item.selling_price,
                tax: item.ppn,
                discount: item.discount,
                quantity: item.qty,
                submission_id: item.submission_id,
                schedules: item.product_schedules ? item.product_schedules: null ,

            })
        });
        let payload = {
            id: Router.router.query.id,
            submission_name: this.state.submission_name,
            date: this.state.date,
            due_date: this.state.due_date,
            category_id: this.state.category_id,
            status: 'oke',
            reference_doc_number: 'INCOME - ' + this.state.date,
            customer_email: this.state.customer_name,
            items: items,
            order_for: this.state.orderFor,
        }
        this.props.updateSubmission(Router.router.query.id, payload).then(() => {
            Router.replace("/submissions/income");
            alertSuccess('Submission updated')
        })
    }

    generateStartFinishSchedule = (times, scheduleDate) => {
        const selectedRentTime = times.sort();

        let counter = 0;
        let finalSelectedRentTime = [];
        let temp = "";
        for (const rent of selectedRentTime) {
          let nextRentTime = selectedRentTime[counter + 1];
          if (nextRentTime !== undefined) {
            const splittedRent = rent.split(" - ");
            let splittedNextRentTime = nextRentTime.split(" - ");
            if (splittedRent[1] === splittedNextRentTime[0]) {
              let joinedRentTime = `${splittedRent[0]} - ${splittedNextRentTime[1]}`;
              if (temp === "") {
                temp = joinedRentTime;
              } else {
                let newTemp = temp.split(" - ");
                temp = newTemp[0] + " - " + splittedNextRentTime[1];
              }
            } else {
              if (temp !== "") {
                finalSelectedRentTime.push(temp);
              } else {
                finalSelectedRentTime.push(rent);
              }
              temp = "";
            }
          } else {
            if (temp) {
              finalSelectedRentTime.push(temp);
              temp = "";
            } else {
              finalSelectedRentTime.push(rent);
            }
          }
          counter++;
        }
        let newFinalSelectedRentTime = [];
        finalSelectedRentTime.forEach((rent) => {
          let splitted = rent.split(" - ");

          const start = dayjs(scheduleDate.fullDate)
            .startOf("day")
            .hour(parseInt(splitted[0].split(".")[0]))
            .format("YYYY-MM-DD HH:mm:ss");
          const finish = dayjs(scheduleDate.fullDate)
            .startOf("day")
            .hour(parseInt(splitted[1].split(".")[0]))
            .format("YYYY-MM-DD HH:mm:ss");
          newFinalSelectedRentTime.push(`${start} - ${finish}`);
        });
        return newFinalSelectedRentTime;
      };

    handleBook = async () => {
        const { carts, selectedDateSchedule, selectedRentTime } = this.props;

        const {
          id,
          product_name,
          product_type,
          unit,
          stock,
          buying_price,
          selling_price,
        } = this.state.selectedProduct.attributes;

        let scheduleTimes = this.generateStartFinishSchedule(
          selectedRentTime,
          selectedDateSchedule
        );

        if (!scheduleTimes || scheduleTimes.length < 1) {
            alertDanger('Anda Belum memilih jam sewa!')
        }else{
            let newCarts = [];
            newCarts = [...this.state.keranjang];
            for (const schedule of scheduleTimes) {
              let payload = {
                // attributes: {
                    id: null,
                    product_id: id,
                    item_name: product_name,
                    product_type,
                    unit,
                    stock,
                    submission_id:Router.router.query.id, 
                    buying_price,
                    selling_price,
                    qty: 1,
                    product_schedules: [],
                    discount: 0,
                    ppn: 0,
                    type: 'schedules'
                // }
              };
              const splittedTime = schedule.split(" - ");
              const newSchedule = {
                id: null,
                product_id: payload.product_id,
                start: splittedTime[0],
                finish: splittedTime[1],
              };
              payload.product_schedules.push(newSchedule);

              payload.qty = dayjs(newSchedule.finish).diff(
                newSchedule.start,
                "hour"
              );

                if(this.state.keranjang.find((data) => data.item_name == payload.item_name)){

                    let index = this.state.keranjang.findIndex((data) => data.item_name == payload.item_name)
                    let item = {...newCarts[index]};
                    item.qty = Number(item.qty) + parseInt(this.props.selectedRentTime.length)
                    item.type = 'schedules'
                    item.total = item.total + (parseInt(this.props.selectedRentTime.length) * item.selling_price)
                    newCarts[index] = item;
                }
                else{
                    payload.total = parseInt(this.props.selectedRentTime.length) * payload.selling_price
                    newCarts.push(payload);
                }
                this.setState({keranjang: newCarts})
            }
            await this.props.resetRentTime();
            await this.props.resetDateSchedule();

            this.setState({ bookPerDayModalVisible: false });
            alertSuccess('items added!')
        }

      };


    render () {
        const status_submission = this.state.status_submission
      return (

        <Layout title="Edit submissions" classname="dashboard theme-light">
            <ModalCustom
                customTitle={
                    <div>
                    <h4
                        style={{
                        fontSize: "1.3rem",
                        color: "#252733",
                        fontWeight: "bold",
                        }}
                    >
                        Choose your preffered time of the day
                    </h4>
