                                                            alertDanger('data cannot be empty')
                                                        }else{
                                                            let data = {
                                                                attributes: {
                                                                    id: null,
                                                                    item_name: this.state.item_name,
                                                                    selling_price: this.state.selling_price,
                                                                    quantity: 0,
                                                                    buying_price: 0,
                                                                    discount: 0,
                                                                    tax: 0,
                                                                    submission_id: 0,
                                                                    company_id: 0,
                                                                    product_id: 0,
                                                                    created_at: null
                                                                },
                                                                id: null,
                                                                type: "item",
                                                                relationships: null
                                                            }
                                                            let add_data = [...this.state.data]
                                                            add_data.splice(this.state.num - 1, 1);
                                                            add_data.splice(0, 0, data)
                                                            this.setState({
                                                                data: add_data,
                                                                total_items: this.state.total_items + 1,
                                                                item_name: null,
                                                                selling_price: null
                                                            })
