                                    <Col sm="9">
                                        <Form.Control plaintext readOnly defaultValue={this.props.auth.full_name} />
                                    </Col>
                                </Form.Group>

                                <Form.Group as={Row} controlId="formPlaintextPassword">
                                    <Form.Label column sm="3">
                                    Customer Name
                                    </Form.Label>
                                    <Col sm="9">
                                        <Form.Control type="text" placeholder="Customer Name" onChange={(e) => this.setState({ customer_name: e.target.value })} defaultValue={this.state.customer_name} />
                                    </Col>
                                </Form.Group>

                                <Form.Group as={Row} controlId="formPlaintextPassword">
                                    <Form.Label column sm="3">
                                        Phone Number
                                    </Form.Label>
                                    <Col sm="9">
                                        <Form.Control type="text" placeholder="Phone Number" onChange={(e) => this.setState({ customer_phone: e.target.value })} defaultValue={this.state.customer_phone} />
                                    </Col>
                                </Form.Group>
                            </Col>
                        </Row>
                    </Form>
                    <Table size="lg" className="mb-5 table-size" responsive>
                        <thead>
                            <tr>
                                <th className="pl-5">No</th>
                                <th>Products or Services</th>
                                <th>Qty</th>
                                <th>Price (IDR)</th>
                                <th>Discount (%)</th>
                                <th>PPN (%)</th>
                                <th>Total (IDR)</th>
                                <th className="pr-5">Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {this.state.keranjang.map((r, index) => (
                                <tr key={index}>
                                    <td className="pl-5">{index+1}</td>
                                    <td>
                                        {r.item_name}
                                        <br/>
                                        {
                                            r.product_schedule && (
                                                <>
                                                <p>Mulai {r.product_schedule.start} <br />
                                                    Selesai {r.product_schedule.finish}
                                                </p>
                                                </>
                                            )
                                        }
                                    </td>
                                    <td>
                                        {!r.product_schedule ? <CounterInput
                                            count={parseFloat(r.qty)}
                                            min={0}
                                            onCountChange={(count) => {
                                                let items = [...this.state.keranjang];
                                                let item = {...items[index]};
                                                item.qty = count
                                                let total_harga = count * item.selling_price
                                                let total_harga_discount = total_harga - ((total_harga * item.discount)/100)
                                                item.total = total_harga_discount + ((total_harga_discount * item.ppn)/100)
                                                items[index] = item;
                                                this.setState({
                                                    keranjang: items,
                                                })
                                            }}
                                        /> : Number(r.qty)}

                                    </td>
                                    <td>
                                        {!r.product_schedule ? <Form.Control type="number" defaultValue={parseFloat(r.selling_price)} 
                                        onChange={(e) => {
                                            let items = [...this.state.keranjang];
                                            let item = {...items[index]};
                                            item.selling_price = e.target.value
                                            let total_harga = item.qty * e.target.value
                                            let total_harga_discount = total_harga - ((total_harga * item.discount)/100)
                                            item.total = total_harga_discount + ((total_harga_discount * item.ppn)/100)
                                            items[index] = item;
                                            this.setState({
                                                keranjang: items,
                                            })
                                        }} 
                                        /> : Number(r.selling_price)}

                                    </td>
                                    <td>
                                        <Form.Control type="number" min="0" max="100" defaultValue={parseFloat(r.discount)} onChange={(e) => {
                                            let items = [...this.state.keranjang];
                                            let item = {...items[index]};
                                            item.discount = e.target.value
                                            let total_harga = item.qty * item.selling_price
                                            let total_harga_discount = total_harga - ((total_harga * item.discount)/100)
                                            item.total = total_harga_discount + ((total_harga_discount * item.ppn)/100)
                                            items[index] = item;
                                            this.setState({
                                                keranjang: items,
                                            })
                                        }} />
                                    </td>
                                    <td>
                                        <Form.Control type="number" min="0" max="100" defaultValue={parseFloat(r.ppn)} onChange={(e) => {
                                            let items = [...this.state.keranjang];
                                            let item = {...items[index]};
                                            item.ppn = e.target.value
                                            let total_harga = item.qty * item.selling_price
                                            let total_harga_discount = total_harga - ((total_harga * item.discount)/100)
                                            item.total = total_harga_discount + ((total_harga_discount * item.ppn)/100)
                                            items[index] = item;
                                            this.setState({
                                                keranjang: items,
                                            })
                                        }} />
                                    </td>
                                    <td>{ r.total }</td>
                                    <td className="pr-5">
                                        <i className="fa fa-trash" style={{ color:'red' }}
                                            onClick={() => {
                                                let data_delete = [...this.state.keranjang]
                                                data_delete.splice(index, 1)
                                                this.setState({
                                                    keranjang: data_delete
                                                })
                                            }}
                                        ></i>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </Table>
                    <Row style={{ textAlign:'right' }} className="mr-5">
                        <Col xs={12}>
                            <Button variant="outline-success" onClick={this.addProduct}><i className="fa fa-plus"></i> Add Products</Button>
                        </Col>
                    </Row>

                    {(status_submission === 'APPROVED' || status_submission === 'PARTIAL PAID' || status_submission === 'PAID') &&  <Row style={{ textAlign:'right' }} className="mt-5 mr-5">
                        <Col xs={10}>
                            <strong>THE PREVIOUS TOTAL</strong> 
                        </Col>

                        <Col xs={2}>
                            <strong>Rp {this.state.total_before}</strong>
                        </Col>
                    </Row>
                    }


                    <Row style={{ textAlign:'right' }} className="mt-5 mr-5">
                        <Col xs={10}>
                            <strong>TOTAL</strong> 
                        </Col>

                        <Col xs={2}>
                            <strong>Rp {this.getTotals(this.state.keranjang)}</strong>
                        </Col>
                    </Row>

                    <Row style={{ textAlign:'right' }} className="mt-5 mr-5">
                        <Col xs={12}>
                            <Button variant="secondary">Cancel</Button> {' '} <Button variant="success" onClick={this.checkOut()}> Submit</Button>
                        </Col>
                    </Row>


                </div>

            </Card>
        </Layout>
      );
    }
  }
function mapStateToProps ( state ) {
    const { selectedRentTime, selectedDateSchedule } = state.productSchedule;
    const {dataProduct, includeProduct} = state.product
    const { data, include } = state.submission;
    return {
        data: data,
        include: include,
        dataItem: state.item.data,
        dataCategory: state.submission.dataCategory,
        dataProduct,
        includeProduct,
        totalProduct: state.product.total,
        selectedRentTime,
        selectedDateSchedule,
        auth: state.auth.user,
    };
}

function mapDispatchToProps (dispatch) {
    return {
        getItemIncome: (payload) => dispatch(getItemIncome(payload)),
        updateSubmission: (id, payload) => dispatch(updateSubmission(id, payload)),
        getSubmissionById: (id, opts) => dispatch(getSubmissionById(id, opts)),
        getSubmissionCategory: (payload) => dispatch(getSubmissionCategory(payload)),
        getProduct: (opts) => dispatch(getProduct(opts)),
        resetDateSchedule: () => dispatch(resetDateSchedule()),
        resetRentTime: () => dispatch(resetRentTime()),
    }
}


export default connect(mapStateToProps, mapDispatchToProps)(edit);
