          <Modal.Header
            closeButton
            style={{ border: "none", paddingLeft: "20px" }}
          >
            <Modal.Title id="example-custom-modal-styling-title">
              <small>Filter</small>
            </Modal.Title>
          </Modal.Header>
          <Modal.Body style={{ padding: "20px 65px 20px 65px" }}>
            <Form>
              <Form.Group as={Row} controlId="formPlaintextEmail">
                <Form.Label column sm="4">
                  Filter Based
                </Form.Label>
                <Col sm="8" style={{ marginTop: "6px" }}>
                  <Form.Check
                    inline
                    label="Date"
                    type="radio"
                    value="date"
                    id={`inline-radio-1`}
                    onChange={(e) => this.setState({ metode: e.target.value })}
                    checked={this.state.metode === "date"}
                  />
                  <Form.Check
                    inline
                    label="Due Date"
                    type="radio"
                    value="due_date"
                    onChange={(e) => this.setState({ metode: e.target.value })}
                    id={`inline-radio-2`}
                    checked={this.state.metode === "due_date"}
                  />
                </Col>
              </Form.Group>

              <Form.Group as={Row} controlId="formPlaintextPassword">
                <Form.Label column sm="4">
                  From
                </Form.Label>
                <Col sm="8">
                  <Form.Control
                    type="date"
                    onChange={(e) =>
                      this.setState({ date_from: e.target.value })
                    }
                  />
                </Col>
              </Form.Group>

              <Form.Group as={Row} controlId="formPlaintextPassword">
                <Form.Label column sm="4">
                  To
                </Form.Label>
                <Col sm="8" style={{ textAlign: "end" }}>
                  <Form.Control
                    type="date"
                    placeholder="Password"
                    onChange={(e) => this.setState({ date_to: e.target.value })}
                  />
                </Col>
              </Form.Group>
            </Form>
          </Modal.Body>
          <Modal.Footer style={{ border: "none" }}>
            <Button
              variant="light"
              onClick={() =>
                this.setState({
                  modalFilter: false,
                })
              }
            >
              Cancel
            </Button>
            <Button variant="success" onClick={this.handleFilter}>
              Apply
            </Button>
          </Modal.Footer>
        </Modal>
        <Row>
          <Col lg={12} md={12} sm={12}>
            <Row className="position-relative show-grid mb-4">
              <Col xs={12}>
                <Form>
                  <Form.Row className="align-items-center">
                    <Col xs="3">
                      <Form.Label htmlFor="inlineFormInputGroup" srOnly>
                        Find Transaction
                      </Form.Label>
                      <InputGroup className="mb-2">
                        <InputGroup.Prepend>
                          <InputGroup.Text style={{ backgroundColor: "white" }}>
                            <i className="fa fa-search"></i>
                          </InputGroup.Text>
                        </InputGroup.Prepend>
                        <FormControl
                          id="inlineFormInputGroup"
                          onChange={(e) =>
                            this.setState({
                              textSearch: e.target.value,
                            })
                          }
                          onKeyPress={this.handleSearch}
                          placeholder="Find Transaction"
                        />
                      </InputGroup>
                    </Col>
                    <Col xs="8">
                      <Button
                        variant="success"
                        className="mb-2"
                        onClick={() =>
                          this.setState({
                            modalFilter: true,
                          })
                        }
                      >
                        FILTER{" "}
                        <img
                          className="mr-2 ml-2"
                          style={{ width: "15px" }}
                          src="https://storage.googleapis.com/spaces-semanggi-app/back-office/super-entity/png/filter-list.png"
                        />
                      </Button>
                    </Col>
                  </Form.Row>
                </Form>
              </Col>
            </Row>
            <Card style={{ backgroundColor: "white", borderRadius: "10px" }}>
              <Row className="position-relative show-grid mt-5 mb-5">
                <Col xs={12}>
                  <Tabs
                    defaultActiveKey="submitted"
                    id="uncontrolled-tab-example"
                    className="ml-4"
                    style={{ justifyContent: "left" }}
                  >
                    <Tab eventKey="submitted" title="Submitted">
                      <Row className="mr-5">
                        <Col xs={12} style={{ textAlign: "end" }}>
                          <Link
                            href="/submissions/income/create"
                            as={`/submissions/income/create`}
                          >
                            <Button variant="success">Create Submission</Button>
                          </Link>
                        </Col>
                      </Row>
                      <Row>
                        <Col xs={12} className="mt-4">
                          <span
                            className="ml-5 mb-5"
                            style={{
                              fontFamily: "arial",
                              fontSize: "20px",
                              color: "rgb(13, 170, 102)",
                            }}
                          >
                            <i className="fa fa-money"></i> Total Income :{" "}
                            {convertCurrency(
                              parseFloat(this.props.total_amount)
                            )}
                          </span>
                          <DataTable
                            data={this.state.data}
                            columns={this.state.columns}
                            highlightOnHover
                            wrap={true}
                            noHeader={true}
                            paginationTotalRows={this.props.totalSubmission}
                            paginationPerPage={this.state.num}
                            selectableRows
                            pagination
                            paginationServer
                            onChangePage={this.handlePageChange}
                            onChangeRowsPerPage={this.handlePerRowsChange}
                          />
                        </Col>
                      </Row>
                    </Tab>
                    <Tab eventKey="draft" title="Draft">
                      <Row className="mr-5">
                        <Col xs={12} style={{ textAlign: "end" }}>
                          <Link
                            href="/submissions/income/create"
                            as={`/submissions/income/create`}
                          >
                            <Button variant="success">Create Submission</Button>
                          </Link>
                        </Col>
                      </Row>
                      <Row>
                        <Col xs={12}>
                          <DataTable
                            data={this.state.dataDraft}
                            columns={this.state.columnsDraft}
                            highlightOnHover
                            paginationTotalRows={
                              this.props.totalSubmissionDraft
                            }
                            paginationPerPage={this.state.numDraft}
                            selectableRows
                            wrap={true}
                            pagination
                            paginationServer
                            onChangePage={this.handlePageChangeDraft}
                            onChangeRowsPerPage={this.handlePerRowsChangeDraft}
                          />
                        </Col>
                      </Row>
                    </Tab>
                    <Tab eventKey="category" title="Category" className="mr-5">
                      <Modal
                        show={this.state.modalCategory}
                        onHide={() =>
                          this.setState({
                            modalCategory: false,
                            categoryName: null,
                            max: null,
                            tabIndexCategory: -1,
                          })
                        }
                        aria-labelledby="contained-modal-title-vcenter"
                        centered
                      >
                        <Modal.Header closeButton style={{ border: "none" }}>
                          <Modal.Title id="contained-modal-title-vcenter">
                            {this.state.tabIndexCategory == -1
                              ? "Create Submission Category"
                              : "Edit Submission Category"}
                          </Modal.Title>
                        </Modal.Header>
                        <Modal.Body className="p-5">
                          <Row>
                            <Col xs={4} className="pt-1">
                              Category Name
                            </Col>
                            <Col xs={8}>
                              <Form.Control
                                type="text"
                                placeholder="Category Name"
                                onChange={(e) =>
                                  this.setState({
                                    categoryName: e.target.value,
                                  })
                                }
                                defaultValue={this.state.categoryName}
                              />
                            </Col>
                          </Row>
                          <Row className="pt-4">
                            <Col xs={4} className="pt-1">
                              Maximum Pending
                            </Col>
                            <Col xs={8}>
                              <Form.Control
                                type="text"
                                placeholder="Maximum Pending"
                                onChange={(e) =>
                                  this.setState({
                                    max: e.target.value,
                                  })
                                }
                                defaultValue={this.state.max}
                              />
                            </Col>
                          </Row>

                          <Row className="pt-1">
                            <Col xs={4} className="pt-1"></Col>
                            <Col xs={8}>
                              <small>
                                <i>
                                  Set maximum pending transactions allowed for
                                  this submission category
                                </i>
                              </small>
                            </Col>
                          </Row>
                        </Modal.Body>
                        <Modal.Footer style={{ border: "none" }}>
                          <Button
                            variant="secondary"
                            onClick={() =>
                              this.setState({
                                modalCategory: false,
                                categoryName: null,
                                max: null,
                                tabIndexCategory: -1,
                              })
                            }
                          >
                            CANCEL
                          </Button>
                          <Button variant="success" onClick={this.saveCategory}>
                            {this.state.tabIndexCategory == -1
                              ? "CREATE"
                              : "SAVE"}
                          </Button>
                        </Modal.Footer>
                      </Modal>
                      {this.state.dataDeleteCategory && (
                        <Confirm
                          title="Delete Submission Category"
                          body={
                            <div>
                              <small>
                                Are you sure you want to Delete Submission
                                Category with the folowing details:{" "}
                              </small>
                              <div
                                style={{
                                  background: "#F7F8FC",
                                  borderRadius: "15px",
                                  marginTop: "20px",
                                  padding: "10px 10px 10px 10px",
                                }}
                              >
                                <table>
                                  <tbody>
                                    <tr>
                                      <th width="100px">
                                        <small>Categoy Name</small>
                                      </th>
                                      <td>
                                        <small>:</small>
                                      </td>
                                      <td>
                                        <small>
                                          {
                                            this.state.dataDeleteCategory
                                              .attributes.category_name
                                          }
                                        </small>
                                      </td>
                                    </tr>
                                    <tr>
                                      <th width="100px">
                                        <small>Maximum Pending</small>
                                      </th>
                                      <td>
                                        <small>:</small>
                                      </td>
                                      <td>
                                        <small>
                                          {
                                            this.state.dataDeleteCategory
                                              .attributes.maximum
                                          }
                                        </small>
                                      </td>
                                    </tr>
                                  </tbody>
                                </table>
                              </div>
                            </div>
                          }
                          show={this.state.modalDeleteCategory}
                          deleteData={() => {
                            this.props
                              .destroy(this.state.dataDeleteCategory.id)
                              .then(() => {
                                let data_delete = [...this.state.dataCategory];
                                data_delete.splice(
                                  this.state.tabIndexCategory,
                                  1
                                );
                                this.setState({
                                  modalDeleteCategory: false,
                                  dataCategory: data_delete,
                                  dataDeleteCategory: null,
                                  tabIndexCategory: -1,
                                });
                                toast.success("Data Berhasil di Hapus", {
                                  position: toast.POSITION.TOP_RIGHT,
                                  autoClose: 3000,
                                });
                              });
                          }}
                          onHide={() =>
                            this.setState({ modalDeleteCategory: false })
                          }
                        />
                      )}
                      <Row>
                        <Col style={{ textAlign: "end" }} xs={12}>
                          <Button
                            variant="success"
                            onClick={() =>
                              this.setState({ modalCategory: true })
                            }
                          >
                            CREATE SUBMISSION CATEGORY
                          </Button>
                        </Col>
                      </Row>
                      <Row>
                        <Col style={{ textAlign: "right" }} xs={12}>
                          <DataTable
                            data={this.state.dataCategory}
                            columns={this.state.columnsCategory}
                            highlightOnHover
                            pagination
                            // paginationServer
                          />
                        </Col>
                      </Row>
                    </Tab>
                  </Tabs>
                </Col>
                <Col xs={4}></Col>
              </Row>
              <Row className="position-relative show-grid m-2">
                <Col xs={12}></Col>
              </Row>
            </Card>
          </Col>
        </Row>
      </Layout>
    );
  }
}

function mapStateToProps(state) {
  const {
    dataCategory,
    data,
    include,
    totalSubmission,
    dataDraft,
    includeDraft,
    totalSubmissionDraft,
    total_amount,
    total_potensial,
  } = state.submission;
  return {
    dataCategory: dataCategory,
    data: data,
    include: include,
    totalSubmission: totalSubmission,
    dataDraft: dataDraft,
    includeDraft: includeDraft,
    totalSubmissionDraft: totalSubmissionDraft,
    total_amount,
    total_potensial,
    dataLogin: state.auth.user,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    getSubmission: (opts) => dispatch(getSubmission(opts)),
    getSubmissionDraft: (payload) => dispatch(getSubmissionDraft(payload)),
    getSubmissionCategory: (payload) =>
      dispatch(getSubmissionCategory(payload)),
    storeSubmissionCategory: (payload) =>
      dispatch(storeSubmissionCategory(payload)),
    updateSubmissionCategory: (payload) =>
      dispatch(updateSubmissionCategory(payload)),
    destroy: (id) => dispatch(destroy(id)),
    destroySubmission: (id) => dispatch(destroySubmission(id)),
    updateStatusSubmission: (id, payload, opts) =>
      dispatch(updateStatusSubmission(id, payload, opts)),
    updateFulfilledSubmission: (id, payload, opts) =>
      dispatch(updateFulfilledSubmission(id, payload, opts)),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(index);
