import * as React from 'react';
import Link from 'next/link';
import {Button, Row, Col, Card, Form, InputGroup, FormControl, Modal, Tabs, Tab, Badge, OverlayTrigger, Tooltip} from 'react-bootstrap';
import DataTable, { createTheme } from 'react-data-table-component';
import Layout from '../../../components/Layout';
import Confirm from '../../../components/Confirm'
import {toast} from 'react-toastify'
import { getSubmission, getSubmissionDraft, getSubmissionCategory, storeSubmissionCategory, updateSubmissionCategory, destroy, destroySubmission, updateStatusSubmission, updateFulfilledSubmission } from "../../../redux/actions/submission";
import {connect} from 'react-redux'
import {alertSuccess} from '../../../components/alert'
import Router from 'next/router';
import ModalCustom from "components/Modal";
import Print from '../../../components/Print'
import ReactToPrint from "react-to-print";
import SubmissionService from '../../../service/submissoin'
import { convertCurrency } from "libs/numbers";

class index extends React.Component {


  constructor(props){
    super(props)
    this.state = {
            columnsCategory: [
                {
                    name: 'No',
                    selector: (row, index) => index+1,
                    sortable: true,
                    width: '5%'
                },
                {
                    name: 'Category',
                    selector: 'attributes.category_name',
                    sortable: true,
                    wrap: true,
                    width: '50%'
                },
                {
                    name: 'Maximum Pending',
                    selector: 'attributes.maximum',
                    sortable: true,
                    width: '30%'
                },
                {
                    name: 'Action',
                    selector: (row, index) => (
                    <div>
                        <i style={{ color:'green' }} className="fa fa-edit" onClick={ () => this.editDataCategory(row, index)}></i>{' '} &nbsp;
                        <i className="fa fa-trash" style={{ color:'red' }} onClick={() => this.setState({modalDeleteCategory: true, dataDeleteCategory: row, tabIndexCategory: index})}></i>

                    </div>),
                    sortable: true,
                    width: '10%'
                },
            ],
            columns: [
                {
                    name: 'No',
                    selector: (row, index) => ((index+1) + (this.state.page - 1) * this.state.num) + '.',
                    sortable: true,
                    width: '7%',
                    wrap:true,
                },
                {
                    name: 'Date',
                    wrap:true,
                    selector: (row,index) => (<u style={{ color:'#0DAA66', cursor: 'pointer' }}
                        onClick={() => {
                            Router.replace({pathname: "/submissions/income/invoice/[id]", query: {id: row.attributes.id}});
                        }}
                    >{row.attributes.date}</u>),
                    sortable: true,
                },
                // {
                //     name: 'Invoice Number',
                //     selector: 'attributes.reference_doc_number',
                //     sortable: true,
                //     wrap:true,
                // },
                {
                    name: 'Submission',
                    selector: 'attributes.submission_name',
                    wrap:true,
                    sortable: true,
                    width: '20%'
                },
                {
                    name: 'Amounnt (IDR)',
                    selector: (row,index) => convertCurrency(parseFloat(row.attributes.amount)),
                    sortable: true,
                    wrap:true,
                },
                {
                    name: 'Paid (IDR)',
                    selector: (row,index) => convertCurrency(parseFloat(row.attributes.paid)),
                    sortable: true,
                    wrap:true,
                },
                {
                    name: 'Status',
                    selector: (row, index) => this.statusSubmission(row.attributes.status),
                    sortable: true,
                    wrap:true,
                },
                {
                    name: 'Action',
                    selector: (row, index) => (<div>
                        {(row.attributes.status == 'PENDING' || row.attributes.status == 'PARTIAL APPROVED') ? <img className="mr-2" style={{width: '13px'}} src="https://storage.googleapis.com/spaces-semanggi-app/back-office/super-entity/png/approved.png" onClick={() => this.submissionApproval({data: row, dataIndex: index, type: 'approved'})} /> : ''}
                        {(row.attributes.status == 'PENDING' || row.attributes.status == 'PARTIAL APPROVED') ? <img className="mr-2" style={{width: '13px'}} src="https://storage.googleapis.com/spaces-semanggi-app/back-office/super-entity/png/reject.png" onClick={() => this.submissionApproval({data: row, dataIndex: index, type: 'rejected'})} /> : ''}

                        <Link href="/submissions/income/edit/[id]" as={`/submissions/income/edit/${row.id}`}>
                            <img className="mr-2" style={{ width: '15px' }} src="https://storage.googleapis.com/spaces-semanggi-app/back-office/super-entity/png/border-color-24px.png"/>
                        </Link>


                        {/* <i style={{ color:'green' }} className="fa fa-check" onClick={ () => this.editData(row, index)}></i>{' '}<i className="fa fa-times" style={{ color:'red' }} onClick={() => this.setState({modalDelete: true, dataDelete: row, tabIndex: index})}></i>{' '}<i className="fa fa-print" style={{ color:'blue' }} onClick={() => this.setState({modalDelete: true, dataDelete: row, tabIndex: index})}></i> */}

                        <img style={{width: '15px'}} src="https://storage.googleapis.com/spaces-semanggi-app/back-office/super-entity/png/delete.png" onClick={() => this.setState({modalDelete: true, dataDelete: row, tabIndex: index})} />

                        <ReactToPrint
                            copyStyles
                            // onAfterPrint={() => this.setState({idPrint: null})}
                            onBeforeGetContent={() => new Promise((resolve, reject) => {
                                let opts = {
                                    params: {
                                        include: 'submission_category,user,company,payment_transaction',
                                    }
                                }
                                SubmissionService.getSubmissionById(row.attributes.id, opts).then((data) => {
                                    data.data.data.attributes.company = data.data.included.company[data.data.data.relationships.company.id]
                                    data.data.data.attributes.user = data.data.included.user[data.data.data.relationships.user.id]
                                    data.data.data.attributes.item.forEach(item => {
                                        let total_harga = item.quantity * data.data.data.attributes.submission_type === 'EXPENSE' ? item.buying_price : item.selling_price
                                        let total_harga_discount = total_harga - ((total_harga * item.discount)/100)
                                        item.total = total_harga_discount + ((total_harga_discount * item.tax)/100)
                                    });
                                    this.setState({ idPrint: data.data.data }, () => resolve());
                                })
                              })} 
                            trigger={() => 
                            <span>
                                <i className="fa fa-print"></i>
                            </span>}
                            content={() => this.componentRef}
                        />


                        </div>),
                    sortable: true,
                    wrap:true,
                },
                {
                    name: '',
                    selector: (row, index) => (
                    row.attributes.fullfilment === null ? <div style={{ cursor:'pointer' }} onClick={() => this.setState({ modalFulfilment: true, dataDelete: row, tabIndex: index })}>
                        {
                            <OverlayTrigger placement="right" overlay={<Tooltip id="tooltip-disabled">On Going</Tooltip>}>
                                <span className="d-inline-block">
                                    <Badge variant="warning">&emsp;</Badge>
                                </span>
                            </OverlayTrigger> 
                        }    
                    </div> : <div>
                                <OverlayTrigger placement="right" overlay={<Tooltip id="tooltip-disabled">{row.attributes.fullfilment ? 'Fulfilled' : 'Unfulfilled'}</Tooltip>}>
                                    <span className="d-inline-block">
                                        <Badge variant={row.attributes.fullfilment ? 'success' : 'danger'}>&emsp;</Badge>
                                    </span>
                                </OverlayTrigger>
                            </div>),
                },
                {
                    name: '',
                    selector: (row, index) => (
                    row.attributes.read.length ? <div style={{ cursor:'pointer' }} onClick={() => this.setState({ modalFulfilment: true, dataDelete: row, tabIndex: index })}>
                        {
                            <OverlayTrigger placement="right" overlay={<Tooltip id="tooltip-disabled">
                                { row.attributes.read.find((data) => data.user_id === props.dataLogin.id) && <div>Anda</div>}
                                {
                                    row.attributes.read.map((data) => (
                                        data.user_id !== props.dataLogin.id ? <div key={data.id}>{data.user_read.full_name}</div> : ''
                                    ))
                                }
                            </Tooltip>}>
                                {row.attributes.read.find((data) => data.user_id === props.dataLogin.id) ? 
                                    <span className="d-inline-block">
                                        <i class="fa fa-eye-slash fa-lg" style={{color: '#4caf50'}}></i>
                                    </span> :

                                    <span className="d-inline-block">
                                        <i class="fa fa-eye fa-lg"></i>
                                    </span> 
                                }

                            </OverlayTrigger> 
                        }    
                    </div> : <div>
                                <OverlayTrigger placement="right" overlay={<Tooltip id="tooltip-disabled">No Read</Tooltip>}>
                                    <span className="d-inline-block">
                                        <i class="fa fa-eye-slash fa-lg"></i>
                                    </span>
                                </OverlayTrigger>
                            </div>),
                },
            ],
            columnsDraft: [
                {
                    name: 'No',
                    selector: (row, index) => ((index+1) + (this.state.pageDraft - 1) * this.state.numDraft) + '.',
                    sortable: true,
                    width: '5%',
                    wrap:true,
                },
                {
                    name: 'Date',
                    selector: (row,index) => (<u style={{ color:'#0DAA66' }}>{row.attributes.date}</u>),
                    sortable: true,
                    wrap:true,
                },
                {
                    name: 'Invoice Number',
                    selector: 'attributes.reference_doc_number',
                    sortable: true,
                    wrap:true,
                },
                {
                    name: 'Submission',
                    selector: 'attributes.submission_name',
                    wrap:true,
                    sortable: true,
                    width: '20%'
                },
                {
                    name: 'Amounnt (IDR)',
                    selector: (row,index) => convertCurrency(parseFloat(row.attributes.amount)),
                    sortable: true,
                    wrap:true,
                },
                {
                    name: 'Paid (IDR)',
                    selector: (row,index) => convertCurrency(parseFloat(row.attributes.paid)),
                    sortable: true,
                    wrap:true,
                },
                {
                    name: 'Status',
                    selector: (row, index) => (<div data-tag="allowRowEvents" style={{textAlign: 'center', background: '#BDBDBD',
                    borderRadius: '20px', padding:'10px', fontSize: '11px'}}>{
                        row.attributes.status} </div>),
                    sortable: true,
                },
                {
                    name: 'Action',
                    selector: (row, index) => (<div>
                        <i style={{ color:'green' }} className="fa fa-check" onClick={ () => this.editData(row, index)}></i>{' '}<i className="fa fa-times" style={{ color:'red' }} onClick={() => this.setState({modalDelete: true, dataDelete: row, tabIndex: index})}></i>{' '}<i className="fa fa-print" style={{ color:'blue' }} onClick={() => this.setState({modalDelete: true, dataDelete: row, tabIndex: index})}></i></div>),
                    sortable: true,
                    wrap:true,
                },
                {
                    name: '',
                    selector: (row, index) => (<div><Badge variant="warning">On Going</Badge></div>),
                    sortable: true,
                },
            ],
            loading: true,
            dataCategory: [],
            dataDraft: [],
            data: [],
            modalFilter: false,
            startDate: new Date(),
            endDate: null,
            modalCategory: false,
            tabIndexCategory: -1,
            categoryName: null,
            max: null,
            dataDeleteCategory: null,
            modalDeleteCategory: false,
            modalAttachmentCategory: false,
            page: 1,
            num: 10,
            pageDraft: 1,
            numDraft: 10,
            modalDelete: false,
            dataDelete: null,
            metode: 'date',
            btnFilter: false,
            date_from: null,
            date_to: null,
            modalFulfilment: false,
            fullfilment: null,
            statusFullfilment: 'cancel',
            tabIndex: -1,
            textSearch: null,
            idPrint: null
        }

        this.editDataCategory = this.editDataCategory.bind(this)
        this.saveCategory = this.saveCategory.bind(this)
    }

    statusSubmission(data){
        if(data === 'PAID'){
            return (
                <Badge variant="success">{ data }</Badge>
            )
        }else if(data === 'PARTIAL PAID'){
            return (
                <Badge style={{ backgroundColor: '#BDBDBD', color:'white' }}>{ data }</Badge>
            )
        }else if(data === 'COMPLETED'){
            return (
                <Badge variant="success">{ data }</Badge>
            )
        }else if(data === 'APPROVED'){
            return (
                <Badge style={{ backgroundColor: 'blue', color:'white' }}>{ data }</Badge>
            )
        }else if(data === 'REFUND'){
            return (
                <Badge variant="danger">{ data }</Badge>
            )
        }else if(data === 'CANCELLED'){
            return (
                <Badge variant="dark">{ data }</Badge>
            )
        }else if(data === 'PENDING'){
            return (
                <Badge variant="warning">{ data }</Badge>
            )
        }else if(data === 'REJECTED'){
            return (
                <Badge variant="danger">{ data }</Badge>
            )
        }
    }

    editDataCategory(data, index){
        this.setState({
            modalCategory: true,
            tabIndexCategory: index,
            categoryName: data.attributes.category_name,
            max: data.attributes.maximum,
            dataDeleteCategory: data
        })
    }

    saveCategory(){
        if(this.state.tabIndexCategory == -1){
            let payload = {
                category_name: this.state.categoryName,
                maximum: this.state.max,
                submission_type: 'INCOME'
            }
            this.props.storeSubmissionCategory(payload).then(() => {
                let add_data = [...this.state.dataCategory]
                add_data.splice(0, 0, this.props.dataCategory)
                this.setState({
                    dataCategory: add_data,
                    modalCategory: false
                })
                alertSuccess('Data berhasil di simpan')
            })

        }else{
            let payload = {
                category_name: this.state.categoryName,
                maximum: this.state.max,
                submission_type: 'INCOME',
                id: this.state.dataDeleteCategory.id,
                _method: 'PATCH'
            }

            this.props.updateSubmissionCategory(payload).then(() => {
                let edit_data = [...this.state.dataCategory]
                edit_data.splice(this.state.tabIndexCategory, 1);
                edit_data.splice(this.state.tabIndexCategory, 0, this.props.dataCategory)
                this.setState({
                    dataCategory: edit_data,
                    modalCategory: false,
                    dataDeleteCategory: null
                })
                alertSuccess('Data berhasil di update')
            })
        }
    }

    async submissionApproval(data){
        if(data.type == 'approved'){
            const opts = {
                params: {
                    include: 'submission_category'
                }
            }
            const payload = {}

            this.props.updateStatusSubmission(data.data.attributes.id, payload, opts).then(() => {
                let edit_data = [...this.state.data]
                edit_data.splice(data.dataIndex, 1);
                edit_data.splice(data.dataIndex, 0, this.props.data)
                this.setState({
                    data: edit_data
                })
                alertSuccess('Data berhasil di perbarui')
            })
        }else{
            const opts = {
                params: {
                    include: 'submission_category'
                }
            }
            const payload = {
                status: 'reject'
            }

            this.props.updateStatusSubmission(data.data.attributes.id, payload, opts).then(() => {
                let edit_data = [...this.state.data]
                edit_data.splice(data.dataIndex, 1);
                edit_data.splice(data.dataIndex, 0, this.props.data)
                this.setState({
                    data: edit_data
                })
                alertSuccess('Data berhasil di perbarui')
            })
        }
    }

    getData = async (page, size = this.state.num) => {
        let opts = {
            params: {
                include: 'submission_category',
                'filter[submission_category.submission_type][is]': 'INCOME',
                'filter[status][not]': 'DRAFT',
                'page[limit]': size,
                'page[num]': page,
            }
        }

        //if textSearch not null
        if(this.state.textSearch){
            let search = 'filter[submission][like]'
            opts.params[search] = this.state.textSearch
        }

        //if date_from and date_to not null
        if(this.state.date_from && this.state.date_to){
            let filter = `filter[${this.state.metode}][between]`
            opts.params[filter] = `${this.state.date_from},${this.state.date_to}`
        }



        await this.props.getSubmission(opts).then(() => {
            this.setState({
                data:this.props.data,
                page: page,
                num: size
            })
        })
    }

    handleFullfilment = async () => {
        let payload = {
            status: this.state.fullfilment == 'false' ? this.state.statusFullfilment : null
        }

        this.props.updateFulfilledSubmission(this.state.dataDelete.attributes.id, payload, {}).then(() => {
            let edit_data = [...this.state.data]
            edit_data.splice(this.state.tabIndex, 1)
            edit_data.splice(this.state.tabIndex, 0, this.props.data)
            this.setState({
                data: edit_data,
                modalFulfilment: false,
                dataDelete: null,
                tabIndex: -1
            })
            alertSuccess('Data berhasil di update')
        })
    };

    getDataDraft = async (page, size = this.state.num) => {
        let opts = {
            params: {
                include: 'submission_category',
                'filter[submission_category.submission_type][is]': 'INCOME',
                'filter[status][is]': 'DRAFT',
                'page[limit]': size,
                'page[num]': page,
            }
        }

        await this.props.getSubmissionDraft(opts).then(() => {
            this.setState({
                dataDraft:this.props.dataDraft,
                pageDraft: page,
                numDraft: size
            })
        })
    }

    async componentDidMount() {

        this.getData(this.state.page, this.state.num)
        this.getDataDraft(this.state.pageDraft, this.state.numDraft)


        let payload = {
            type: 'INCOME'
        }

        await this.props.getSubmissionCategory(payload).then(() => {
            this.setState({
                dataCategory: this.props.dataCategory
            })
        })
    }

    handlePageChange = page => {
        this.getData(page)
    }

    handlePerRowsChange = async (newPerPage, page) => {
        this.getData(page, newPerPage);
    };

    handlePageChangeDraft = page => {
        this.getDataDraft(page)
    }

    handlePerRowsChangeDraft = async (newPerPage, page) => {
        this.getDataDraft(page, newPerPage);
    };

    handleSearch = (e) => {
        if (e.key === 'Enter') {
            e.preventDefault()
            this.getData(1, this.state.num)
        }
    }

    handleFilter = (e) => {
        e.preventDefault()
        this.getData(1, this.state.num)
        this.setState({
            btnFilter: true,
            modalFilter: false
        })
    }

