      return (

        <Layout title="Income" classname="dashboard theme-light">
            {/* Print */}
            <div style={{display:'none'}}>
                <Print ref={(el) => (this.componentRef = el)} data={this.state.idPrint} type="Invoice" />
            </div>
            <ModalCustom
                customTitle={
                    <div>
                    <h4
                        style={{
                        fontSize: "1.3rem",
                        color: "#252733",
                        fontWeight: "bold",
                        }}
                    >
                        Fulfillment
                    </h4>
                    {/* <h5 style={{ fontSize: "0.875rem", color: "#252733" }}>
                        Select one or multiple schedule to you want to book. Rent price
                        is{" "}
                        <span style={{ fontWeight: "bold" }}> */}
                        {/* Rp{" "}
                        {formatCurrency(
                            this.state.selectedProduct &&
                            this.state.selectedProduct.attributes
                            ? this.state.selectedProduct.attributes.selling_price
