import * as React from 'react';
import {Button, Row, Col, Card, Form, InputGroup, FormControl, Modal, Tabs, Tab, Alert, Table} from 'react-bootstrap';
import DataTable, { createTheme } from 'react-data-table-component';
import Layout from '../../components/Layout';
import {connect} from 'react-redux'
import CounterInput from 'react-counter-input'
import Router from "next/router";
import { getUser, storeUser, updateUser , deleteUser } from "../../redux/actions/user";
import {alertSuccess, alertDanger} from '../../components/alert'
import ModalCustom from "components/ModalCustom";
// import BookPerDay from "../../components/BookPerDay";
import {formatCurrency} from '../../libs/numbers'
import dayjs from 'dayjs'
import { isThisHour } from 'date-fns';


class index extends React.Component {

    constructor(props){
        super(props)
        this.state = {
            modalProduct: false,
            columns: [
                {
                    name: 'ID',
                    selector: (row, index) =>  ((index+1) + (this.state.page - 1) * this.state.num) + '.',
                    sortable: true,
                    width: '10%'
                },
                {
                    name: 'Name',
                    selector: 'attributes.item_name',
                    sortable: true,
                },
                {
                    name: 'Selling Price (IDR)',
                    selector: 'attributes.selling_price',
                    sortable: true,
                },
                {
                    name: 'Actions',
                    selector: (row, index) => (<div style={{ color:'green' }}><i style={{ color:'#FFF', borderRadius:'50%', background:'green', padding:'2px' }} className="fa fa-plus" onClick={ () => {

                        if(this.state.keranjang.find((data) => data.name == row.name)){
                            row.percent = 0
                            row.wage_price = 0
                            keranjang.push(row)
                            this.setState({
                                keranjang: keranjang
                            })
                            let data_delete = [...this.state.keranjang]
                            data_delete.splice(index, 1)
                            this.setState({
                                keranjang: data_delete
                            })
                        }

                        alertSuccess('items added!')

                    }}></i> Add</div>),
                    width: '10%'
                }
            ],
            data: [],
            keranjang: [],
            total: 0,
            bonus:0,
            date: null,
            due_date: null,
            submission_name: null,
            customer_name: null,
            customer_phone: null,
            page: 1,
            num: 5,
            dataCategory: [],
            category_id: null,
            item_name: null,
            selling_price: null,
            total_items: 0,
            dataSearch: null,
            modalShow: false,
            modalOrder: false,
            orderFor: {
                type: "PERSONAL",
            },
            company_id: null,
            name: null, 
            username: null,
            email: null,
            phone: null,
            password: null,
            pageService: 1,
            numService: 5,
            totalService: 0,
            dataService: null,
            includeProduct: null,
            search_item: '',
            search_item_service: ''
        }
    }

    getTotals = (data) => {
        let total = 0;
        data.forEach(item => {
          total += item.attributes.total;
        });
        return parseFloat(total);
    };

    addProduct = async () => {
        this.setState({ modalProduct:true })
    }

    searchItem = async () => {
        this.getData(1, this.state.num)
    }

    getData = (page, size = this.state.num) => {

        this.state.data = []
        let payload = {
            page: page,
            limit: size,
            "filter": "[roles.name][is][Buruh]"
        }

        this.props.getUser(payload).then(() => {
            this.setState({
                data: this.props.dataUser,
                total_items: this.props.total
            })
        })
    }

    handlePageChange = page => {
        this.getData(page)
    }

    handlePerRowsChange = async (newPerPage, page) => {
        this.getData(page, newPerPage);
    };

    async componentDidMount() {
        this.getData(this.state.page, this.state.num)

        let payload = {
            type: 'INCOME'
        }
        await this.props.getSubmissionCategory(payload).then(() => {
            this.setState({
                dataCategory: this.props.dataCategory
            })
        })
    }

    onKeyPressEmail = async (e) => {
        if (e.key === 'Enter') {
            e.preventDefault()
            this.state.orderFor.company_id = null
            await this.props.searchUser({ q: this.state.customer_name }).then(() => {
                this.setState({
                    dataSearch: this.props.dataUser,
                    modalShow: true,
                    password: Math.random().toString(36).slice(-8)
                })

            })
        }
    }

    saveSubmission = (e) => {
        // e.preventDefault()
        let items = []
        this.state.keranjang.forEach(item => {
            items.push({
                item_name: item.attributes.item_name,
                buying_price: item.attributes.buying_price,
                selling_price: item.attributes.selling_price,
                tax: item.attributes.ppn,
                discount: item.attributes.discount,
                quantity: item.attributes.qty,
                schedules: item.attributes.product_schedules ? item.attributes.product_schedules: null 
            })
        });

        this.state.orderFor.company_id = this.state.orderFor.type == 'COMPANY' ? this.state.company_id : 0
        let payload = {
            submission_name: this.state.submission_name,
            date: this.state.date,
            due_date: this.state.due_date,
            category_id: this.state.category_id,
            status: 'oke',
            reference_doc_number: 'INCOME - ' + this.state.date,
            customer_email: this.state.customer_name,
            order_for: this.state.orderFor,
            items: items
        }

        this.props.storeSubmission(payload).then(() => {
            Router.replace("/submissions/income");
            alertSuccess('Submission created')
        })
    }


    checkOut = () => {
        if(this.state.dataSearch && this.state.dataSearch.user_has_company.length){
           this.setState({
                modalOrder: true
            })
        }else{
            this.saveSubmission()
        }
    }

    submitCheckout = () => {
        this.saveSubmission()
    }

    saveUser = () => {
        let payload = {
            full_name: this.state.name,
            username: this.state.username,
            email: this.state.email,
            phone: this.state.phone,
            role_id: 15,
            type: 'PERSONAL',
            password: this.state.password
        }
        this.props.createUser(payload).then(() => {
            this.setState({
                customer_name: this.state.email,
                modalShow: false
            })
        })
    }

    generateStartFinishSchedule = (times, scheduleDate) => {
        const selectedRentTime = times.sort();

        let counter = 0;
        let finalSelectedRentTime = [];
        let temp = "";
        for (const rent of selectedRentTime) {
          let nextRentTime = selectedRentTime[counter + 1];
          if (nextRentTime !== undefined) {
            const splittedRent = rent.split(" - ");
            let splittedNextRentTime = nextRentTime.split(" - ");
            if (splittedRent[1] === splittedNextRentTime[0]) {
              let joinedRentTime = `${splittedRent[0]} - ${splittedNextRentTime[1]}`;
              if (temp === "") {
                temp = joinedRentTime;
              } else {
                let newTemp = temp.split(" - ");
                temp = newTemp[0] + " - " + splittedNextRentTime[1];
              }
            } else {
              if (temp !== "") {
                finalSelectedRentTime.push(temp);
              } else {
                finalSelectedRentTime.push(rent);
              }
              temp = "";
            }
          } else {
            if (temp) {
              finalSelectedRentTime.push(temp);
              temp = "";
            } else {
              finalSelectedRentTime.push(rent);
            }
          }
          counter++;
        }
        let newFinalSelectedRentTime = [];
        finalSelectedRentTime.forEach((rent) => {
          let splitted = rent.split(" - ");

          const start = dayjs(scheduleDate.fullDate)
            .startOf("day")
            .hour(parseInt(splitted[0].split(".")[0]))
            .format("YYYY-MM-DD HH:mm:ss");
          const finish = dayjs(scheduleDate.fullDate)
            .startOf("day")
            .hour(parseInt(splitted[1].split(".")[0]))
            .format("YYYY-MM-DD HH:mm:ss");
          newFinalSelectedRentTime.push(`${start} - ${finish}`);
        });
        return newFinalSelectedRentTime;
      };

    render () {

      return (

        <Layout title="Create new submissions" classname="dashboard theme-light">
            <Modal
                show={this.state.modalOrder}
                onHide={() => this.setState({
                    modalOrder: false
                })}
                dialogClassName="modal-90w"
                aria-labelledby="example-custom-modal-styling-title"
                centered
                backdrop="static"
                class="modal-backdrop foo-modal-backdrop in"
                keyboard={false}
            >
                <Modal.Header closeButton>
                <Modal.Title>Untuk siapa order ini di buat?</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                <Form.Group controlId="exampleForm.ControlSelect1">
                    <Col sm="8" style={{ marginTop:'6px', marginBottom: '10px' }}>
                        <Form.Check 
                            inline 
                            label="Personal"
                            type="radio"
                            value="PERSONAL"
                            id={`inline-radio-1`}
                            onChange={(e) => this.setState({ orderFor: {
                                type:  e.target.value
                            } })}
                            checked={this.state.orderFor.type === 'PERSONAL'}  
                        />
                        <Form.Check
                            inline 
                            label="Company" 
                            type="radio"
                            value="COMPANY"
                            onChange={(e) => this.setState({ orderFor: {
                              type:  e.target.value
                            } })}
                            id={`inline-radio-2`} 
                            checked={this.state.orderFor.type === 'COMPANY'}
                        />
                    </Col>
                    {this.state.orderFor.type === "COMPANY" && (
                    <Col sm="12" style={{ marginTop:'6px' }}>
                    <Form.Label>Pilih Company</Form.Label>
                    <Form.Control as="select" onChange={(e) => this.setState({
                        company_id: e.target.value
                    })}>
                        <option value="">-Pilih Company-</option>
                        {
                            this.state.dataSearch && this.state.dataSearch.user_has_company.map((data, index) => (
                                <option key={data.id} value={data.id}>{data.business_name}</option>
                            )
                            )
                        }
                    </Form.Control>
                    </Col>
                    )}
                </Form.Group>
                </Modal.Body>
                <Modal.Footer>
                <Button variant="secondary" onClick={() => this.setState({
                    modalOrder: false
                })}>
                    Close
                </Button>
                <Button variant="primary" onClick={this.submitCheckout}>Save</Button>
                </Modal.Footer>
            </Modal>

            <Modal
                    show={this.state.modalShow}
                    onHide={() => this.setState({
                        modalShow: false,
                    })}
                    dialogClassName="modal-90w"
                    aria-labelledby="example-custom-modal-styling-title"
                    centered
                    backdrop="static"
                    class="modal-backdrop foo-modal-backdrop in"
                    keyboard={false}
                >
                <Modal.Header closeButton style={{ border:'none', paddingLeft:'20px' }}>
                    <Modal.Title id="example-custom-modal-styling-title">
                    <small>Data User</small>
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body style={{ padding: '20px 30px 20px 30px' }}>
                    {!this.state.dataSearch ? 
                        <div>
                            <Alert variant="warning" onClose={() => this.setState({show: false})}>
                                <Alert.Heading>
                                    <small><strong>User Not Found</strong></small>
                                </Alert.Heading>
                                <p>Maaf, email atau nomor telepon user tidak ditemukan.</p>
                            </Alert>
                            <Form.Group as={Row} controlId="formPlaintextPassword">
                                <Form.Label column sm="4">
                                <span style={{ fontSize: '13px' }}>Name</span>
                                </Form.Label>
                                <Col sm="8">
                                <Form.Control type="text" placeholder="Name" onChange={(e) => this.setState({name: e.target.value})} />
                                </Col>
                            </Form.Group>    

                            <Form.Group as={Row} controlId="formPlaintextPassword">
                                <Form.Label column sm="4">
                                <span style={{ fontSize: '13px' }}>Username</span>
                                </Form.Label>
                                <Col sm="8">
                                <Form.Control type="text" placeholder="Username" onChange={(e) => this.setState({username: e.target.value})} />
                                </Col>
                            </Form.Group>

                            <Form.Group as={Row} controlId="formPlaintextPassword">
                                <Form.Label column sm="4">
                                <span style={{ fontSize: '13px' }}>Email</span>
                                </Form.Label>
                                <Col sm="8">
                                <Form.Control type="text" placeholder="Email" onChange={(e) => this.setState({email: e.target.value})} />
                                </Col>
                            </Form.Group>

                            <Form.Group as={Row} controlId="formPlaintextPassword">
                                <Form.Label column sm="4">
                                <span style={{ fontSize: '13px' }}>Phone</span>
                                </Form.Label>
                                <Col sm="8">
                                <Form.Control type="text" placeholder="Phone" onChange={(e) => this.setState({phone: e.target.value})} />
                                </Col>
                            </Form.Group>
                            <Form.Group as={Row} controlId="formPlaintextPassword">
                                <Form.Label column sm="4">
                                <span style={{ fontSize: '13px' }}>Password</span>
                                </Form.Label>
                                <Col sm="8" className="mt-2">
                                    {this.state.password}
                                </Col>
                            </Form.Group>
                            <Form.Group as={Row} controlId="formPlaintextPassword">
                                <Form.Label column sm="4">
                                {/* <span style={{ fontSize: '13px' }}>Password</span> */}
                                </Form.Label>
                                <Col sm="8">
                                    Give this password to the user you just created. They can change their password in the section profile after logging in to the system.
                                </Col>
                            </Form.Group>
                        </div>
                        : 
                        <div>
                            <Alert variant="success" onClose={() => this.setState({show: false})}>
                                <Alert.Heading>
                                    <small><strong>User Found with the folowing details:</strong></small>
                                </Alert.Heading>
                                <hr />
                                <tr>
                                    <th width="100px"><small>Full Name</small></th>
                                    <td><small>: {this.state.dataSearch.full_name}</small></td>
                                </tr>
                                <tr>
                                    <th><small>Email</small></th>
                                    <td><small>: {this.state.dataSearch.email}</small></td>
                                </tr>
                                <tr>
                                    <th><small>Username</small></th>
                                    <td><small>: {this.state.dataSearch.username}</small></td>
                                </tr>
                                <tr>
                                    <th><small>Phone</small></th>
                                    <td><small>: {this.state.dataSearch.phone}</small></td>
                                </tr>
                                <tr>
                                    <th><small>Company</small></th>
                                    <td><small>: {this.state.dataSearch.user_has_company.length ? this.state.dataSearch.user_has_company[0].business_name : '-' }</small></td>
                                </tr>
                            </Alert>
                        </div>
                    }
                </Modal.Body>
                <Modal.Footer style={{ border:'none' }}>
                   {!this.state.dataSearch ? <Button variant="success" onClick={this.saveUser}>Save</Button> : 
                    <Button variant="success" onClick={() => this.setState({
                        modalShow: false
                    })}>OK</Button>
                   }
                </Modal.Footer>
            </Modal>


            <Modal 
                show={this.state.modalProduct} 
                onHide={() => this.setState({ modalProduct: false })}
                size="lg"
                aria-labelledby="contained-modal-title-vcenter"
                centered
            >
                <Modal.Header closeButton style={{ border:'none'}}>
                    <Modal.Title>Add Product Service</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Tabs
                        style={{justifyContent: 'left'}}
                        defaultActiveKey="products" transition={false} id="noanim-tab-example"
                    >
                                <Tab eventKey="products" title="Items">
                                    <Row className="mt-5">
                                        <Col xs={12}>
                                        <Form.Group as={Row} controlId="formPlaintextPassword">
                                            <Col sm="5">
                                                <Form.Control type="text" placeholder="Cari Item" defaultValue={this.state.search_item} onChange={(e) => this.setState({search_item: e.target.value})} />
                                            </Col>
                                            <Col sm="2">
                                                <Button onClick={this.searchItem} variant="success" style={{ width: '100%' }} size="sm"><i className="fa fa-search"></i> Cari Item</Button>
                                            </Col>
                                        </Form.Group>
                                        </Col>
                                    </Row>
                                    <Row>
                                        <Col xs={12}>
                                            <DataTable
                                                noHeader
                                                data={this.state.data}
                                                columns={this.state.columns}
                                                highlightOnHover
                                                paginationTotalRows={this.state.total_items}
                                                paginationPerPage={this.state.num}
                                                pagination
                                                paginationServer
                                                onChangePage={this.handlePageChange}
                                                onChangeRowsPerPage={this.handlePerRowsChange}
                                            />
                                        </Col>
                                    </Row>
                                    <Row className="position-relative show-grid">
                                        <Col xs={12}>
                                        <Form.Group as={Row} controlId="formPlaintextPassword">
                                            <Form.Label column sm="12">
                                            Custom Item
                                            </Form.Label>
                                            <Col sm="5">
                                                <Form.Control type="text" placeholder="Item Name" defaultValue={this.state.item_name} onChange={(e) => this.setState({item_name: e.target.value})} />
                                            </Col>
                                            <Col sm="5">
                                                <Form.Control type="number" min={0} defaultValue={this.state.selling_price} placeholder="Selling Price" onChange={(e) => this.setState({selling_price: e.target.value})} />
                                            </Col>
                                            <Col sm="2">
                                                <Button variant="success" style={{ width: '100%' }} size="sm" onClick={ () => {
                                                    if(this.state.data.find((data) => data.attributes.item_name == this.state.item_name)){
                                                        alertDanger('item names cannot be the same')
                                                    }else{
                                                        if(this.state.item_name == null || this.state.selling_price == null){
                                                            alertDanger('data cannot be empty')
                                                        }else{
                                                            let d = [...this.state.data]
                                                            let data = {
                                                                attributes: {
                                                                    id: null,
                                                                    item_name: this.state.item_name,
                                                                    selling_price: this.state.selling_price,
                                                                    quantity: 0,
                                                                    buying_price: 0,
                                                                    discount: 0,
                                                                    tax: 0,
                                                                    submission_id: 0,
                                                                    company_id: 0,
                                                                    product_id: 0,
                                                                    created_at: null
                                                                },
                                                                id: null,
                                                                type: "item",
                                                                relationships: null
                                                            }
                                                            d.splice(this.state.num -1, 1)
                                                            d.splice(0, 0, data)
                                                            this.setState({
                                                                data: d,
                                                                total_items: this.state.total_items + 1
                                                            })
                                                        }
                                                    }
                                                }}><i className="fa fa-plus"></i> Add</Button>
                                            </Col>
                                        </Form.Group>
                                        </Col>
                                    </Row>
                                </Tab>
                                <Tab eventKey="product_category" title="Services">
                                    <Row className="mt-5">
                                        <Col xs={12}>
                                        <Form.Group as={Row} controlId="formPlaintextPassword">
                                            <Col sm="5">
                                                <Form.Control type="text" placeholder="Cari Item" defaultValue={this.state.search_item_service} onChange={(e) => this.setState({search_item_service: e.target.value})} />
                                            </Col>
                                            <Col sm="2">
                                                <Button onClick={this.searchItemService} variant="success" style={{ width: '100%' }} size="sm"><i className="fa fa-search"></i> Cari Item</Button>
                                            </Col>
                                        </Form.Group>
                                        </Col>
                                    </Row>
                                    <Row>
                                        <Col xs={12}>
                                            <DataTable
                                               noHeader
                                               data={this.state.dataService}
                                               columns={this.state.columnsService}
                                               highlightOnHover
                                               paginationTotalRows={this.state.totalService}
                                               paginationPerPage={this.state.numService}
                                               pagination
                                               paginationServer
                                               onChangePage={this.handlePageChangeService}
                                               onChangeRowsPerPage={this.handlePerRowsChangeService}
                                                // paginationServer
                                            />
                                        </Col>
                                    </Row>
                                </Tab>
                            </Tabs>
                </Modal.Body>
                <Modal.Footer style={{ border:'none'}}>
                <Button variant="success" onClick={() => this.setState({ modalProduct: false })}>
                    FINISH
                </Button>
                </Modal.Footer>
            </Modal>
            <Card style={{ backgroundColor: 'white', borderRadius:'10px' }} >
                <div className="mb-5">
                    <Form className="m-5">
                        <Row>
                            <Col xs={6}>
                                <Form.Group as={Row} controlId="formPlaintextPassword">
                                    <Form.Label column sm="3">
                                        Transaction Name
                                    </Form.Label>
                                    <Col sm="9">
                                        <Form.Control type="text" placeholder="Transaction Name" onChange={(e) => this.setState({ submission_name: e.target.value })} />
                                    </Col>
                                </Form.Group>

                            </Col>
                            <Col xs={6}>
                                <Form.Group as={Row} controlId="formPlaintextPassword">
                                    <Form.Label column sm="3">
                                    Bonus
                                    </Form.Label>
                                    <Col sm="9">
                                        <Form.Control type="text" placeholder="Bonus" onChange={(e) => this.setState({ bonus: e.target.value })} />
                                    </Col>

                                </Form.Group>
                            </Col>
                        </Row>
                    </Form>
                    <Table size="lg" className="mb-5 table-size" responsive>
                        <thead>
                            <tr>
                                <th className="pl-5">No</th>
                                <th>Emplpyee Name</th>
                                <th>Percent (%)</th>
                                <th>Price (IDR)</th>
                                <th className="pr-5">Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {this.state.keranjang.map((r, index) => (
                                <tr key={index}>
                                    <td className="pl-5">{index+1}</td>
                                    <td>
                                        {r.name}
                                    </td>

                                    <td><Form.Control type="number" defaultValue={parseFloat(r.percent)} 
                                    onChange={(e) => {
                                        let items = [...this.state.keranjang];
                                        let item = {...items[index]};
                                        let attributes = {...item}
                                        percent = e.target.value
                                        let total_harga_discount = bonus + ((percent * bonus)/100)
                                        wage_price = total_harga_discount 
                                        items[index].attributes = attributes;
                                        this.setState({
                                            keranjang: items,
                                        })
                                    }} 
                                    />
                                    </td>
                                    <td>
                                        <Form.Control type="number" min="0" max="100" defaultValue="0" onChange={(e) => {
                                            let items = [...this.state.keranjang];
                                            let item = {...items[index]};
                                            let attributes = {...item.attributes}
                                            let total_harga = attributes.qty * attributes.selling_price
                                            let total_harga_discount = total_harga - ((total_harga * e.target.value)/100)
                                            attributes.discount = e.target.value
                                            attributes.total = total_harga_discount + ((total_harga_discount * attributes.ppn)/100)
                                            items[index].attributes = attributes;
                                            this.setState({
                                                keranjang: items,
                                            })
                                        }} />
                                    </td>
                                    <td>{ r.attributes.total }</td>
                                    <td className="pr-5">
                                        <i className="fa fa-trash" style={{ color:'red' }}
                                            onClick={() => {
                                                let data_delete = [...this.state.keranjang]
                                                data_delete.splice(index, 1)
                                                this.setState({
                                                    keranjang: data_delete
                                                })
                                            }}
                                        ></i>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </Table>
                    <Row style={{ textAlign:'right' }} className="mr-5">
                        <Col xs={12}>
                            <Button variant="outline-success" onClick={this.addProduct}><i className="fa fa-plus"></i> Add Products</Button>
                        </Col>
                    </Row>

                    <Row style={{ textAlign:'right' }} className="mt-5 mr-5">
                        <Col xs={10}>
                            <strong>TOTAL</strong> 
                        </Col>

                        <Col xs={2}>
                            <strong>Rp {this.getTotals(this.state.keranjang)}</strong>
                        </Col>
                    </Row>


                    <Row style={{ textAlign:'right' }} className="mt-5 mr-5">
                        <Col xs={12}>
                            <Button variant="secondary">Cancel</Button> {' '} <Button variant="success" onClick={this.checkOut}> Submit</Button>
                        </Col>
                    </Row>


                </div>

            </Card>
        </Layout>
      );
    }
  }
