const initialState = {
  companies: [],
  include: {},
  data: null,
  companyIncluded: {},
  total: 0,
};

export default function (state = initialState, action) {
  const { type, payload } = action;
  switch (type) {
    case "GET_COMPANY":
      return {
        ...state,
        data: payload.data,
        companyIncluded: payload.included,
        //   total: payload.total
      };
    case "GET_ALL_COMPANY":
      return {
        ...state,
        companies: payload.data,
        include: payload.included,
        total: payload.total,
      };
    case "COMPANY_UPDATE":
      return {
        ...state,
        data: payload.data,
      };

    default:
      return state;
  }
}
