const initialState = {
    dataCategory: null,
    data: null,
    include: null,
    totalSubmission: 0,
    dataDraft: null,
    includeDraft: null,
    totalSubmissionDraft: 0,
    dataComment: null,
    includeComment: null,
    responseUpdateStatus: null,
    total_amount: 0,
    total_potensial: 0,
    inlcudeAttachment: null,
    dataAttachment: null
  }

  export default function (state = initialState, action) {
    const { type, payload } = action;
    switch (type) {

      case "GET_SUBMISSION":
        return {
          ...state,
          data: payload.data,
          include: payload.include,
          totalSubmission: payload.total,
          total_amount: payload.total_amount,
          total_potensial: payload.total_potensial
        };
      case "GET_SUBMISSION_COMMENT":
        return {
          ...state,
          dataComment: payload.data,
          includeComment: payload.include,
          // totalSubmission: payload.total
        };
      case "SUBMISSION_UPDATE":
        return {
          ...state,
          data: payload.data,
        };
      case "SUBMISSION_UPDATE_STATUS":
        return {
          ...state,
          data: payload.data,
        };
      case "SUBMISSION_UPDATE_FULFILLED":
        return {
          ...state,
          data: payload.data,
        };
      case "GET_SUBMISSION_ID":
        return {
          ...state,
          data: payload.data,
          include: payload.include,
        };
      case "GET_SUBMISSION_DRAFT":
          return {
            ...state,
            dataDraft: payload.data,
            includeDraft: payload.include,
            totalSubmissionDraft: payload.total
          };

      case "STORE_SUBMISSION":
        return {
          ...state,
          data: payload.data,
          include: payload.include
        };
      case "GET_CATEGORY_SUBMISSION":
        return {
          ...state,
          dataCategory: payload.data,
        };

      case "STORE_CATEGORY_SUBMISSION":
        return {
          ...state,
          dataCategory: payload.data,
        };

        case "UPDATE_CATEGORY_SUBMISSION":
        return {
          ...state,
          dataCategory: payload.data,
        };
        case "GET_SUBMISSION_ATTACHMENT":
        return {
          ...state,
          dataAttachment: payload.data,
          inlcudeAttachment: payload.include
        };
        case "STORE_SUBMISSION_ATTACHMENT":
        return {
          ...state,
          dataAttachment: payload.data,
          inlcudeAttachment: payload.include
        };

      case "FAIL":
        return {
          ...state,
          dataCategory: null,
          data: null,
          include: null,
          totalSubmission: 0,
          dataDraft: null,
          includeDraft: null,
          totalSubmissionDraft: 0,
          dataComment: null,
          includeComment: null,
          responseUpdateStatus: null,
          total_amount: 0,
          total_potensial: 0
        };
      case "DELETE_CATEGORY_SUBMISSION":
        return {
          ...state,
        };
      case "DELETE_SUBMISSION":
        return {
          ...state,
        };
      case "STORE_SUBMISSION_COMMENT":
          return {
            ...state,
            dataComment: payload.data,
            includeComment: payload.include
          };
      case "DELETE_SUBMISSION_COMMENT":
            return {
              ...state,
            };
      case "DELETE_SUBMISSION_ATTACHMENT":
            return {
              ...state,
            };
      default:
        return state;
    }
  }
