import BaseService from './baseService.js'

class WalletService extends BaseService{
    getWallet(opts = {}){
        this.endPoint = `/get-company-wallets`
        return this.get(opts)
    }

    storeWallet(payload){
        this.endPoint = '/company-wallets'
        return this.post(payload);
    }

    updateWallet(payload, opts ={}){
        this.endPoint = '/company-wallets/' + payload.id
        return this.post(payload)
    }

    deleteWallet(id, opts = {}){
        this.endPoint = '/company-wallets'
        return this.delete(id, opts)
    }

    getRekonsil(payload, opts = {}){
        this.endPoint = `/get-payment-reconciliations?page[limit]=${payload.limit}&page[num]=${payload.page}&include=from_wallet,to_wallet`
        return this.get(opts)
    }

    storeRekonsil(payload){
        this.endPoint = '/payment-reconciliations?include=from_wallet,to_wallet'
        return this.post(payload)
    }

}

export default new WalletService()
