import BaseService from "./baseService.js";
import cloneDeep from 'lodash/cloneDeep'

class UserService extends BaseService {
  getCustomer(opts = {}) {
    this.endPoint = "/get-user-customer";
    return this.get(opts);
  }

  searchUser(payload) {
    this.endPoint = "/user-search";
    return this.post(payload);
  }

  storeUser(payload, opts = {}) {
    this.endPoint = "/users";
    return this.post(payload, opts);
  }

  createUser(payload, opts = {}) {
    this.endPoint = "/users";
    return this.post(payload, opts);
  }

  editUserCompany(payload, opts = {}) {
    this.endPoint = "/update-user-role";
    return this.post(payload, opts);
  }

  getUsers(opts = {}) {
    this.endPoint = `/get-users`;
    return this.get(opts);
  }

  destroy(id, opts = {}) {
    this.endPoint = "/users";
    return this.delete(id, opts);
  }

  assignUserToCompany(payload, opts = {}) {
    this.endPoint = "/assign-user-to-company";
    return this.post(payload, opts);
  }

  updateProfile(payload, opts = {}){
    this.endPoint = "/update-profile";
    const newOpts = cloneDeep(opts, {
      headers: {
          'Content-Type': 'multipart/form-data'
      }
  })
    return this.post(payload, newOpts);
  }

  getUser(id, opts = {}){
    this.endPoint = "/users";
    return this.getOne(id, opts);
  }

  changePassword(payload, opts = {}){
    this.endPoint = "/change-password";
    return this.post(payload, opts);
  }

  deleteUserFromCompany(id, opts = {}){
    this.endPoint = "/delete-user-from-company";
    return this.delete(id, opts);
  }

  assignCompanyToReseller(id, payload, opts = {}){
    this.endPoint = "/assign-company-reseller";
    return this.putOne(id, payload, opts);
  }

}

export default new UserService();
