import * as React from 'react';
import {Button, Row, Col, Card, Form, InputGroup, FormControl, Modal, Tabs, Tab, Alert, Table} from 'react-bootstrap';
import DataTable, { createTheme } from 'react-data-table-component';
import Layout from '../../../components/Layout';
import {connect} from 'react-redux'
import CounterInput from 'react-counter-input'
import Router from "next/router";
import {alertSuccess, alertDanger} from '../../../components/alert'
import { getUser } from "../../../redux/actions/user";


class index extends React.Component {

    constructor(props){
        super(props)
        this.state = {
            modalProduct: false,
            columns: [
                {
                    name: 'ID',
                    selector: (row, index) =>  ((index+1) + (this.state.page - 1) * this.state.num) + '.',
                    sortable: true,
                    width: '10%'
                },
                {
                    name: 'Name',
                    selector: 'name',
                    sortable: true,
                },
                {
                    name: 'Selling Price (IDR)',
                    selector: 'selling_price',
                    sortable: true,
                },
                {
                    name: 'Actions',
                    selector: (row, index) => (<div style={{ color:'green' }}><i style={{ color:'#FFF', borderRadius:'50%', background:'green', padding:'2px' }} className="fa fa-plus" onClick={ () => {
                        let keranjang = this.state.keranjang
                        // row.attributes.amount = Number(row.attributes.amount)
                        row.percent = 0
                        row.wage_price = 0       
                        keranjang.push(row)
                        this.setState({
                            keranjang: keranjang
                        })
                        let delete_data = [...this.state.data]
                        delete_data.splice(index, 1)
                        this.setState({
                            data: delete_data
                        })
                        alertSuccess('items added!')

                    }}></i> Add</div>),
                    width: '10%'
                }
            ],
            data: [],
            keranjang: [],
            total: 0,
            bonus:0,
            date: null,
            due_date: null,
            submission_name: null,
            customer_name: null,
            customer_phone: null,
            page: 1,
            num: 5,
            dataCategory: [],
            category_id: null,
            item_name: null,
            selling_price: null,
            total_items: 0,
            dataSearch: null,
            modalShow: false,
            modalOrder: false,
            name: null, 
            wages:[],
            includeProduct: null,
            search_item: '',
            search_item_service: ''
        }
    }

    getTotals = (data) => {
        let total = 0;
        data.forEach(item => {
          total += item.total;
        });
        return parseFloat(total);
    };

    addProduct = async () => {
        this.setState({ modalProduct:true })
    }
    searchItem = async () => {
        this.getData()
    }

    async getData () {
        // let opts = {
        //     page: 1,
        //     limit: 5,
        // }

        await this.props.getUser({}).then(() => {
            this.setState({
                data: this.props.dataUser,
            })
        })
        console.log(this.state.data)
    }
    handlePageChange = page => {
        this.getData(page)
    }

    handlePerRowsChange = async (newPerPage, page) => {
        this.getData(page, newPerPage);
    };

    async componentDidMount() {
        this.getData()
    }


    saveSubmission = (e) => {
        let items = []
        this.state.keranjang.forEach(item => {
            items.push({
                item_name: item.item_name,
                buying_price: item.buying_price,
                selling_price: item.selling_price,
                tax: item.ppn,
                discount: item.discount,
                quantity: item.qty,
                schedules: item.product_schedules ? item.product_schedules: null 
            })
        });

        this.state.orderFor.company_id = this.state.orderFor.type == 'COMPANY' ? this.state.company_id : 0
        let payload = {
            submission_name: this.state.submission_name,
            date: this.state.date,
            due_date: this.state.due_date,
            category_id: this.state.category_id,
            status: 'oke',
            reference_doc_number: 'INCOME - ' + this.state.date,
            customer_email: this.state.customer_name,
            order_for: this.state.orderFor,
            items: items
        }

        this.props.storeSubmission(payload).then(() => {
            Router.replace("/submissions/income");
            alertSuccess('Submission created')
        })
    }

    submitCheckout = () => {
        this.saveSubmission()
    }

    render () {

      return (

        <Layout title="Create new submissions" classname="dashboard theme-light">

            <Modal 
                show={this.state.modalProduct} 
                onHide={() => this.setState({ modalProduct: false })}
                size="lg"
                aria-labelledby="contained-modal-title-vcenter"
                centered
            >
                <Modal.Header closeButton style={{ border:'none'}}>
                    <Modal.Title>Add Employee</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Tabs
                        style={{justifyContent: 'left'}}
                        defaultActiveKey="products" transition={false} id="noanim-tab-example"
                    >
                        <Tab eventKey="products" title="Data">
                            <Row className="mt-5">
                                <Col xs={12}>
                                <Form.Group as={Row} controlId="formPlaintextPassword">
                                    <Col sm="5">
                                        <Form.Control type="text" placeholder="Cari Item" defaultValue={this.state.search_item} onChange={(e) => this.setState({search_item: e.target.value})} />
                                    </Col>
                                    <Col sm="2">
                                        <Button onClick={this.searchItem} variant="success" style={{ width: '100%' }} size="sm"><i className="fa fa-search"></i> Cari Item</Button>
                                    </Col>
                                </Form.Group>
                                </Col>
                            </Row>
                            <Row>
                                <Col xs={12}>
                                    <DataTable
                                        noHeader
                                        data={this.state.data}
                                        columns={this.state.columns}
                                        highlightOnHover
                                        paginationTotalRows={this.state.total_items}
                                        paginationPerPage={this.state.num}
                                        pagination
                                        paginationServer
                                        onChangePage={this.handlePageChange}
                                        onChangeRowsPerPage={this.handlePerRowsChange}
                                    />
                                </Col>
                            </Row>
                        </Tab>
                    </Tabs>
                </Modal.Body>
                <Modal.Footer style={{ border:'none'}}>
                <Button variant="success" onClick={() => this.setState({ modalProduct: false })}>
                    FINISH
                </Button>
                </Modal.Footer>
            </Modal>
            <Card style={{ backgroundColor: 'white', borderRadius:'10px' }} >
                <div className="mb-5">
                    <Form className="m-5">
                        <Row>
                            <Col xs={6}>
                                <Form.Group as={Row} controlId="formPlaintextPassword">
                                    <Form.Label column sm="3">
                                        Transaction Name
                                    </Form.Label>
                                    <Col sm="9">
                                        <Form.Control type="text" placeholder="Transaction Name" onChange={(e) => this.setState({ submission_name: e.target.value })} />
                                    </Col>
                                </Form.Group>

                            </Col>
                            <Col xs={6}>
                                <Form.Group as={Row} controlId="formPlaintextPassword">
                                    <Form.Label column sm="3">
                                    Bonus
                                    </Form.Label>
                                    <Col sm="9">
                                        <Form.Control type="text" placeholder="Bonus" onChange={(e) => this.setState({ bonus: e.target.value })} />
                                    </Col>

                                </Form.Group>
                            </Col>
                        </Row>
                    </Form>
                    <Table size="lg" className="mb-5 table-size" responsive>
                        <thead>
                            <tr>
                                <th className="pl-5">No</th>
                                <th>Emplpyee Name</th>
                                <th>Percent (%)</th>
                                <th>Price (IDR)</th>
                                <th className="pr-5">Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {this.state.keranjang.map((r, index) => (
                                <tr key={index}>
                                    <td className="pl-5">{index+1}</td>
                                    <td>
                                        {r.name}
                                    </td>

                                    <td><Form.Control type="number" max="100" defaultValue="0" defaultValue={parseFloat(r.percent)} 
                                    onChange={(e) => {
                                        let items = [...this.state.keranjang];
                                        let item = {...items[index]};
                                        let attributes = {...item}
                                        // percent = e.target.value == null ? 0 : e.target.value 
                                        let total_harga = (this.state.bonus * r.percent)/100
                                        wage_price = total_harga
                                        items[index] = attributes;
                                        this.setState({
                                            keranjang: items,
                                        })
                                        console.log({bonus:this.state.bonus, a:wage_price})
                                    }} 
                                    />
                                    </td>
                                    {/* <td>
                                        <Form.Control type="number" defaultValue="0" readOnly onChange={(e) => {
                                            let items = [...this.state.keranjang];
                                            let item = {...items[index]};
                                            let attributes = {...item.attributes}
                                            let total_harga = qty * selling_price
                                            let total_harga_discount = total_harga - ((total_harga * e.target.value)/100)
                                            discount = e.target.value
                                             = total_harga_discount + ((total_harga_discount * ppn)/100)
                                            items[index].attributes = attributes;
                                            this.setState({
                                                keranjang: items,
                                            })
                                        }} />
                                    </td> */}
                                    <td>{ r.wage_price }</td>
                                    <td className="pr-5">
                                        <i className="fa fa-trash" style={{ color:'red' }}
                                            onClick={() => {
                                                let data_delete = [...this.state.keranjang]
                                                data_delete.splice(index, 1)
                                                this.setState({
                                                    keranjang: data_delete
                                                })
                                            }}
                                        ></i>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </Table>
                    <Row style={{ textAlign:'right' }} className="mr-5">
                        <Col xs={12}>
                            <Button variant="outline-success" onClick={this.addProduct}><i className="fa fa-plus"></i> Add Products</Button>
                        </Col>
                    </Row>

                    <Row style={{ textAlign:'right' }} className="mt-5 mr-5">
                        <Col xs={10}>
                            <strong>TOTAL</strong> 
                        </Col>

                        <Col xs={2}>
                            <strong>Rp {this.getTotals(this.state.keranjang)}</strong>
                        </Col>
                    </Row>


                    <Row style={{ textAlign:'right' }} className="mt-5 mr-5">
                        <Col xs={12}>
                            <Button variant="secondary">Cancel</Button> {' '} <Button variant="success" onClick={this.checkOut}> Submit</Button>
                        </Col>
                    </Row>


                </div>

            </Card>
        </Layout>
      );
    }
  }
  function mapStateToProps ( state ) {
    const { dataUser, total } = state.user;
    return {
        dataUser,
        total
    };
}

function mapDispatchToProps (dispatch) {
    return {
        getUser: (opts) => dispatch(getUser(opts)),
    }
}


export default connect(mapStateToProps,mapDispatchToProps)(index);
