import ProductService from "../../service/product";

export const getProduct = (opts) => (dispatch) => {
    return ProductService.getProduct(opts).then(
    (data) => {
      dispatch({
          type: "GET_PRODUCT",
          payload: {
              data: data.data.data,
              included: data.data.included,
              total: data.data.meta.pagination.total,
          }
      })
      return Promise.resolve();
    },
    (error) => {
      dispatch({
        type: "FAIL",
      });

      return Promise.reject();
    }
  );
};

export const storeProduct = (payload) => (dispatch) => {
  return ProductService.storeProduct(payload).then(
    (data) => {
      dispatch({
        type: "STORE_PRODUCT",
        payload: {
          data: data.data.data,
          included: data.data.included
        },
      });

      return Promise.resolve();
    },
    (error) => {
      dispatch({
        type: "FAIL",
      });

      return Promise.reject();
    }
  );
};

export const updateProduct = (payload) => (dispatch) => {
  return ProductService.updateProduct(payload).then(
    (data) => {
      dispatch({
        type: "UPDATE_PRODUCT",
        payload: {
          data: data.data.data,
          included: data.data.included
        },
      });
      return Promise.resolve();
    },
    (error) => {
      dispatch({
        type: "FAIL",
      });

      return Promise.reject();
    }
  );
};

export const destroyProduct = (id) => (dispatch) => {
    return ProductService.deleteProduct(id).then((data) => {
        dispatch({
            type: "DELETE_PRODUCT"
        })
    }, (error) => {
      console.log(error)
    })
}

