import BaseService from './baseService.js'
import cloneDeep from 'lodash/cloneDeep'

class SubmissionService extends BaseService{

    getSubmission(opts = {}){
        this.endPoint = `/get-submissions`
        return this.get(opts)
    }

    getSubmissionComment(opts = {}){
        this.endPoint = '/get-submission-comments'
        return this.get(opts)
    }

    updateFulfilled(id, payload, opts = {}){
        this.endPoint = '/update-fullfilments'
        return this.putOne(id, payload, opts);
    }

    storeSubmission(payload){
        this.endPoint = '/submissions'
        return this.post(payload);
    }

    getSubmissionById(id, opts = {}){
        this.endPoint = '/submissions'

        return this.getOne(id, opts)
    }

    updateSubmission(id, payload, opts ={}){
        this.endPoint = '/submissions'
        return this.putOne(id, payload, opts)
    }

    updateStatusSubmission(id, payload, opts ={}){
        this.endPoint = '/update-statuses'
        return this.putOne(id, payload, opts)
    }

    getSubmissionCategory(payload, opts = {}){
        this.endPoint = '/get-submission-categories?filter[submission_type][is]=' + payload.type
        return this.get(opts)
    }

    storeSubmissionCategory(payload){
        this.endPoint = '/submission-categories'
        return this.post(payload);
    }

    updateSubmissionCategory(payload, opts ={}){
        this.endPoint = '/submission-categories/' + payload.id
        return this.post(payload)
    }

    deleteSubmissionCategory(id, opts = {}){
        this.endPoint = '/submission-categories'
        return this.delete(id, opts)
    }

    deleteSubmission(id, opts = {}){
        this.endPoint = '/submissions'
        return this.delete(id, opts)
    }

    storeSubmissionComment(payload, opts = {}){
        this.endPoint = '/submission-comments'
        return this.post(payload, opts);
    }

    deleteSubmissionComment(id, opts = {}){
        this.endPoint = '/submission-comments'
        return this.delete(id, opts)
    }

    getSubmissionAttachment(opts = {}){
        this.endPoint = '/get-submission-attachments'
        return this.get(opts);
    }

    storeSubmissionAttachment(payload, opts = {}){
        this.endPoint = "/submission-attachments"
        const newOpts = cloneDeep(opts, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
        })
        return this.post(payload, newOpts);
    }

    deleteSubmissionAttachment(id, opts = {}){
        this.endPoint = '/submission-attachments'
        return this.delete(id, opts)
    }
}

export default new SubmissionService()
