import BaseService from './baseService.js'
import cloneDeep from 'lodash/cloneDeep'

class ProductCategoryService extends BaseService{
    getProductCategory(opts = {}){
        this.endPoint = '/get-product-categories'
        return this.get(opts)
    }

    storeProductCategory(payload){
        this.endPoint = '/product-categories'
        return this.post(payload);
    }

    updateProductCategory(payload, opts ={}){
        this.endPoint = '/product-categories/' + payload.id
        return this.post(payload)
    }

    deleteProductCategory(id, opts = {}){
        this.endPoint = '/product-categories'
        return this.delete(id, opts)
    }

    deleteAttachmentProductCategory(id, opts={}){
        this.endPoint = '/product-category-attachments'
        return this.delete(id, opts)
    }

    createAttachmentProductCategory(payload, opts={}){
        this.endPoint = '/product-category-attachments'
        const newOpts = cloneDeep(opts, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
        })
        return this.post(payload, newOpts)
    }

}

export default new ProductCategoryService()
