import ProductCategoryService from "../../service/product_category_service";

export const get = (opts) => (dispatch) => {
    return ProductCategoryService.getProductCategory(opts).then(
    (data) => {
      dispatch({
          type: "GET_CATEGORY_PRODUCT",
          payload: {
              data: data.data.data,
              included: data.data.included,
              total: data.data.meta.pagination.total,
          }
      })
      return Promise.resolve();
    },
    (error) => {
      dispatch({
        type: "FAIL",
      });

      return Promise.reject();
    }
  );
};

export const store = (payload) => (dispatch) => {
  return ProductCategoryService.storeProductCategory(payload).then(
    (data) => {
      dispatch({
        type: "STORE_CATEGORY_PRODUCT",
        payload: {
          data: data.data.data,
        },
      });
      return Promise.resolve();
    },
    (error) => {
      dispatch({
        type: "FAIL",
      });

      return Promise.reject();
    }
  );
};

export const update = (payload) => (dispatch) => {
  return ProductCategoryService.updateProductCategory(payload).then(
    (data) => {
      dispatch({
        type: "UPDATE_CATEGORY_PRODUCT",
        payload: {
          data: data.data.data,
        },
      });
      return Promise.resolve();
    },
    (error) => {
      dispatch({
        type: "FAIL",
      });

      return Promise.reject();
    }
  );
};

export const destroy = (id) => (dispatch) => {
    return ProductCategoryService.deleteProductCategory(id).then((data) => {
        dispatch({
            type: "DELETE_CATEGORY_PRODUCT"
        })
    }, (error) => {
      console.log(error)
    })
}

export const destroyAttachment = (id) => (dispatch) => {
    return ProductCategoryService.deleteAttachmentProductCategory(id).then((data) => {
        dispatch({
            type: "DELETE_ATTACHMENT_CATEGORY_PRODUCT"
        })
    }, (error) => {
      console.log(error)
    })
}
export const createAttachmentProductCategory = (payload) => (dispatch) => {
    return ProductCategoryService.createAttachmentProductCategory(payload)
          .then((data) => {
            dispatch({
              type: "STORE_CATEGORY_PRODUCT_ATTACHMENT",
              payload: { 
                data: data.data.data
              }
            })

            return Promise.resolve();

          }, (error) => {
            dispatch({
              type: "FAIL",
            })

            return Promise.reject();
          })
  }
