const initialState = {
    data: null,
    totalWallet: 0,
    dataRekonsil: null,
    totalRekonsil: 0,
    included : {}
  }

  export default function (state = initialState, action) {
    const { type, payload } = action;
    switch (type) {
      case "GET_WALLET":
        return {
          ...state,
          data: payload.data,
          totalWallet: payload.total
        };
      case "STORE_WALLET":
        return {
          ...state,
          data: payload.data,
        };


        case "UPDATE_WALLET":
        return {
          ...state,
          data: payload.data,
        };

      case "FAIL":
        return {
          ...state,
          data: null,
        };
      case "DELETE_WALLET":
        return {
          ...state,
        };
      case "GET_REKONSIL":
        return {
          ...state,
          dataRekonsil: payload.data,
          totalRekonsil: payload.total,
          included: payload.included
        };
      case "STORE_REKONSIL":
        return {
          ...state,
          dataRekonsil: payload.data,
          included: payload.included
        };
      default:
        return state;
    }
  }
