import * as React from 'react';
import {Button,Tabs, Tab, Row, Col, Card, Form, InputGroup, FormControl, Modal, Badge} from 'react-bootstrap';
import DataTable from 'react-data-table-component';
import Layout from '../../components/Layout';
import { getContentCategory, storeContentCategory, updateContentCategory, deleteContentCategory } from "../../redux/actions/content";
import {connect} from 'react-redux'
import {toast} from 'react-toastify'

class category extends React.Component {

  constructor(props){
    super(props)
    this.state = {
            columnsCategory:[
                {
                    name: 'ID',
                    selector: 'id',
                    sortable: true,
                    width: '20%'
                },
                {
                    name: 'Category',
                    selector: 'attributes.category_name',
                    sortable: true,
                    width: '65%'
                },
                {
                    name: 'Actions',
                    selector: (row, index) => (<div>
                    <img className="mr-2" style={{width: '15px'}} src="https://storage.googleapis.com/spaces-semanggi-app/back-office/super-entity/png/border-color-24px.png" onClick={ () => this.setState({
                        modal:true, 
                        tabIndex:index, 
                        dataDetailCategory:row,
                        payloadCategory: {
                            ...this.state.payloadCategory,
                            category_name: row.attributes.category_name
                        }
                    })} /><img style={{width: '15px'}} src="https://storage.googleapis.com/spaces-semanggi-app/back-office/super-entity/png/delete.png" onClick={() => this.handleDelete(row.attributes.id)} /></div>),
                    width: '10%'
                }
            ],
            payloadCategory: {
                category_name: null
            },
            dataCategory: [],
            modal: false,
            tabIndex: -1,
            dataDetailCategory: null
        }

    }


    handleSaveCategory = async (e) => {
        e.preventDefault()
        if(this.state.tabIndex === -1){
            await this.props.storeContentCategory(this.state.payloadCategory)
            toast.success("Data Berhasil di Simpan", {
                position: toast.POSITION.TOP_RIGHT,
                autoClose: 3000
            })
        }else{
            await this.props.updateContentCategory(this.state.dataDetailCategory.attributes.id, this.state.payloadCategory, {});
            toast.success("Data Berhasil di Update", {
                position: toast.POSITION.TOP_RIGHT,
                autoClose: 3000
            })
        }
        this.getData()
        this.setState({
            modal: false
        })
    }  

    async componentDidMount() {
        this.getData()
    }

    getData = async () => {
        await this.props.getContentCategory({}).then(() => {
            this.setState({
                dataCategory: this.props.dataCategory
            })
        })
    }

    handleDelete = async (id) => {
        await this.props.deleteContentCategory(id).then(() => {
            this.getData()
            toast.success("Data Berhasil di Delete", {
                position: toast.POSITION.TOP_RIGHT,
                autoClose: 3000
            })
        })
    }

    render () {
      return (

        <Layout title="Content Category" classname="dashboard theme-light">
        {/* Modal Form */}
        <Modal
            show={this.state.modal}
            onHide={() => this.setState({
                modal: false, 
                tabIndex: -1, 
                statusProduct: null,
                payloadCategory: {
                    ...this.state.payloadCategory,
                    category_name: null
                }
            })}
            backdrop="static"
            keyboard={false}
            centered
            size="lg"
            aria-labelledby="contained-modal-title-vcenter"
        >
            <Modal.Header style={{ border:'none' }} closeButton>
            <Modal.Title>{this.state.tabIndex == -1 ? 'Create Category' : 'Edit Category'}</Modal.Title>
            </Modal.Header>
            <Form onSubmit={this.saveProduct}>
            <Modal.Body className="p-5">
                <Row>
                    <Col xs={4} className="pt-1">
                        <strong>Category Name</strong>
                    </Col>
                    <Col xs={8}>
                        <Form.Control type="text" defaultValue={this.state.payloadCategory.category_name} onChange={(e) => this.setState({
                            payloadCategory:{
                                ...this.state.payloadCategory,
                                category_name: e.target.value
                            },

                        })} min="0" placeholder="Category Name" />
                    </Col>
                </Row>
            </Modal.Body>
            <Modal.Footer style={{ border:'none' }}>
            <Button variant="secondary" onClick={() => this.setState({
                modal: false, 
                tabIndex: -1, 
                statusProduct: null,
                payloadCategory: {
                    ...this.state.payloadCategory,
                    category_name: null
                }
            })}>
                Close
            </Button>
            <Button variant="primary" type="submit" onClick={this.handleSaveCategory}>Save</Button>
            </Modal.Footer>
            </Form>
        </Modal>

        <Row className="pt-15">
            <Col lg={12} md={12} sm={12}>
                <Card style={{ backgroundColor: 'white', borderRadius:'10px' }}>
                    <Row className="position-relative show-grid" style={{ margin:'20px 20px 0px 20px' }}>
                    <Col xs={12}>
                        <Row className="position-relative show-grid" >
                            <Col xs={10}>
                                <Form>
                                    <Form.Row>
                                        <Col xs="4">
                                            <Form.Label htmlFor="inlineFormInputGroup" srOnly>
                                                Find Category Contents By Name
                                            </Form.Label>
                                            <InputGroup className="mb-2">
                                                <InputGroup.Prepend>
                                                <InputGroup.Text style={{ backgroundColor:'white' }}><i className="fa fa-search"></i></InputGroup.Text>
                                                </InputGroup.Prepend>
                                                <FormControl id="inlineFormInputGroup" placeholder="Find Category Contents By Name" onKeyPress={this.handleSearch} onChange={(e) => this.setState({ inputSearch: e.target.value })} />
                                            </InputGroup>
                                        </Col>
                                    </Form.Row>
                                </Form>
                            </Col>
                            <Col xs={2} style={{ textAlign:'end' }}>
                                <Button variant="success" onClick={() => {
                                    this.setState({modal: true})
                                }}>Add Category</Button>
                            </Col>
                        </Row>
                        <Row>
                            <Col xs={12}>
                                <DataTable
                                    data={this.state.dataCategory}
                                    columns={this.state.columnsCategory}
                                    highlightOnHover
                                    noHeader={true}
                                    paginationTotalRows={this.props.total}
                                    paginationPerPage={this.state.num}
                                    pagination
                                    paginationServer
                                    onChangePage={this.handlePageChange}
                                    onChangeRowsPerPage={this.handlePerRowsChange}
                                    // paginationServer
                                />
                            </Col>
                        </Row>

                    </Col>
                    </Row>
                </Card>
            </Col>
        </Row>
        </Layout>
      );
    }

  }
  function mapStateToProps ( state ) {

    const { dataCategory, total } = state.content;
    return {
        dataCategory,
        total
    };
}

  function mapDispatchToProps (dispatch) {
    return {
    getContentCategory: (opts) => dispatch(getContentCategory(opts)),
    storeContentCategory: (payload, opts) => dispatch(storeContentCategory(payload, opts)),
    updateContentCategory: (id, payload, opts) => dispatch(updateContentCategory(id, payload, opts)),
    deleteContentCategory: (id) => dispatch(deleteContentCategory(id)),
    }
  }


export default connect(mapStateToProps, mapDispatchToProps)(category);
