import UserService from "../../service/user";
import { hideLoadingSpinner } from "components/Layout";

export const getCustomer = (payload) => (dispatch) => {
  return UserService.getCustomer(payload).then(
    (data) => {
      dispatch({
        type: "GET_CUSTOMER",
        payload: {
          data: data.data.data,
          included: data.data.included,
          total: data.data.meta.pagination.total,
        },
      });
      return Promise.resolve();
    },
    (error) => {
      dispatch({
        type: "FAIL",
      });
      hideLoadingSpinner();
      return Promise.reject();
    }
  );
};

export const getUsers = (opts) => (dispatch) => {
  return UserService.getUsers(opts).then(
    (data) => {
      dispatch({
        type: "GET_USERS",
        payload: {
          data: data.data.data,
          included: data.data.included,
          total: data.data.meta.pagination.total,
        },
      });
      return Promise.resolve();
    },
    (error) => {
      dispatch({
        type: "FAIL",
      });
      hideLoadingSpinner();
      return Promise.reject();
    }
  );
};
export const getAllUsers = (opts) => (dispatch) => {
  return UserService.getUsers(opts).then(
    (data) => {
      dispatch({
        type: "GET_ALL_USERS",
        payload: {
          data: data.data.data,
          included: data.data.included,
          total: data.data.meta.pagination.total,
        },
      });
      return Promise.resolve();
    },
    (error) => {
      dispatch({
        type: "FAIL",
      });
      hideLoadingSpinner();
      return Promise.reject();
    }
  );
};

export const searchUser = (payload) => (dispatch) => {
  return UserService.searchUser(payload).then(
    (data) => {
      dispatch({
        type: "SEARCH_USER",
        payload: {
          data: data.data,
        },
      });
      return Promise.resolve(data.data);
    },
    (error) => {
      dispatch({
        type: "FAIL",
      });
      hideLoadingSpinner();
      return Promise.reject();
    }
  );
};

export const storeUser = (payload, opts) => (dispatch) => {
  return UserService.storeUser(payload, opts).then(
    (data) => {
      // dispatch({
      //   type: "STORE_USER",
      //   payload: {
      //     data: data.data.data,
      //   },
      // });
      return Promise.resolve();
    },
    (error) => {
      // dispatch({
      //   type: "FAIL",
      // });

      return Promise.reject();
    }
  );
};

export const createUser = (payload, opts) => (dispatch) => {
  return UserService.createUser(payload, opts).then(
    (data) => {
      dispatch({
        type: "STORE_USER",
        payload: {
          data: data.data.data,
        },
      });
      return Promise.resolve();
    },
    (error) => {
      dispatch({
        type: "FAIL",
      });
      hideLoadingSpinner();
      return Promise.reject();
    }
  );
};

export const editUserCompany = (id, payload, opts) => (dispatch) => {
  return UserService.editUserCompany(id, payload, opts).then(
    (data) => {
      return Promise.resolve();
    },
    (error) => {
      hideLoadingSpinner();
      return Promise.reject();
    }
  );
};

export const destroyUser = (id) => (dispatch) => {
  return UserService.destroy(id).then(
    (data) => {
      dispatch({
        type: "DELETE_USERS",
      });
    },
    (error) => {
      hideLoadingSpinner();
      console.log(error);
    }
  );
};

export const deleteUserFromCompany = (id) => (dispatch) => {
  return UserService.deleteUserFromCompany(id).then(
    (data) => {
      dispatch({
        type: "DELETE_USERS_FOR_COMPANY",
      });
    },
    (error) => {
      hideLoadingSpinner();
      console.log(error);
    }
  );
};

export const updateProfile = (payload, opts) => (dispatch) => {
  return UserService.updateProfile(payload, opts).then(
    (data) => {
      dispatch({
        type: "UPDATE_PROFILE",
        payload: {
          data: data.data.data,
        },
      });
      return Promise.resolve();
    },
    (error) => {
      dispatch({
        type: "FAIL",
      });
      hideLoadingSpinner();
      return Promise.reject();
    }
  );
};

export const getUser = (id, opts) => (dispatch) => {
  return UserService.getUser(id, opts).then(
    (data) => {
      dispatch({
        type: "GET_USER",
        payload: {
          data: data.data.data,
          included: data.data.included,
        },
      });
      return Promise.resolve();
    },
    (error) => {
      dispatch({
        type: "FAIL",
      });
      hideLoadingSpinner();
      return Promise.reject();
    }
  );
};

export const changePassword = (payload, opts) => (dispatch) => {
  return UserService.changePassword(payload, opts).then(
