import React, { Component } from "react";
import Link from "next/link";
import {
  Card,
  Col,
  Row,
  Table,
  Form,
  InputGroup,
  FormControl,
  Button,
  Badge,
} from "react-bootstrap";
import Layout from "../components/Layout";
import Chart from "chart.js";
import {getDashboard} from "../redux/actions/dashboard";
import {connect} from 'react-redux'
import { convertCurrency } from "libs/numbers";

const whiteColor = {
  color: "white",
  colorImportant: true,
};
class index extends React.Component {
  constructor(props) {
    super(props);
    this.chartRef = React.createRef();
    this.chartRef2 = React.createRef();
    this.chartRef3 = React.createRef();
    this.state = {
      customStyles: {
        rows: {
          style: {
            cursor: "pointer",
          },
        },
      },
      loading: true,
      data: [],
      financialStatus: {},
      dataDonutExpenses: [],
      dataDonutIncome: [],
      totalDonutExpenses: 0,
      totalDonutIncome: 0,
      transactionUpdates: [],
      labelDonut: ["Potential", "Unpaid", "Partial", "Paid", "Complete"],
      trendingLabels: [],
      dataIncome: [],
      dataExpenses: [],
    };
  }

  async componentDidMount() {
    await this.getData()
    const myChartRef = await this.chartRef;
    await new Chart(myChartRef.current.getContext("2d"), {
      type: "line",
      data: {
        //Bring in data
        labels: this.state.trendingLabels,
        datasets: [
          {
            label: "Income",
            data: this.state.dataIncome,
            fill: false,
            borderColor: "#0DAA66",

          },
          {
            label: "Expenses",
            data: this.state.dataExpenses,
            fill: false,
            borderColor: "#FF6666",
          },
        ],
      },
      options: {
        legend: {
          position: "bottom",
          labels: {
            boxWidth: 10,
            padding: 30,
          },
        },
        tooltips: {
          backgroundColor: "#FFFFFF",
          titleFontColor: "#000000",
          bodyFontColor: "#000000",
        },
        scales:{
          yAxes: [{
            ticks:{
              min:0
            }
          }]
        }
      },
    });

    const myChartRef2 = await this.chartRef2;

    await new Chart(myChartRef2.current.getContext("2d"), {
      type: "doughnut",
      data: {
        labels: this.state.labelDonut,
        datasets: [
          {
            data: this.state.dataDonutIncome,
            backgroundColor: [
              "#F4A15A",
              "#E36358",
              "#5D9EC8",
              "#6CB35F",
              "#B190D1",
            ],
          },
        ],
      },
      options: {
        legend: {
          display: false,
        },
        cutoutPercentage: 70,
        tooltips: {
          backgroundColor: "#FFFFFF",
          bodyFontColor: "#000000",
        },
      },
    });

    const myChartRef3 = await this.chartRef3;

    await new Chart(myChartRef3.current.getContext("2d"), {
      type: "doughnut",
      data: {
        labels: this.state.labelDonut,
        datasets: [
          {
            data: this.state.dataDonutExpenses,
            backgroundColor: [
              "#F4A15A",
              "#E36358",
              "#5D9EC8",
              "#6CB35F",
              "#B190D1",
            ],
          },
        ],
      },
      options: {
        legend: {
          display: false,
        },
        cutoutPercentage: 70,
        tooltips: {
          backgroundColor: "#FFFFFF",
          bodyFontColor: "#000000",
        },
      },
    });
  }

  async getData(){
    await this.props.getDashboard().then(() => {
        let label = this.state.labelDonut.map(v => v.toLowerCase());
        let tempExpense = [
          this.props.data.statistic.expenses[label[0]],
          this.props.data.statistic.expenses[label[1]],
          this.props.data.statistic.expenses[label[2]],
          this.props.data.statistic.expenses[label[3]],
          this.props.data.statistic.expenses[label[4]],
        ]

        let tempIncome = [
          this.props.data.statistic.income[label[0]],
          this.props.data.statistic.income[label[1]],
          this.props.data.statistic.income[label[2]],
          this.props.data.statistic.income[label[3]],
          this.props.data.statistic.income[label[4]],
        ]

        let tempTrendingLabels = []
        let tempDataIncome = []
        let tempDataExpenses = []

        this.props.data.trending.EXPENSE.forEach(expense => {
          tempTrendingLabels.push(expense.date)
          tempDataExpenses.push(Number(expense.total))
        });

        this.props.data.trending.INCOME.forEach(income => {
          tempDataIncome.push(Number(income.total))
        });

        this.setState({
          financialStatus: this.props.data.presentase,
          transactionUpdates: this.props.data.transaction,
          dataDonutExpenses: tempExpense,
          dataDonutIncome: tempIncome,
          totalDonutExpenses: this.props.data.statistic.expenses.total_expense,
          totalDonutIncome: this.props.data.statistic.income.total_income,
          trendingLabels: tempTrendingLabels.reverse(),
          dataIncome: tempDataIncome.reverse(),
          dataExpenses: tempDataExpenses.reverse()
        })
    })
  }

  render() {
    return (
      <Layout title="Dashboard" classname="dashboard theme-light">
        <Row className="pb-4">
          <Col lg={12} md={12} sm={12}>
            <Card
              text="white"
              className="text-center py-5"
              style={{
                borderRadius: "10px",
                backgroundImage:
                  'url("https://storage.googleapis.com/spaces-semanggi-app/back-office/super-entity/png/bg-dashboard.png")',
              }}
            >
              <Card.Body>
                <h6 className="py-1" style={whiteColor}>
                  Hello, {this.props.user && this.props.user.full_name}!
                </h6>
                <h6 className="py-1" style={whiteColor}>
                  Here are some things to get your started today!
                </h6>
                {/* <h6 className="py-1" style={whiteColor}>
                  There are 3 new companies waiting to be activated,
                </h6>
                <h6 className="py-1" style={whiteColor}>
                  and 10 invoices to follow up.
                </h6> */}
              </Card.Body>
            </Card>
          </Col>
        </Row>
        <Row className="pb-4">
          <Col lg={12} md={12} sm={12}>
            <Row>
              <Col lg={4} md={4} sm={4}>
                <Card
                  style={{ backgroundColor: "white", borderRadius: "10px" }}
                >
                  <p className="m-3">
                    <strong>Financial Status</strong>
                  </p>
                  <Row className="ml-3 mt-4 mr-3 mb-1">
                    <Col lg={6} md={6} sm={6}>
                      <h5>
                        Total Income{" "}
                        {this.state.financialStatus.income && this.state.financialStatus.income.status == 1 ? 
                          <img
                            style={{ width: "15px" }}
                            src="https://storage.googleapis.com/spaces-semanggi-app/back-office/super-entity/png/trending-green.png"
                          /> : this.state.financialStatus.income && this.state.financialStatus.income.status === 0 &&
                          <img
                          style={{ width: "15px" }}
                          src="https://storage.googleapis.com/spaces-semanggi-app/back-office/super-entity/png/trending-red.png"
                          /> 
                        }
                      </h5>
                    </Col>
                    <Col lg={6} md={6} sm={6}>
                      <span style={{ textAlign: "end", color: "#0DAA66" }}>
                        {this.state.financialStatus.income ? convertCurrency(Number(this.state.financialStatus.income.total)) : 0}
                      </span>
                    </Col>
                  </Row>
                  <Row className="ml-3 mr-3 mb-4">
                    <Col lg={12} md={12} sm={12} style={{ textAlign: "end" }}>
                    <small>{this.state.financialStatus.income && this.state.financialStatus.income.percent>0 && this.state.financialStatus.income.percent + '% '}{this.state.financialStatus.income && this.state.financialStatus.income.status === 1 ? 'increase from' : this.state.financialStatus.income && this.state.financialStatus.income.status === 0 ? 'decrease from' : 'Total income same with'} last months income</small>
                    </Col>
                  </Row>
                  <hr className="mx-3" />
                  <Row className="ml-3 mt-4 mr-3 mb-1">
                    <Col lg={6} md={6} sm={6}>
                      <h5>
                        Total Expenses{" "}
                        {this.state.financialStatus.expense && this.state.financialStatus.expense.status == 1 ? 
                          <img
                            style={{ width: "15px" }}
                            src="https://storage.googleapis.com/spaces-semanggi-app/back-office/super-entity/png/trending-green.png"
                          /> : this.state.financialStatus.expense && this.state.financialStatus.expense.status === 0 &&
                          <img
                          style={{ width: "15px" }}
                          src="https://storage.googleapis.com/spaces-semanggi-app/back-office/super-entity/png/trending-red.png"
                          /> 
                        }
                      </h5>
                    </Col>
                    <Col lg={6} md={6} sm={6}>
                      <span style={{ textAlign: "end", color: "#FF6666" }}>
                        {this.state.financialStatus.expense ? convertCurrency(Number(this.state.financialStatus.expense.total)) : 0}
                      </span>
                    </Col>
