import * as React from 'react';
import {Button, Row, Col, Card, Form, InputGroup, FormControl, Modal, Tabs, Tab, Alert, Table} from 'react-bootstrap';
import DataTable, { createTheme } from 'react-data-table-component';
import Layout from '../../components/Layout';
import {connect} from 'react-redux'
import CounterInput from 'react-counter-input'
import { getUser } from "../../redux/actions/user";
import Router from "next/router";
import {alertSuccess, alertDanger} from '../../components/alert'
import ModalCustom from "components/ModalCustom";
// import BookPerDay from "../../components/BookPerDay";
import {formatCurrency} from '../../libs/numbers'
import dayjs from 'dayjs'
import { isThisHour } from 'date-fns';


class index extends React.Component {

    constructor(props){
        super(props)
        this.state = {
            modalProduct: false,
            columns: [
                {
                    name: 'ID',
                    selector: (row, index) =>  ((index+1) + (this.state.page - 1) * this.state.num) + '.',
                    sortable: true,
                    width: '10%'
                },
                {
                    name: 'Name',
                    selector: 'attributes.item_name',
                    sortable: true,
                },
                {
                    name: 'Selling Price (IDR)',
                    selector: 'attributes.selling_price',
                    sortable: true,
                },
                {
                    name: 'Actions',
                    selector: (row, index) => (<div style={{ color:'green' }}><i style={{ color:'#FFF', borderRadius:'50%', background:'green', padding:'2px' }} className="fa fa-plus" onClick={ () => {

                        if(this.state.keranjang.find((data) => data.name == row.name)){
                            row.percent = 0
                            row.wage_price = 0
                            keranjang.push(row)
                            this.setState({
                                keranjang: keranjang
                            })
                            let data_delete = [...this.state.keranjang]
                            data_delete.splice(index, 1)
                            this.setState({
                                keranjang: data_delete
                            })
                        }

                        alertSuccess('items added!')

                    }}></i> Add</div>),
                    width: '10%'
                }
            ],
            data: [],
            keranjang: [],
            total: 0,
            bonus:0,
            date: null,
            due_date: null,
            submission_name: null,
            customer_name: null,
            customer_phone: null,
            page: 1,
            num: 5,
            dataCategory: [],
            category_id: null,
            item_name: null,
            selling_price: null,
            total_items: 0,
            dataSearch: null,
            modalShow: false,
            modalOrder: false,
            orderFor: {
                type: "PERSONAL",
            },
            company_id: null,
            name: null, 
            username: null,
            email: null,
            phone: null,
            password: null,
            pageService: 1,
            numService: 5,
            totalService: 0,
            dataService: null,
            includeProduct: null,
            search_item: '',
            search_item_service: ''
        }
    }

    getTotals = (data) => {
        let total = 0;
        data.forEach(item => {
          total += item.attributes.total;
        });
        return parseFloat(total);
    };

    addProduct = async () => {
        this.setState({ modalProduct:true })
    }

    searchItem = async () => {
        this.getData(1, this.state.num)
    }

    getData = (page, size = this.state.num) => {

        this.state.data = []
        let payload = {
            page: page,
            limit: size,
            item_name: this.state.search_item
        }

        this.props.getUser(payload).then(() => {
            this.setState({
                data: this.props.dataUser,
                total_items: this.props.total
            })
        })
    }

    handlePageChange = page => {
        this.getData(page)
    }

    handlePerRowsChange = async (newPerPage, page) => {
        this.getData(page, newPerPage);
    };

    async componentDidMount() {
        this.getData(this.state.page, this.state.num)

        let payload = {
            type: 'INCOME'
        }
        await this.props.getSubmissionCategory(payload).then(() => {
            this.setState({
                dataCategory: this.props.dataCategory
            })
        })
    }

    onKeyPressEmail = async (e) => {
        if (e.key === 'Enter') {
            e.preventDefault()
            this.state.orderFor.company_id = null
            await this.props.searchUser({ q: this.state.customer_name }).then(() => {
                this.setState({
                    dataSearch: this.props.dataUser,
                    modalShow: true,
                    password: Math.random().toString(36).slice(-8)
                })

            })
        }
    }

    saveSubmission = (e) => {
        // e.preventDefault()
        let items = []
        this.state.keranjang.forEach(item => {
            items.push({
                item_name: item.attributes.item_name,
                buying_price: item.attributes.buying_price,
                selling_price: item.attributes.selling_price,
                tax: item.attributes.ppn,
                discount: item.attributes.discount,
                quantity: item.attributes.qty,
                schedules: item.attributes.product_schedules ? item.attributes.product_schedules: null 
            })
        });

        this.state.orderFor.company_id = this.state.orderFor.type == 'COMPANY' ? this.state.company_id : 0
        let payload = {
            submission_name: this.state.submission_name,
            date: this.state.date,
            due_date: this.state.due_date,
            category_id: this.state.category_id,
            status: 'oke',
            reference_doc_number: 'INCOME - ' + this.state.date,
            customer_email: this.state.customer_name,
            order_for: this.state.orderFor,
            items: items
        }

        this.props.storeSubmission(payload).then(() => {
            Router.replace("/submissions/income");
            alertSuccess('Submission created')
        })
    }


    checkOut = () => {
        if(this.state.dataSearch && this.state.dataSearch.user_has_company.length){
           this.setState({
                modalOrder: true
            })
        }else{
            this.saveSubmission()
        }
    }

    submitCheckout = () => {
        this.saveSubmission()
    }

    saveUser = () => {
        let payload = {
            full_name: this.state.name,
            username: this.state.username,
            email: this.state.email,
            phone: this.state.phone,
            role_id: 15,
            type: 'PERSONAL',
            password: this.state.password
        }
        this.props.createUser(payload).then(() => {
            this.setState({
                customer_name: this.state.email,
                modalShow: false
            })
        })
    }

    generateStartFinishSchedule = (times, scheduleDate) => {
        const selectedRentTime = times.sort();

        let counter = 0;
        let finalSelectedRentTime = [];
        let temp = "";
        for (const rent of selectedRentTime) {
          let nextRentTime = selectedRentTime[counter + 1];
          if (nextRentTime !== undefined) {
            const splittedRent = rent.split(" - ");
            let splittedNextRentTime = nextRentTime.split(" - ");
            if (splittedRent[1] === splittedNextRentTime[0]) {
              let joinedRentTime = `${splittedRent[0]} - ${splittedNextRentTime[1]}`;
              if (temp === "") {
                temp = joinedRentTime;
              } else {
                let newTemp = temp.split(" - ");
                temp = newTemp[0] + " - " + splittedNextRentTime[1];
              }
            } else {
              if (temp !== "") {
                finalSelectedRentTime.push(temp);
              } else {
                finalSelectedRentTime.push(rent);
              }
              temp = "";
            }
          } else {
            if (temp) {
              finalSelectedRentTime.push(temp);
              temp = "";
            } else {
              finalSelectedRentTime.push(rent);
            }
          }
          counter++;
        }
        let newFinalSelectedRentTime = [];
        finalSelectedRentTime.forEach((rent) => {
          let splitted = rent.split(" - ");

          const start = dayjs(scheduleDate.fullDate)
            .startOf("day")
            .hour(parseInt(splitted[0].split(".")[0]))
            .format("YYYY-MM-DD HH:mm:ss");
          const finish = dayjs(scheduleDate.fullDate)
            .startOf("day")
            .hour(parseInt(splitted[1].split(".")[0]))
            .format("YYYY-MM-DD HH:mm:ss");
          newFinalSelectedRentTime.push(`${start} - ${finish}`);
        });
        return newFinalSelectedRentTime;
      };

    render () {

      return (

        <Layout title="Create new submissions" classname="dashboard theme-light">
            <Modal
                show={this.state.modalOrder}
                onHide={() => this.setState({
                    modalOrder: false
                })}
                dialogClassName="modal-90w"
                aria-labelledby="example-custom-modal-styling-title"
                centered
                backdrop="static"
                class="modal-backdrop foo-modal-backdrop in"
                keyboard={false}
            >
                <Modal.Header closeButton>
                <Modal.Title>Untuk siapa order ini di buat?</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                <Form.Group controlId="exampleForm.ControlSelect1">
