                                <span style={{ fontSize: '13px' }}>Name</span>
                                </Form.Label>
                                <Col sm="8">
                                <Form.Control type="text" placeholder="Name" onChange={(e) => this.setState({name: e.target.value})} />
                                </Col>
                            </Form.Group>    

                            <Form.Group as={Row} controlId="formPlaintextPassword">
                                <Form.Label column sm="4">
                                <span style={{ fontSize: '13px' }}>Username</span>
                                </Form.Label>
                                <Col sm="8">
                                <Form.Control type="text" placeholder="Username" onChange={(e) => this.setState({username: e.target.value})} />
                                </Col>
                            </Form.Group>

                            <Form.Group as={Row} controlId="formPlaintextPassword">
