                                </tr>
                                <tr>
                                    <th><small>Username</small></th>
                                    <td><small>: {this.state.dataSearch.username}</small></td>
                                </tr>
                                <tr>
                                    <th><small>Phone</small></th>
                                    <td><small>: {this.state.dataSearch.phone}</small></td>
                                </tr>
                                <tr>
                                    <th><small>Company</small></th>
                                    <td><small>: {this.state.dataSearch.user_has_company.length ? this.state.dataSearch.user_has_company[0].business_name : '-' }</small></td>
                                </tr>
                            </Alert>
