                    <Modal.Title>Add Product Service</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Tabs
                        style={{justifyContent: 'left'}}
                        defaultActiveKey="products" transition={false} id="noanim-tab-example"
