                                    <Col sm="9">
                                        <Form.Control type="text" placeholder="Transaction Name" onChange={(e) => this.setState({ submission_name: e.target.value })} />
                                    </Col>
                                </Form.Group>

                            </Col>
                            <Col xs={6}>
                                <Form.Group as={Row} controlId="formPlaintextPassword">
                                    <Form.Label column sm="3">
                                    Bonus
                                    </Form.Label>
                                    <Col sm="9">
                                        <Form.Control type="text" placeholder="Bonus" onChange={(e) => this.setState({ bonus: e.target.value })} />
                                    </Col>

                                </Form.Group>
                            </Col>
                        </Row>
                    </Form>
                    <Table size="lg" className="mb-5 table-size" responsive>
                        <thead>
                            <tr>
                                <th className="pl-5">No</th>
                                <th>Emplpyee Name</th>
                                <th>Percent (%)</th>
                                <th>Price (IDR)</th>
                                <th className="pr-5">Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {this.state.keranjang.map((r, index) => (
                                <tr key={index}>
                                    <td className="pl-5">{index+1}</td>
                                    <td>
                                        {r.name}
                                    </td>

                                    <td><Form.Control type="number" defaultValue={parseFloat(r.percent)} 
                                    onChange={(e) => {
                                        let items = [...this.state.keranjang];
                                        let item = {...items[index]};
                                        let attributes = {...item}
                                        percent = e.target.value
                                        let total_harga_discount = bonus + ((percent * bonus)/100)
                                        wage_price = total_harga_discount 
                                        items[index].attributes = attributes;
                                        this.setState({
                                            keranjang: items,
                                        })
                                    }} 
                                    />
                                    </td>
                                    <td>
                                        <Form.Control type="number" min="0" max="100" defaultValue="0" onChange={(e) => {
                                            let items = [...this.state.keranjang];
                                            let item = {...items[index]};
                                            let attributes = {...item.attributes}
                                            let total_harga = attributes.qty * attributes.selling_price
                                            let total_harga_discount = total_harga - ((total_harga * e.target.value)/100)
                                            attributes.discount = e.target.value
                                            attributes.total = total_harga_discount + ((total_harga_discount * attributes.ppn)/100)
                                            items[index].attributes = attributes;
                                            this.setState({
                                                keranjang: items,
                                            })
                                        }} />
                                    </td>
                                    <td>{ r.attributes.total }</td>
                                    <td className="pr-5">
                                        <i className="fa fa-trash" style={{ color:'red' }}
                                            onClick={() => {
                                                let data_delete = [...this.state.keranjang]
                                                data_delete.splice(index, 1)
                                                this.setState({
                                                    keranjang: data_delete
                                                })
                                            }}
                                        ></i>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </Table>
                    <Row style={{ textAlign:'right' }} className="mr-5">
                        <Col xs={12}>
                            <Button variant="outline-success" onClick={this.addProduct}><i className="fa fa-plus"></i> Add Products</Button>
                        </Col>
                    </Row>

                    <Row style={{ textAlign:'right' }} className="mt-5 mr-5">
                        <Col xs={10}>
                            <strong>TOTAL</strong> 
                        </Col>

                        <Col xs={2}>
                            <strong>Rp {this.getTotals(this.state.keranjang)}</strong>
                        </Col>
                    </Row>


                    <Row style={{ textAlign:'right' }} className="mt-5 mr-5">
                        <Col xs={12}>
                            <Button variant="secondary">Cancel</Button> {' '} <Button variant="success" onClick={this.checkOut}> Submit</Button>
                        </Col>
                    </Row>


                </div>

            </Card>
        </Layout>
      );
    }
  }
