import ContentService from "../../service/content";

export const getContentCategory = (opts) => (dispatch) => {
    return ContentService.getContentCategory(opts).then(
    (data) => {
      dispatch({
          type: "GET_CONTENT_CATEGORY",
          payload: {
              data: data.data.data,
              total: data.data.meta.pagination.total,
          }
      })
      return Promise.resolve();
    },
    (error) => {
      dispatch({
        type: "FAIL",
      });

      return Promise.reject();
    }
  );
};

export const storeContentCategory = (payload, opts = {}) => (dispatch) => {
    return ContentService.storeContentCategory(payload, opts).then(
    (data) => {
      dispatch({
          type: "STORE_CONTENT_CATEGORY",
      })
      return Promise.resolve();
    },
    (error) => {
      dispatch({
        type: "FAIL",
      });

      return Promise.reject();
    }
  );
};

export const updateContentCategory = (id, payload, opts = {}) => (dispatch) => {
    return ContentService.updateContentCategory(id, payload, opts).then(
    (data) => {
      dispatch({
          type: "UPDATE_CONTENT_CATEGORY",
      })
      return Promise.resolve();
    },
    (error) => {
      dispatch({
        type: "FAIL",
      });

      return Promise.reject();
    }
  );
};

export const deleteContentCategory = (id, opts = {}) => (dispatch) => {
    return ContentService.deleteContentCategory(id, opts).then(
    (data) => {
      dispatch({
          type: "DELETE_CONTENT_CATEGORY",
      })
      return Promise.resolve();
    },
    (error) => {
      dispatch({
        type: "FAIL",
      });

      return Promise.reject();
    }
  );
};



export const getContent = (opts) => (dispatch) => {
    return ContentService.getContent(opts).then(
    (data) => {
      dispatch({
          type: "GET_CONTENT",
          payload: {
              data: data.data.data,
              total: data.data.total,
          }
      })
      return Promise.resolve();
    },
    (error) => {
      dispatch({
        type: "FAIL",
      });

      return Promise.reject();
    }
  );
};

export const storeContent = (payload, opts = {}) => (dispatch) => {
    return ContentService.storeContent(payload, opts).then(
    (data) => {
      dispatch({
          type: "STORE_CONTENT",
      })
      return Promise.resolve();
    },
    (error) => {
      dispatch({
        type: "FAIL",
      });

      return Promise.reject();
    }
  );
};

export const updateContent = (id, payload, opts = {}) => (dispatch) => {
    return ContentService.updateContent(id, payload, opts).then(
    (data) => {
      dispatch({
          type: "UPDATE_CONTENT",
      })
      return Promise.resolve();
    },
    (error) => {
      dispatch({
        type: "FAIL",
      });

      return Promise.reject();
    }
  );
};

export const deleteContent = (id, opts = {}) => (dispatch) => {
    return ContentService.deleteContent(id, opts).then(
    (data) => {
      dispatch({
          type: "DELETE_CONTENT",
      })
      return Promise.resolve();
    },
    (error) => {
      dispatch({
        type: "FAIL",
      });

      return Promise.reject();
    }
  );
};
