const dayjs = require("dayjs");

const initialState = {
  selectedDateSchedule: {
    fullDate: new Date(),
    dateStr: dayjs().format("YYYY-MM-DD HH:mm:ss"),
  },
  selectedRentTime: [],
  productSchedules: [],
  includedProductSchedules: {},
};

const productSchedules = (state = initialState, action) => {
  const { type, payload } = action;
  switch (type) {
    case "GET_PRODUCT_SCHEDULES":
      return {
        ...state,
        productSchedules: payload.data,
        includedProductSchedules: payload.included,
      };
    case "SELECT_DATE_SCHEDULE":
      let newSelectedDateSchedule = Object.assign(
        state.selectedDateSchedule,
        payload
      );
      return {
        ...state,
        selectedDateSchedule: newSelectedDateSchedule,
      };
    case "SELECT_RENT_TIME":
      return {
        ...state,
        selectedRentTime: [...state.selectedRentTime, payload],
      };
    case "DESELECT_RENT_TIME":
      const filteredRentTime = state.selectedRentTime.filter(
        (rent) => rent !== payload
      );
      return {
        ...state,
        selectedRentTime: filteredRentTime,
      };
    case "RESET_DATE_SCHEDULE":
      return {
        ...state,
        selectedDateSchedule: Object.assign(state.selectedDateSchedule, {
          fullDate: new Date(),
          dateStr: dayjs().format("YYYY-MM-DD HH:mm:ss"),
        }),
      };
    case "RESET_RENT_TIME":
      return {
        ...state,
        selectedRentTime: [],
      };
    default:
      return state;
  }
};

export default productSchedules;
